SELECT c.ID, c.Name, c.CommitteeType, c.Purpose, c.MeetingFrequency, COUNT(cm.ID) AS ActiveMemberCount FROM [AssociationDemo].[vwCommittees] c INNER JOIN [AssociationDemo].[vwCommitteeMemberships] cm ON c.ID = cm.CommitteeID WHERE c.IsActive = 1 AND cm.IsActive = 1 AND cm.EndDate IS NULL GROUP BY c.ID, c.Name, c.CommitteeType, c.Purpose, c.MeetingFrequency ORDER BY ActiveMemberCount DESC