SELECT c.Region, COUNT(DISTINCT c.ID) AS ChapterCount, COUNT(co.ID) AS TotalOfficers, CAST(COUNT(co.ID) AS FLOAT) / NULLIF(COUNT(DISTINCT c.ID), 0) AS AvgOfficersPerChapter FROM [AssociationDemo].[vwChapters] c LEFT JOIN [AssociationDemo].[vwChapterOfficers] co ON c.ID = co.ChapterID AND co.IsActive = 1 WHERE c.IsActive = 1 AND c.Region IS NOT NULL GROUP BY c.Region ORDER BY AvgOfficersPerChapter DESC