SELECT c.ID, c.Name AS CommitteeName, c.CommitteeType, c.MaxMembers, COUNT(cm.ID) AS CurrentMembers, c.Purpose, c.MeetingFrequency, c.FormedDate FROM [AssociationDemo].[vwCommittees] c LEFT JOIN [AssociationDemo].[vwCommitteeMemberships] cm ON c.ID = cm.CommitteeID AND cm.IsActive = 1 WHERE c.IsActive = 1 AND c.MaxMembers IS NOT NULL GROUP BY c.ID, c.Name, c.CommitteeType, c.MaxMembers, c.Purpose, c.MeetingFrequency, c.FormedDate HAVING COUNT(cm.ID) >= c.MaxMembers ORDER BY COUNT(cm.ID) DESC, c.Name