SELECT ct.ID, ct.Name, ct.Abbreviation, ct.Description, ct.Level, ct.CostUSD, ct.RenewalRequiredMonths, ct.CECreditsRequired, CAST(ct.CostUSD / NULLIF(ct.RenewalRequiredMonths, 0) AS DECIMAL(10, 2)) AS CostPerMonth, ct.AccreditingBody, ct.DurationMonths, ct.ExamRequired, ct.PracticalRequired, ct.TargetAudience, ct.CertificationCount FROM [AssociationDemo].[vwCertificationTypes] ct WHERE ct.CECreditsRequired > 0 AND ct.IsActive = 1 AND ct.CostUSD IS NOT NULL AND ct.RenewalRequiredMonths > 0 ORDER BY CostPerMonth DESC