function AITimeSeriesChart({ data, groupBy, activeTab, selectedPoint, onPointClick, styles, utilities, components, callbacks, savedUserSettings, onSaveUserSettings }) {
  console.log('[AITimeSeriesChart] Rendering with', data?.length || 0, 'data points');

  const [hoveredPoint, setHoveredPoint] = useState(null);
  const [showRuns, setShowRuns] = useState(savedUserSettings?.showRuns !== false);
  const [showTokens, setShowTokens] = useState(savedUserSettings?.showTokens !== false);
  const [showCost, setShowCost] = useState(savedUserSettings?.showCost !== false);

  const chartRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 400 });

  // Update dimensions on resize
  useEffect(() => {
    const updateDimensions = () => {
      if (chartRef.current) {
        const { width, height } = chartRef.current.getBoundingClientRect();
        setDimensions({ width: Math.max(width, 400), height: Math.max(height, 300) });
      }
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  // Calculate chart dimensions
  const margin = { top: 20, right: 60, bottom: 40, left: 70 };
  const chartWidth = dimensions.width - margin.left - margin.right;
  const chartHeight = dimensions.height - margin.top - margin.bottom;

  // Calculate scales
  const { xScale, yLeftScale, yRightScale, maxTokens, maxRuns, maxCost } = useMemo(() => {
    if (!data || data.length === 0) {
      return {
        xScale: () => 0,
        yLeftScale: () => 0,
        yRightScale: () => 0,
        maxTokens: 0,
        maxRuns: 0,
        maxCost: 0
      };
    }

    const maxTokens = Math.max(...data.map(d => d.tokens || 0));
    const maxRuns = Math.max(...data.map(d => d.runs || 0));
    const maxCost = Math.max(...data.map(d => d.cost || 0));

    // X scale (time)
    const xScale = (index) => (index / (data.length - 1 || 1)) * chartWidth;

    // Y scales
    const yLeftScale = (value) => chartHeight - (value / (maxTokens || 1)) * chartHeight;
    const yRightScale = (value, max) => chartHeight - (value / (max || 1)) * chartHeight;

    return { xScale, yLeftScale, yRightScale, maxTokens, maxRuns, maxCost };
  }, [data, chartWidth, chartHeight]);

  // Generate line paths
  const { runsPath, tokensPath, costPath } = useMemo(() => {
    if (!data || data.length === 0) {
      return { runsPath: '', tokensPath: '', costPath: '' };
    }

    const runsPoints = data.map((d, i) => `${xScale(i)},${yRightScale(d.runs, maxRuns)}`);
    const tokensPoints = data.map((d, i) => `${xScale(i)},${yLeftScale(d.tokens)}`);
    const costPoints = data.map((d, i) => `${xScale(i)},${yRightScale(d.cost, maxCost)}`);

    return {
      runsPath: `M ${runsPoints.join(' L ')}`,
      tokensPath: `M ${tokensPoints.join(' L ')}`,
      costPath: `M ${costPoints.join(' L ')}`
    };
  }, [data, xScale, yLeftScale, yRightScale, maxTokens, maxRuns, maxCost]);

  // Format numbers for display
  const formatNumber = (num) => {
    if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
    return num.toFixed(0);
  };

  const formatCost = (cost) => {
    if (cost >= 1000) return `$${(cost / 1000).toFixed(1)}K`;
    if (cost >= 1) return `$${cost.toFixed(2)}`;
    return `$${cost.toFixed(4)}`;
  };

  // Handle series toggle
  const toggleSeries = (series) => {
    const updates = {
      showRuns: showRuns,
      showTokens: showTokens,
      showCost: showCost
    };

    switch(series) {
      case 'runs':
        updates.showRuns = !showRuns;
        setShowRuns(!showRuns);
        break;
      case 'tokens':
        updates.showTokens = !showTokens;
        setShowTokens(!showTokens);
        break;
      case 'cost':
        updates.showCost = !showCost;
        setShowCost(!showCost);
        break;
    }

    onSaveUserSettings?.({
      ...savedUserSettings,
      ...updates
    });
  };

  if (!data || data.length === 0) {
    return (
      <div style={{
        width: '100%',
        height: '100%',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: styles.colors.surface,
        borderRadius: styles.borders?.radius || '4px',
        color: styles.colors.textSecondary
      }}>
        No data available for the selected time range
      </div>
    );
  }

  return (
    <div 
      ref={chartRef}
      style={{
        width: '100%',
        height: '100%',
        backgroundColor: styles.colors.surface,
        borderRadius: styles.borders?.radius || '4px',
        padding: styles.spacing.md
      }}
    >
      {/* Legend */}
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        gap: styles.spacing.lg,
        marginBottom: styles.spacing.md
      }}>
        <label 
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: styles.spacing.xs,
            cursor: 'pointer',
            opacity: showRuns ? 1 : 0.5
          }}
        >
          <input
            type="checkbox"
            checked={showRuns}
            onChange={() => toggleSeries('runs')}
            style={{ cursor: 'pointer' }}
          />
          <span style={{
            width: '20px',
            height: '3px',
            backgroundColor: styles.colors.primary
          }} />
          <span style={{ color: styles.colors.text, fontSize: styles.typography.fontSize.sm }}>Runs</span>
        </label>

        <label 
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: styles.spacing.xs,
            cursor: 'pointer',
            opacity: showTokens ? 1 : 0.5
          }}
        >
          <input
            type="checkbox"
            checked={showTokens}
            onChange={() => toggleSeries('tokens')}
            style={{ cursor: 'pointer' }}
          />
          <span style={{
            width: '20px',
            height: '3px',
            backgroundColor: styles.colors.success
          }} />
          <span style={{ color: styles.colors.text, fontSize: styles.typography.fontSize.sm }}>Tokens</span>
        </label>

        <label 
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: styles.spacing.xs,
            cursor: 'pointer',
            opacity: showCost ? 1 : 0.5
          }}
        >
          <input
            type="checkbox"
            checked={showCost}
            onChange={() => toggleSeries('cost')}
            style={{ cursor: 'pointer' }}
          />
          <span style={{
            width: '20px',
            height: '3px',
            backgroundColor: styles.colors.warning
          }} />
          <span style={{ color: styles.colors.text, fontSize: styles.typography.fontSize.sm }}>Cost</span>
        </label>
      </div>

      {/* Chart */}
      <svg 
        width={dimensions.width} 
        height={dimensions.height - 50}
        style={{ display: 'block' }}
      >
        <g transform={`translate(${margin.left},${margin.top})`}>
          {/* Grid lines */}
          {[0, 0.25, 0.5, 0.75, 1].map((val) => (
            <line
              key={val}
              x1={0}
              y1={chartHeight * (1 - val)}
              x2={chartWidth}
              y2={chartHeight * (1 - val)}
              stroke={styles.colors.border}
              strokeOpacity={0.3}
              strokeDasharray="2,2"
            />
          ))}

          {/* Lines */}
          {showTokens && (
            <path
              d={tokensPath}
              fill="none"
              stroke={styles.colors.success}
              strokeWidth={2}
            />
          )}
          {showRuns && (
            <path
              d={runsPath}
              fill="none"
              stroke={styles.colors.primary}
              strokeWidth={2}
            />
          )}
          {showCost && (
            <path
              d={costPath}
              fill="none"
              stroke={styles.colors.warning}
              strokeWidth={2}
            />
          )}

          {/* Data points */}
          {data.map((point, i) => (
            <g key={i}>
              {showRuns && (
                <circle
                  cx={xScale(i)}
                  cy={yRightScale(point.runs, maxRuns)}
                  r={selectedPoint?.date === point.date ? 6 : 4}
                  fill={styles.colors.primary}
                  stroke="white"
                  strokeWidth={2}
                  style={{ cursor: 'pointer' }}
                  onClick={() => onPointClick?.(point)}
                  onMouseEnter={() => setHoveredPoint({ ...point, index: i })}
                  onMouseLeave={() => setHoveredPoint(null)}
                />
              )}
              {showTokens && (
                <circle
                  cx={xScale(i)}
                  cy={yLeftScale(point.tokens)}
                  r={selectedPoint?.date === point.date ? 6 : 4}
                  fill={styles.colors.success}
                  stroke="white"
                  strokeWidth={2}
                  style={{ cursor: 'pointer' }}
                  onClick={() => onPointClick?.(point)}
                  onMouseEnter={() => setHoveredPoint({ ...point, index: i })}
                  onMouseLeave={() => setHoveredPoint(null)}
                />
              )}
              {showCost && (
                <circle
                  cx={xScale(i)}
                  cy={yRightScale(point.cost, maxCost)}
                  r={selectedPoint?.date === point.date ? 6 : 4}
                  fill={styles.colors.warning}
                  stroke="white"
                  strokeWidth={2}
                  style={{ cursor: 'pointer' }}
                  onClick={() => onPointClick?.(point)}
                  onMouseEnter={() => setHoveredPoint({ ...point, index: i })}
                  onMouseLeave={() => setHoveredPoint(null)}
                />
              )}
            </g>
          ))}

          {/* Axes */}
          {/* X-axis */}
          <line
            x1={0}
            y1={chartHeight}
            x2={chartWidth}
            y2={chartHeight}
            stroke={styles.colors.border}
