function AIDistributionChart({ data, activeTab, groupBy, styles, utilities, components, callbacks, savedUserSettings, onSaveUserSettings }) {
  console.log('[AIDistributionChart] Rendering with', data?.length || 0, 'items');

  const [chartType, setChartType] = useState(savedUserSettings?.chartType || 'pie');
  const [metric, setMetric] = useState(savedUserSettings?.metric || 'runs');

  // Aggregate data by agent/prompt
  const aggregatedData = useMemo(() => {
    if (!data || data.length === 0) return [];

    const nameField = activeTab === 'agents' ? 'Agent' : 'Prompt';
    const idField = activeTab === 'agents' ? 'AgentID' : 'PromptID';
    const grouped = {};

    data.forEach(item => {
      const key = item[nameField] || item[idField] || 'Unknown';

      if (!grouped[key]) {
        grouped[key] = {
          name: key,
          runs: 0,
          tokens: 0,
          cost: 0
        };
      }

      grouped[key].runs++;
      grouped[key].tokens += (item.TotalTokensUsed || item.TokensUsed || 0);
      grouped[key].cost += (item.TotalCost || 0);
    });

    // Sort by selected metric and take top 10
    const sorted = Object.values(grouped)
      .sort((a, b) => b[metric] - a[metric])
      .slice(0, 10);

    // Calculate totals for percentage
    const total = sorted.reduce((sum, item) => sum + item[metric], 0);

    return sorted.map(item => ({
      ...item,
      percentage: total > 0 ? (item[metric] / total) * 100 : 0
    }));
  }, [data, activeTab, metric]);

  // Generate colors
  const colors = [
    styles.colors.primary,
    styles.colors.success,
    styles.colors.warning,
    styles.colors.error,
    '#9b59b6',
    '#3498db',
    '#1abc9c',
    '#f39c12',
    '#95a5a6',
    '#34495e'
  ];

  const handleChartTypeChange = (type) => {
    setChartType(type);
    onSaveUserSettings?.({
      ...savedUserSettings,
      chartType: type
    });
  };

  const handleMetricChange = (newMetric) => {
    setMetric(newMetric);
    onSaveUserSettings?.({
      ...savedUserSettings,
      metric: newMetric
    });
  };

  const formatValue = (value) => {
    if (metric === 'cost') return `$${value.toFixed(2)}`;
    if (metric === 'tokens' && value > 1000) return `${(value / 1000).toFixed(1)}K`;
    return value.toLocaleString();
  };

  const renderPieChart = () => {
    const size = 300;
    const center = size / 2;
    const radius = size / 2 - 20;

    let currentAngle = -Math.PI / 2;

    return (
      <svg width={size} height={size}>
        {aggregatedData.map((item, index) => {
          const angle = (item.percentage / 100) * Math.PI * 2;
          const startX = center + Math.cos(currentAngle) * radius;
          const startY = center + Math.sin(currentAngle) * radius;
          const endX = center + Math.cos(currentAngle + angle) * radius;
          const endY = center + Math.sin(currentAngle + angle) * radius;
          const largeArc = angle > Math.PI ? 1 : 0;

          const path = `
            M ${center} ${center}
            L ${startX} ${startY}
            A ${radius} ${radius} 0 ${largeArc} 1 ${endX} ${endY}
            Z
          `;

          // Calculate label position
          const labelAngle = currentAngle + angle / 2;
          const labelRadius = radius * 0.7;
          const labelX = center + Math.cos(labelAngle) * labelRadius;
          const labelY = center + Math.sin(labelAngle) * labelRadius;

          currentAngle += angle;

          return (
            <g key={index}>
              <path
                d={path}
                fill={colors[index]}
                stroke="white"
                strokeWidth={2}
                opacity={0.8}
                style={{ cursor: 'pointer' }}
              >
                <title>
                  {item.name}: {formatValue(item[metric])} ({item.percentage.toFixed(1)}%)
                </title>
              </path>
              {item.percentage > 5 && (
                <text
                  x={labelX}
                  y={labelY}
                  textAnchor="middle"
                  dominantBaseline="middle"
                  fontSize={12}
                  fill="white"
                  fontWeight="bold"
                  pointerEvents="none"
                >
                  {item.percentage.toFixed(0)}%
                </text>
              )}
            </g>
          );
        })}
      </svg>
    );
  };

  const renderBarChart = () => {
    const maxValue = Math.max(...aggregatedData.map(d => d[metric]));
    const barHeight = 30;
    const chartWidth = 400;
    const chartHeight = aggregatedData.length * (barHeight + 10);

    return (
      <svg width={chartWidth} height={chartHeight}>
        {aggregatedData.map((item, index) => {
          const barWidth = (item[metric] / maxValue) * (chartWidth - 150);
          const y = index * (barHeight + 10);

          return (
            <g key={index}>
              <rect
                x={0}
                y={y}
                width={barWidth}
                height={barHeight}
                fill={colors[index]}
                opacity={0.8}
              />
              <text
                x={barWidth + 5}
                y={y + barHeight / 2}
                dominantBaseline="middle"
                fontSize={12}
                fill={styles.colors.text}
              >
                {formatValue(item[metric])}
              </text>
              <text
                x={chartWidth - 5}
                y={y + barHeight / 2}
                textAnchor="end"
                dominantBaseline="middle"
                fontSize={11}
                fill={styles.colors.textSecondary}
              >
                {item.name.length > 20 ? item.name.substring(0, 20) + '...' : item.name}
              </text>
            </g>
          );
        })}
      </svg>
    );
  };

  return (
    <div style={{
      width: '100%',
      height: '100%',
      backgroundColor: styles.colors.surface,
      borderRadius: styles.borders?.radius || '4px',
      padding: styles.spacing.md,
      display: 'flex',
      flexDirection: 'column'
    }}>
      {/* Header */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: styles.spacing.md
      }}>
        <h3 style={{
          margin: 0,
          color: styles.colors.text,
          fontSize: styles.typography.fontSize.lg
        }}>
          Distribution Analysis
        </h3>

        {/* Controls */}
        <div style={{
          display: 'flex',
          gap: styles.spacing.sm
        }}>
          {/* Metric selector */}
          <select
            value={metric}
            onChange={(e) => handleMetricChange(e.target.value)}
            style={{
              padding: styles.spacing.xs,
              border: `1px solid ${styles.colors.border}`,
              borderRadius: styles.borders?.radius || '4px',
              backgroundColor: styles.colors.background,
              color: styles.colors.text,
              fontSize: styles.typography.fontSize.sm
            }}
          >
            <option value="runs">Runs</option>
            <option value="tokens">Tokens</option>
            <option value="cost">Cost</option>
          </select>

          {/* Chart type toggle */}
          <div style={{
            display: 'flex',
            gap: '2px',
            backgroundColor: styles.colors.background,
            padding: '2px',
            borderRadius: styles.borders?.radius || '4px'
          }}>
            <button
              onClick={() => handleChartTypeChange('pie')}
              style={{
                padding: `${styles.spacing.xs} ${styles.spacing.sm}`,
                backgroundColor: chartType === 'pie' ? styles.colors.primary : 'transparent',
                color: chartType === 'pie' ? 'white' : styles.colors.text,
                border: 'none',
                borderRadius: styles.borders?.radius || '4px',
                cursor: 'pointer',
                fontSize: styles.typography.fontSize.sm
              }}
            >
              Pie
            </button>
            <button
