function AIDetailTable({ data, activeTab, styles, utilities, components, callbacks, savedUserSettings, onSaveUserSettings }) {
  console.log('[AIDetailTable] Rendering with', data?.length || 0, 'items');

  const [sortField, setSortField] = useState(savedUserSettings?.sortField || 'timestamp');
  const [sortDirection, setSortDirection] = useState(savedUserSettings?.sortDirection || 'desc');
  const [currentPage, setCurrentPage] = useState(0);
  const [expandedRows, setExpandedRows] = useState(new Set());

  const rowsPerPage = 25;

  // Get the correct timestamp field
  const timestampField = activeTab === 'agents' ? 'StartedAt' : 'RunAt';
  const nameField = activeTab === 'agents' ? 'Agent' : 'Prompt';
  const idField = activeTab === 'agents' ? 'AgentID' : 'PromptID';

  // Sort data
  const sortedData = useMemo(() => {
    if (!data || data.length === 0) return [];

    const sorted = [...data].sort((a, b) => {
      let aVal = a[sortField];
      let bVal = b[sortField];

      // Handle null/undefined
      if (aVal == null) return sortDirection === 'asc' ? 1 : -1;
      if (bVal == null) return sortDirection === 'asc' ? -1 : 1;

      // Compare values
      if (typeof aVal === 'string') {
        aVal = aVal.toLowerCase();
        bVal = bVal.toLowerCase();
      }

      if (aVal < bVal) return sortDirection === 'asc' ? -1 : 1;
      if (aVal > bVal) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });

    return sorted;
  }, [data, sortField, sortDirection]);

  // Paginate data
  const paginatedData = useMemo(() => {
    const start = currentPage * rowsPerPage;
    const end = start + rowsPerPage;
    return sortedData.slice(start, end);
  }, [sortedData, currentPage, rowsPerPage]);

  const totalPages = Math.ceil(sortedData.length / rowsPerPage);

  const handleSort = (field) => {
    if (sortField === field) {
      const newDirection = sortDirection === 'asc' ? 'desc' : 'asc';
      setSortDirection(newDirection);
      onSaveUserSettings?.({
        ...savedUserSettings,
        sortField: field,
        sortDirection: newDirection
      });
    } else {
      setSortField(field);
      setSortDirection('asc');
      onSaveUserSettings?.({
        ...savedUserSettings,
        sortField: field,
        sortDirection: 'asc'
      });
    }
    setCurrentPage(0);
  };

  const toggleRowExpansion = (id) => {
    const newExpanded = new Set(expandedRows);
    if (newExpanded.has(id)) {
      newExpanded.delete(id);
    } else {
      newExpanded.add(id);
    }
    setExpandedRows(newExpanded);
  };

  const formatTimestamp = (timestamp) => {
    if (!timestamp) return '-';
    const date = new Date(timestamp);
    return date.toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDuration = (ms) => {
    if (!ms) return '-';
    if (ms < 1000) return `${ms}ms`;
    if (ms < 60000) return `${(ms / 1000).toFixed(1)}s`;
    return `${(ms / 60000).toFixed(1)}m`;
  };

  const exportToCSV = () => {
    console.log('[AIDetailTable] Exporting to CSV');

    const headers = [
      'Timestamp',
      activeTab === 'agents' ? 'Agent' : 'Prompt',
      'Success',
      'Tokens',
      'Cost'
    ];

    // Add Duration header only for prompts
    if (activeTab === 'prompts') {
      headers.push('Duration');
    }

    const rows = sortedData.map(item => {
      const row = [
        item[timestampField],
        item[nameField] || item[idField],
        item.Success ? 'Yes' : 'No',
        (item.TotalTokensUsed || item.TokensUsed) || 0,
        item.TotalCost || 0
      ];

      // Add Duration value only for prompts
      if (activeTab === 'prompts') {
        row.push(item.ExecutionTimeMS || 0);
      }

      return row;
    });

    const csv = [
      headers.join(','),
      ...rows.map(row => row.join(','))
    ].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `ai-${activeTab}-details-${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const columns = [
    { field: timestampField, label: 'Timestamp', width: '150px' },
    { field: nameField, label: activeTab === 'agents' ? 'Agent' : 'Prompt', width: 'auto' },
    { field: 'Success', label: 'Status', width: '80px' },
    { field: 'TotalTokens', label: 'Tokens', width: '100px' },
    { field: 'TotalCost', label: 'Cost', width: '100px' }
  ];

  // Add Duration column only for Prompt Runs (Agent Runs don't have ExecutionTimeMS)
  if (activeTab === 'prompts') {
    columns.push({ field: 'ExecutionTimeMS', label: 'Duration', width: '100px' });
  }

  return (
    <div style={{
      width: '100%',
      height: '100%',
      backgroundColor: styles.colors.surface,
      borderRadius: styles.borders?.radius || '4px',
      display: 'flex',
      flexDirection: 'column'
    }}>
      {/* Header */}
      <div style={{
        padding: styles.spacing.md,
        borderBottom: `1px solid ${styles.colors.border}`,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h3 style={{
          margin: 0,
          color: styles.colors.text,
          fontSize: styles.typography.fontSize.lg
        }}>
          Detailed Records ({sortedData.length})
        </h3>

        <button
          onClick={exportToCSV}
          style={{
            padding: `${styles.spacing.sm} ${styles.spacing.md}`,
            backgroundColor: styles.colors.primary,
            color: 'white',
            border: 'none',
            borderRadius: styles.borders?.radius || '4px',
            cursor: 'pointer',
            fontSize: styles.typography.fontSize.sm
          }}
        >
          Export CSV
        </button>
      </div>

      {/* Table */}
      <div style={{ flex: 1, overflow: 'auto' }}>
        {paginatedData.length === 0 ? (
          <div style={{
            padding: styles.spacing.xl,
            textAlign: 'center',
            color: styles.colors.textSecondary
          }}>
