exampleUsage:
// Simple column configuration (backward compatible)
<DataGrid
  entityName="Products"
  data={products}
  columns={['Name', 'SKU', 'Price', 'Category', 'InStock']}
  onRowClick={handleRowClick}
/>

// Advanced column configuration with custom rendering
<DataGrid
  entityName="Products"
  data={products}
  columns={[
    { field: 'Name', header: 'Product Name', width: '200px' },
    { 
      field: 'Price',
      header: 'Unit Price',
      render: (value) => (
        <span style={{ fontWeight: 'bold', color: '#059669' }}>
          {value >= 1000 ? `$${(value/1000).toFixed(1)}K` : `$${value}`}
        </span>
      ),
      width: '120px',
      sortable: true
    },
    {
      field: 'InStock',
      header: 'Availability',
      render: (value) => (
        <span style={{ 
          padding: '2px 8px',
          borderRadius: '12px',
          backgroundColor: value ? '#D1FAE5' : '#FEE2E2',
          color: value ? '#065F46' : '#991B1B'
        }}>
          {value ? 'In Stock' : 'Out of Stock'}
        </span>
      ),
      width: '130px',
      sortable: false
    },
    'Category'  // Simple string still works
  ]}
  sorting={true}
  paging={true}
  pageSize={20}
  filtering={true}
  filterFields={['Name', 'SKU', 'Category']}
  selectionMode="none"
  onRowClick={handleRowClick}
  onSelectionChanged={handleSelection}
  onPageChanged={handlePageChange}
  onSortChanged={handleSortChange}
  onFilterChanged={handleFilterChange}
/>
