usageExample:
// Basic usage
<AIInsightsPanel
  insights={aiInsights}
  loading={loadingInsights}
  error={insightsError}
  onGenerate={generateInsights}
  title="Sales Analysis Insights"
  iconColor="#10B981"
/>

// With custom buttons
<AIInsightsPanel
  insights={aiInsights}
  loading={loadingInsights}
  error={insightsError}
  onGenerate={generateInsights}
  onClose={() => setShowInsights(false)}
  customButtons={[
    {
      icon: 'fa-solid fa-share',
      title: 'Share',
      onClick: handleShare
    }
  ]}
/>
