usageExample:
// UI Mode with dropdown
<DataExportPanel
  data={tableData}
  columns={columns}
  getHtmlElement={() => dashboardRef.current}
  getAiInsightsElement={() => aiInsightsRef.current}
  filename="sales-report"
  formats={['csv', 'excel', 'pdf']}
  buttonStyle="dropdown"
/>

// Headless mode for programmatic export
const exportPanel = <DataExportPanel
  mode="headless"
  data={data}
  columns={columns}
/>;
// Then call: exportPanel.exportToCSV() or exportPanel.exportToPDF()
