exampleUsage:
// Basic usage (no filters)
<DealPipelineVisualization
  utilities={utilities}
  styles={styles}
  components={components}
  callbacks={callbacks}
  savedUserSettings={savedUserSettings}
  onSaveUserSettings={onSaveUserSettings}
/>

// With event handlers
<DealPipelineVisualization
  onStageClick={(event) => {
    console.log(`Stage ${event.stage}: ${event.dealCount} deals, $${event.totalValue}`);
  }}
  onDealClick={(event) => {
    console.log(`Deal ${event.dealName} clicked`);
    // Navigate to deal detail page
    router.push(`/deals/${event.dealId}`);
  }}
  utilities={utilities}
  styles={styles}
  components={components}
  callbacks={callbacks}
  savedUserSettings={savedUserSettings}
  onSaveUserSettings={onSaveUserSettings}
/>

// With custom configuration via savedUserSettings
<DealPipelineVisualization
  savedUserSettings={{
    stage: 'Proposal',
    accountType: 'Enterprise',
    minAmount: 50000,
    monthsBack: 12,
    daysBack: 180,
    chartType: 'doughnut'
  }}
  utilities={utilities}
  styles={styles}
  components={components}
  callbacks={callbacks}
  onSaveUserSettings={onSaveUserSettings}
/>
