exampleUsage:
// Basic usage
<InvoiceAgingAnalysis
  utilities={utilities}
  styles={styles}
  components={components}
  callbacks={callbacks}
  savedUserSettings={savedUserSettings}
  onSaveUserSettings={onSaveUserSettings}
/>

// With event handlers
<InvoiceAgingAnalysis
  onBucketClick={(event) => {
    console.log(`Bucket ${event.bucket}: ${event.invoiceCount} invoices, $${event.totalOutstanding}`);
  }}
  onAccountClick={(event) => {
    console.log(`Account ${event.accountName}: ${event.invoiceCount} invoices overdue`);
  }}
  onInvoiceClick={(event) => {
    console.log(`Invoice ${event.invoiceNumber} clicked`);
    // Navigate to invoice detail page
    router.push(`/invoices/${event.invoiceId}`);
  }}
  utilities={utilities}
  styles={styles}
  components={components}
  callbacks={callbacks}
  savedUserSettings={savedUserSettings}
  onSaveUserSettings={onSaveUserSettings}
/>

// With pre-configured filters via savedUserSettings
<InvoiceAgingAnalysis
  savedUserSettings={{
    appliedMinOutstanding: 5000,
    appliedAccountType: 'Customer',
    appliedMonthsBack: 6,
    appliedPaymentMethod: 'Credit Card',
    appliedTopN: 20
  }}
  utilities={utilities}
  styles={styles}
  components={components}
  callbacks={callbacks}
  onSaveUserSettings={onSaveUserSettings}
/>
