  @Specification_68e43a51 = N'{
  "name": "VelocityHeatmap",
  "namespace": "CRM/Analytics",
  "type": "Chart",
  "location": "embedded",
  "code": "// VelocityHeatmap Sub-component  \nconst VelocityHeatmap = ({ data, onCellClick, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) => {\n  const chartRef = useRef(null);\n\n  useEffect(() => {\n    if (!chartRef.current || !data || !window.echarts) return;\n\n    const chart = window.echarts.init(chartRef.current);\n\n    const option = {\n      tooltip: {\n        position: ''top'',\n        formatter: function(params) {\n          return `$' + '{params.name}<br/>\n                  $' + '{params.value[2]} deals<br/>\n                  Avg: $' + '{params.value[3]} days`;\n        }\n      },\n      grid: {\n        height: ''70%'',\n        top: ''10%'',\n        left: ''15%'',\n        right: ''5%''\n      },\n      xAxis: {\n        type: ''category'',\n        data: data.stages,\n        splitArea: { show: true },\n        axisLabel: { rotate: 45 }\n      },\n      yAxis: {\n        type: ''category'',\n        data: data.months,\n        splitArea: { show: true }\n      },\n      visualMap: {\n        min: 0,\n        max: data.maxDays || 60,\n        calculable: true,\n        orient: ''horizontal'',\n        left: ''center'',\n        bottom: ''5%'',\n        inRange: {\n          color: [''#28a745'', ''#ffc107'', ''#dc3545'']\n        }\n      },\n      series: [{\n        name: ''Deal Velocity'',\n        type: ''heatmap'',\n        data: data.values,\n        label: {\n          show: true,\n          formatter: function(params) {\n            return params.value[3] || '''';\n          }\n        },\n        emphasis: {\n          itemStyle: {\n            shadowBlur: 10,\n            shadowColor: ''rgba(0, 0, 0, 0.5)''\n          }\n        }\n      }]\n    };\n\n    chart.setOption(option);\n\n    if (onCellClick) {\n      chart.on(''click'', function(params) {\n        onCellClick({\n          stage: data.stages[params.value[0]],\n          month: data.months[params.value[1]],\n          dealCount: params.value[2],\n          avgDays: params.value[3]\n        });\n      });\n    }\n\n    const handleResize = () => chart.resize();\n    window.addEventListener(''resize'', handleResize);\n\n    return () => {\n      window.removeEventListener(''resize'', handleResize);\n      chart.dispose();\n    };\n  }, [data, onCellClick]);\n\n  return (\n    <div \n      ref={chartRef} \n      style={{ \n        width: ''100%'', \n        height: ''400px'',\n        minHeight: ''300px''\n      }}\n    />\n  );\n}",
  "functionalRequirements": "## Velocity Heatmap\n\n### Purpose\nVisualize deal velocity across stages using a heatmap.\n\n### Features\n- Grid-based heatmap\n- Color intensity for velocity\n- Stage labels\n- Time period labels\n- Hover tooltips\n- Legend",
