  "type": "Other",
  "location": "embedded",
  "code": "// ProductDetailsPanel Sub-component\nconst ProductDetailsPanel = ({ data, isOpen, onClose, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) => {\n  const [activeTab, setActiveTab] = useState(''details'');\n\n  return (\n    <div \n      className=\"productdetailspanel-panel\"\n      style={{\n        position: ''fixed'',\n        top: 0,\n        right: isOpen ? 0 : ''-400px'',\n        width: ''400px'',\n        height: ''100%'',\n        backgroundColor: ''#fff'',\n        boxShadow: ''-2px 0 10px rgba(0,0,0,0.1)'',\n        transition: ''right 0.3s ease'',\n        zIndex: 1000,\n        display: ''flex'',\n        flexDirection: ''column'',\n        overflow: ''hidden''\n      }}\n    >\n      <div style={{\n        padding: ''20px'',\n        borderBottom: ''1px solid #e0e0e0'',\n        backgroundColor: ''#f5f5f5'',\n        display: ''flex'',\n        justifyContent: ''space-between'',\n        alignItems: ''center''\n      }}>\n        <h3 style={{ margin: 0 }}>{data?.title || ''ProductDetailsPanel''}</h3>\n        <button \n          onClick={onClose}\n          style={{\n            background: ''none'',\n            border: ''none'',\n            fontSize: ''24px'',\n            cursor: ''pointer'',\n            padding: ''0'',\n            width: ''30px'',\n            height: ''30px''\n          }}\n        >√ó</button>\n      </div>\n\n      <div style={{ padding: ''15px'', borderBottom: ''1px solid #e0e0e0'' }}>\n        <div style={{ display: ''flex'', gap: ''10px'' }}>\n          <button\n            onClick={() => setActiveTab(''details'')}\n            style={{\n              padding: ''8px 15px'',\n              border: ''1px solid #ddd'',\n              borderRadius: ''4px'',\n              background: activeTab === ''details'' ? ''#007bff'' : ''#fff'',\n              color: activeTab === ''details'' ? ''#fff'' : ''#333'',\n              cursor: ''pointer''\n            }}\n          >\n            Details\n          </button>\n          <button\n            onClick={() => setActiveTab(''history'')}\n            style={{\n              padding: ''8px 15px'',\n              border: ''1px solid #ddd'',\n              borderRadius: ''4px'',\n              background: activeTab === ''history'' ? ''#007bff'' : ''#fff'',\n              color: activeTab === ''history'' ? ''#fff'' : ''#333'',\n              cursor: ''pointer''\n            }}\n          >\n            History\n          </button>\n        </div>\n      </div>\n\n      <div style={{ flex: 1, overflowY: ''auto'', padding: ''20px'' }}>\n        {activeTab === ''details'' && (\n          <div>\n            {data && Object.entries(data).map(([key, value]) => (\n              <div key={key} style={{ marginBottom: ''15px'' }}>\n                <div style={{ fontSize: ''12px'', color: ''#666'', marginBottom: ''3px'' }}>{key}</div>\n                <div style={{ fontSize: ''14px'', color: ''#333'' }}>{String(value)}</div>\n              </div>\n            ))}\n          </div>\n        )}\n        {activeTab === ''history'' && (\n          <div>\n            <p style={{ color: ''#666'' }}>History information would be displayed here.</p>\n          </div>\n        )}\n      </div>\n    </div>\n  );\n}",
