SET
  @Specification_e560d4a5 = N'{"name":"EntityBrowser","title":"Entity Browser","description":"A comprehensive entity browser with multi-panel display showing entities in a grid or card view with a sliding details panel, collapsible filters, sorting, and entity record opening capability.","type":"dashboard","functionalRequirements":"## Entity Browser Requirements\n\n### Core Functionality\n- Display entities in a responsive grid or card layout based on user preference\n- Allow users to select view mode (grid vs card)\n- Click on an entity to slide in a details panel from the right\n- Show entity metadata including fields and relationships in the details panel\n- Provide a collapsible filter panel on the left side\n- Support sorting by multiple fields with visual indicators\n- Include a search bar for quick entity filtering\n- Provide an ''Open'' button to trigger the OpenEntityRecord callback\n- Remember user''s last selected entity and view preferences\n\n### UX Considerations\n- Smooth animations for panel transitions\n- Responsive design that works on different screen sizes\n- Loading states while fetching data\n- Empty states with helpful messages\n- Keyboard navigation support (arrow keys, tab, enter)\n- Visual feedback for hover and selection states\n- Maintain scroll position when switching between entities","dataRequirements":{"mode":"views","entities":[{"name":"Entities","description":"Metadata about all entities in the system","displayFields":["ID","Name","DisplayName","NameSuffix","Description","SchemaName","BaseTable","BaseView"],"filterFields":["SchemaName","BaseTable"],"sortFields":["Name","DisplayName"],"fieldMetadata":[{"name":"ID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":true,"description":"Unique identifier for the entity"},{"name":"Name","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"System name of the entity"},{"name":"DisplayName","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"User-friendly display name for the entity"},{"name":"NameSuffix","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Optional suffix appended to entity names for display purposes"},{"name":"Description","sequence":5,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Description of the entity"},{"name":"SchemaName","sequence":6,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Database schema name"},{"name":"BaseTable","sequence":7,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Base table in the database"},{"name":"BaseView","sequence":8,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Base view used for the entity"}],"permissionLevelNeeded":["read"],"usageContext":"Main entity list display and filtering"},{"name":"Entity Fields","description":"Fields belonging to each entity","displayFields":["Name","DisplayName","Type","Length","AllowsNull","IsPrimaryKey","IsUnique"],"filterFields":["EntityID"],"sortFields":["Sequence","Name"],"fieldMetadata":[{"name":"EntityID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":false,"description":"Reference to parent entity"},{"name":"Name","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Field name"},{"name":"DisplayName","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"User-friendly field name"},{"name":"Type","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Data type of the field"},{"name":"Length","sequence":5,"defaultInView":true,"type":"int","allowsNull":true,"isPrimaryKey":false,"description":"Maximum length for string fields"},{"name":"AllowsNull","sequence":6,"defaultInView":true,"type":"bit","allowsNull":false,"isPrimaryKey":false,"description":"Whether field allows null values"},{"name":"IsPrimaryKey","sequence":7,"defaultInView":true,"type":"bit","allowsNull":false,"isPrimaryKey":false,"description":"Whether field is part of primary key"},{"name":"IsUnique","sequence":8,"defaultInView":true,"type":"bit","allowsNull":false,"isPrimaryKey":false,"description":"Whether field must be unique"},{"name":"Sequence","sequence":9,"defaultInView":false,"type":"int","allowsNull":false,"isPrimaryKey":false,"description":"Display order of the field"}],"permissionLevelNeeded":["read"],"usageContext":"Details panel to show entity fields"},{"name":"Entity Relationships","description":"Relationships between entities","displayFields":["RelatedEntity","Type","DisplayName","RelatedEntityJoinField"],"filterFields":["EntityID"],"sortFields":["Sequence","RelatedEntity"],"fieldMetadata":[{"name":"EntityID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":false,"description":"Reference to parent entity"},{"name":"RelatedEntity","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"The related entity in the relationship"},{"name":"Type","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Type of relationship (One to Many, Many to One, etc.)"},{"name":"DisplayName","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"User-friendly name for the relationship"},{"name":"RelatedEntityJoinField","sequence":5,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"The field in the related entity that joins to this entity"},{"name":"Sequence","sequence":6,"defaultInView":false,"type":"int","allowsNull":false,"isPrimaryKey":false,"description":"Display order"}],"permissionLevelNeeded":["read"],"usageContext":"Details panel to show entity relationships"}],"queries":[],"description":"This component requires access to entity metadata including entities, their fields, and relationships to provide a comprehensive entity browsing experience"},"technicalDesign":"## Technical Architecture\n\n### Component Structure\n- **Root Component (EntityBrowser)**: Manages overall layout and state coordination\n- **EntityList (Child)**: Displays entities in grid/card view with sorting\n- **EntityDetails (Child)**: Sliding panel showing entity fields and relationships\n- **EntityFilter (Child)**: Collapsible filter panel with dynamic filters\n\n### State Management\n- Selected entity ID (persisted in savedUserSettings)\n- View mode (grid/card) (persisted)\n- Active filters (persisted)\n- Sort configuration (persisted)\n- Panel visibility states (details open, filters collapsed)\n- Search query\n- Loading states for async operations\n\n### Layout\n```\n+------------------+------------------------+------------------+\n|                  |                        |                  |\n|   Filter Panel   |    Entity Grid/Cards   |  Details Panel   |\n|   (Collapsible)  |    (Main Content)      |    (Sliding)     |\n|                  |                        |                  |\n|  [Schema Filter] |  +-----+  +-----+      |  Entity: Orders  |\n|  [Table Filter]  |  | Card |  | Card |     |                  |\n|  [Search Box]    |  +-----+  +-----+      |  Fields:         |\n|                  |                        |  - ID            |\n|  Sort By:        |  +-----+  +-----+      |  - CustomerID    |\n|  [Name ↓]        |  | Card |  | Card |     |  - OrderDate     |\n|                  |  +-----+  +-----+      |                  |\n|                  |                        |  Relationships:  |\n|                  |                        |  → Customers     |\n|                  |                        |  → OrderItems    |\n|                  |                        |                  |\n|                  |                        |  [Open Record]   |\n+------------------+------------------------+------------------+\n```\n\n### Data Flow\n1. Root component loads entities on mount\n2. Passes entity data to EntityList\n3. EntityList handles selection and passes selectedId up\n4. Root loads fields/relationships for selected entity\n5. Passes detailed data to EntityDetails\n6. Filter changes trigger data reload\n7. All user preferences saved via onSaveUserSettings\n\n### Interaction Patterns\n- Click entity card → Select and open details\n- Click filter → Apply and reload data\n- Click sort → Update sort and reload\n- Click ''Open'' → Trigger OpenEntityRecord callback\n- Press Escape → Close details panel\n- Click outside → Close details panel","properties":[],"events":[],"exampleUsage":"<EntityBrowser\n  utilities={utilities}\n  styles={styles}\n  components={components}\n  callbacks={callbacks}\n  savedUserSettings={savedUserSettings}\n  onSaveUserSettings={onSaveUserSettings}\n/>","dependencies":[{"name":"EntityList","title":"Entity List","description":"Displays entities in a grid or card layout with sorting capabilities","type":"table","functionalRequirements":"## Entity List Requirements\n\n- Display entities in grid or card view based on viewMode prop\n- Support sorting by multiple fields\n- Handle entity selection and notify parent\n- Show loading state while data loads\n- Display record count badges\n- Highlight selected entity\n- Support keyboard navigation","dataRequirements":null,"technicalDesign":"## Technical Design\n\n### Props\n- entities: Array of entity objects\n- viewMode: ''grid'' | ''card''\n- selectedEntityId: Currently selected entity\n- onSelectEntity: Callback when entity selected\n- sortBy: Current sort field\n- sortDirection: ''asc'' | ''desc''\n- onSortChange: Callback for sort changes\n\n### Rendering\n- Grid mode: Compact table with columns\n- Card mode: Cards with entity info\n- Sort indicators in headers\n- Selection highlighting","properties":[{"name":"entities","description":"Array of entity objects to display","type":"array","required":true},{"name":"viewMode","description":"Display mode - grid or card view","type":"string","required":true,"possibleValues":["grid","card"]},{"name":"selectedEntityId","description":"ID of the currently selected entity","type":"string","required":true},{"name":"onSelectEntity","description":"Callback when an entity is selected","type":"function","required":true},{"name":"sortBy","description":"Field to sort by","type":"string","required":true,"defaultValue":"Name"},{"name":"sortDirection","description":"Sort direction","type":"string","required":true,"defaultValue":"asc","possibleValues":["asc","desc"]},{"name":"onSortChange","description":"Callback when sort changes","type":"function","required":true}],"events":[{"name":"onSelectEntity","description":"Fired when an entity is selected","parameters":[{"name":"entityId","description":"ID of the selected entity","type":"string"}]},{"name":"onSortChange","description":"Fired when sort configuration changes","parameters":[{"name":"sortBy","description":"Field to sort by","type":"string"},{"name":"sortDirection","description":"Sort direction","type":"string"}]}],"exampleUsage":"<EntityList\n  entities={entities}\n  viewMode={viewMode}\n  selectedEntityId={selectedEntityId}\n  onSelectEntity={handleSelectEntity}\n  sortBy={sortBy}\n  sortDirection={sortDirection}\n  onSortChange={handleSortChange}\n  utilities={utilities}\n  styles={styles}\n  components={components}\n  callbacks={callbacks}\n/>","code":"function EntityList({ \n  entities, \n  viewMode, \n  selectedEntityId, \n  onSelectEntity, \n  sortBy, \n  sortDirection, \n  onSortChange,\n  utilities, \n  styles, \n  components, \n  callbacks, \n  savedUserSettings, \n  onSaveUserSettings \n}) {\n  // Helper function to get border radius value\n  const getBorderRadius = (size) => {\n    return typeof styles.borders.radius === ''object'' ? styles.borders.radius[size] : styles.borders.radius;\n  };\n  \n  // Handle sort column click\n  const handleSortClick = useCallback((field) => {\n    if (sortBy === field) {\n      // Toggle direction if same field\n      const newDirection = sortDirection === ''asc'' ? ''desc'' : ''asc'';\n      onSortChange?.(field, newDirection);\n    } else {\n      // New field, default to ascending\n      onSortChange?.(field, ''asc'');\n    }\n  }, [sortBy, sortDirection, onSortChange]);\n  \n  // Handle entity selection\n  const handleEntityClick = useCallback((entityId) => {\n    onSelectEntity?.(entityId);\n  }, [onSelectEntity]);\n  \n  // Render sort indicator\n  const renderSortIndicator = (field) => {\n    if (sortBy !== field) return null;\n    \n    return (\n      <span style={{ marginLeft: styles.spacing.xs }}>\n        {sortDirection === ''asc'' ? ''↑'' : ''↓''}\n      </span>\n    );\n  };\n  \n  // Grid View\n  if (viewMode === ''grid'') {\n    return (\n      <div style={{\n        width: ''100%'',\n        overflowX: ''auto''\n      }}>\n        <table style={{\n          width: ''100%'',\n          borderCollapse: ''collapse'',\n          backgroundColor: styles.colors.surface\n        }}>\n          <thead>\n            <tr style={{\n              borderBottom: `2px solid $' + '{styles.colors.border}`\n            }}>\n              <th\n                onClick={() => handleSortClick(''Name'')}\n                style={{\n                  padding: styles.spacing.md,\n                  textAlign: ''left'',\n                  fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.text,\n                  cursor: ''pointer'',\n                  userSelect: ''none'',\n                  whiteSpace: ''nowrap''\n                }}\n              >\n                Name {renderSortIndicator(''Name'')}\n              </th>\n              <th\n                onClick={() => handleSortClick(''DisplayName'')}\n                style={{\n                  padding: styles.spacing.md,\n                  textAlign: ''left'',\n                  fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.text,\n                  cursor: ''pointer'',\n                  userSelect: ''none'',\n                  whiteSpace: ''nowrap''\n                }}\n              >\n                Display Name {renderSortIndicator(''DisplayName'')}\n              </th>\n              <th style={{\n                padding: styles.spacing.md,\n                textAlign: ''left'',\n                fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                fontSize: styles.typography.fontSize.md,\n                color: styles.colors.text\n              }}>\n                Description\n              </th>\n              <th style={{\n                padding: styles.spacing.md,\n                textAlign: ''left'',\n                fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                fontSize: styles.typography.fontSize.md,\n                color: styles.colors.text\n              }}>\n                Schema\n              </th>\n              <th style={{\n                padding: styles.spacing.md,\n                textAlign: ''left'',\n                fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                fontSize: styles.typography.fontSize.md,\n                color: styles.colors.text\n              }}>\n                Table\n              </th>\n              <th style={{\n                padding: styles.spacing.md,\n                textAlign: ''left'',\n                fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                fontSize: styles.typography.fontSize.md,\n                color: styles.colors.text\n              }}>\n                Base View\n              </th>\n            </tr>\n          </thead>\n          <tbody>\n            {entities.map((entity) => (\n              <tr\n                key={entity.ID}\n                onClick={() => handleEntityClick(entity.ID)}\n                style={{\n                  backgroundColor: selectedEntityId === entity.ID \n                    ? styles.colors.primary + ''20'' \n                    : ''transparent'',\n                  borderBottom: `1px solid $' + '{styles.colors.borderLight || styles.colors.border}`,\n                  cursor: ''pointer'',\n                  transition: ''background-color 0.2s''\n                }}\n                onMouseEnter={(e) => {\n                  if (selectedEntityId !== entity.ID) {\n                    e.currentTarget.style.backgroundColor = styles.colors.surfaceHover || styles.colors.surface;\n                  }\n                }}\n                onMouseLeave={(e) => {\n                  if (selectedEntityId !== entity.ID) {\n                    e.currentTarget.style.backgroundColor = ''transparent'';\n                  }\n                }}\n              >\n                <td style={{\n                  padding: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.text,\n                  fontWeight: selectedEntityId === entity.ID \n                    ? (styles.typography.fontWeight?.semibold || ''600'')\n                    : (styles.typography.fontWeight?.regular || ''400'')\n                }}>\n                  {entity.Name}\n                </td>\n                <td style={{\n                  padding: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.text\n                }}>\n                  {entity.DisplayName || entity.Name}\n                </td>\n                <td style={{\n                  padding: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.sm,\n                  color: styles.colors.textSecondary,\n                  maxWidth: ''300px'',\n                  overflow: ''hidden'',\n                  textOverflow: ''ellipsis'',\n                  whiteSpace: ''nowrap''\n                }}>\n                  {entity.Description || ''-''}\n                </td>\n                <td style={{\n                  padding: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.textSecondary\n                }}>\n                  {entity.SchemaName || ''-''}\n                </td>\n                <td style={{\n                  padding: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.textSecondary\n                }}>\n                  {entity.BaseTable || ''-''}\n                </td>\n                <td style={{\n                  padding: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.md,\n                  color: styles.colors.textSecondary\n                }}>\n                  {entity.BaseView || ''-''}\n                </td>\n              </tr>\n            ))}\n          </tbody>\n        </table>\n      </div>\n    );\n  }\n  \n  // Card View\n  return (\n    <div style={{\n      display: ''grid'',\n      gridTemplateColumns: ''repeat(auto-fill, minmax(300px, 1fr))'',\n      gap: styles.spacing.lg\n    }}>\n      {entities.map((entity) => (\n        <div\n          key={entity.ID}\n          onClick={() => handleEntityClick(entity.ID)}\n          style={{\n            padding: styles.spacing.lg,\n            backgroundColor: selectedEntityId === entity.ID \n              ? styles.colors.primary + ''20''\n              : styles.colors.surface,\n            border: selectedEntityId === entity.ID\n              ? `2px solid $' + '{styles.colors.primary}`\n              : `1px solid $' + '{styles.colors.border}`,\n            borderRadius: getBorderRadius(''md''),\n            cursor: ''pointer'',\n            transition: ''all 0.2s'',\n            position: ''relative''\n          }}\n          onMouseEnter={(e) => {\n            if (selectedEntityId !== entity.ID) {\n              e.currentTarget.style.transform = ''translateY(-2px)'';\n              e.currentTarget.style.boxShadow = `0 4px 12px $' + '{styles.colors.shadow || ''rgba(0, 0, 0, 0.1)''}`;\n            }\n          }}\n          onMouseLeave={(e) => {\n            if (selectedEntityId !== entity.ID) {\n              e.currentTarget.style.transform = ''translateY(0)'';\n              e.currentTarget.style.boxShadow = ''none'';\n            }\n          }}\n        >\n          {/* Card Header */}\n          <div style={{\n            marginBottom: styles.spacing.md,\n            paddingBottom: styles.spacing.md,\n            borderBottom: `1px solid $' + '{styles.colors.borderLight || styles.colors.border}`\n          }}>\n            <h3 style={{\n              margin: 0,\n              fontSize: styles.typography.fontSize.lg,\n              fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n              color: styles.colors.text,\n              marginBottom: styles.spacing.xs\n            }}>\n              {entity.DisplayName || entity.Name}\n            </h3>\n            {entity.DisplayName && entity.DisplayName !== entity.Name && (\n              <div style={{\n                fontSize: styles.typography.fontSize.sm,\n                color: styles.colors.textSecondary\n              }}>\n                {entity.Name}\n              </div>\n            )}\n          </div>\n          \n          {/* Card Body */}\n          {entity.Description && (\n            <p style={{\n              margin: 0,\n              marginBottom: styles.spacing.md,\n              fontSize: styles.typography.fontSize.md,\n              color: styles.colors.textSecondary,\n              lineHeight: 1.5,\n              display: ''-webkit-box'',\n              WebkitLineClamp: 2,\n              WebkitBoxOrient: ''vertical'',\n              overflow: ''hidden''\n            }}>\n              {entity.Description}\n            </p>\n          )}\n          \n          {/* Card Footer */}\n          <div style={{\n            display: ''flex'',\n            justifyContent: ''space-between'',\n            alignItems: ''center'',\n            fontSize: styles.typography.fontSize.sm,\n            color: styles.colors.textSecondary\n          }}>\n            <div>\n              {entity.SchemaName && (\n                <span style={{ marginRight: styles.spacing.md }}>\n                  Schema: <strong>{entity.SchemaName}</strong>\n                </span>\n              )}\n              {entity.BaseTable && (\n                <span>\n                  Table: <strong>{entity.BaseTable}</strong>\n                </span>\n              )}\n            </div>\n            {entity.BaseView && (\n              <div style={{\n                fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                color: styles.colors.textSecondary\n              }}>\n                View: {entity.BaseView}\n              </div>\n            )}\n          </div>\n          \n          {/* Selection Indicator */}\n          {selectedEntityId === entity.ID && (\n            <div style={{\n              position: ''absolute'',\n              top: styles.spacing.sm,\n              right: styles.spacing.sm,\n              width: ''8px'',\n              height: ''8px'',\n              backgroundColor: styles.colors.primary,\n              borderRadius: ''50%''\n            }} />\n          )}\n        </div>\n      ))}\n    </div>\n  );\n}","dependencies":[],"libraries":[]},{"name":"EntityDetails","title":"Entity Details Panel","description":"Sliding panel that displays detailed information about a selected entity including fields and relationships","type":"form","functionalRequirements":"## Entity Details Requirements\n\n- Slide in from the right when an entity is selected\n- Display entity metadata at the top\n- Show fields in a formatted table\n- Display relationships with icons\n- Include ''Open Record'' button\n- Support closing via X button or Escape key\n- Smooth slide animation\n- Scrollable content area","dataRequirements":null,"technicalDesign":"## Technical Design\n\n### Props\n- entity: Selected entity object\n- fields: Array of entity fields\n- relationships: Array of entity relationships\n- isOpen: Whether panel is visible\n- onClose: Callback to close panel\n- onOpenRecord: Callback to open entity record\n\n### Layout\n- Fixed position overlay\n- Slide animation using transform\n- Header with entity name and close button\n- Sections for metadata, fields, relationships\n- Sticky ''Open Record'' button at bottom","properties":[{"name":"entity","description":"The selected entity object","type":"object","required":true},{"name":"fields","description":"Array of fields for the entity","type":"array","required":true,"defaultValue":[]},{"name":"relationships","description":"Array of relationships for the entity","type":"array","required":true,"defaultValue":[]},{"name":"isOpen","description":"Whether the panel is open","type":"boolean","required":true},{"name":"onClose","description":"Callback to close the panel","type":"function","required":true},{"name":"onOpenRecord","description":"Callback to open the entity record","type":"function","required":true}],"events":[{"name":"onClose","description":"Fired when the panel should close","parameters":[]},{"name":"onOpenRecord","description":"Fired when the open record button is clicked","parameters":[{"name":"entityName","description":"Name of the entity to open","type":"string"}]}],"exampleUsage":"<EntityDetails\n  entity={selectedEntity}\n  fields={entityFields}\n  relationships={entityRelationships}\n  isOpen={detailsPanelOpen}\n  onClose={handleCloseDetails}\n  onOpenRecord={handleOpenRecord}\n  utilities={utilities}\n  styles={styles}\n  components={components}\n  callbacks={callbacks}\n/>","code":"function EntityDetails({ \n  entity, \n  fields, \n  relationships, \n  isOpen, \n  onClose, \n  onOpenRecord,\n  utilities, \n  styles, \n  components, \n  callbacks, \n  savedUserSettings, \n  onSaveUserSettings \n}) {\n  // Helper function to get border radius value\n  const getBorderRadius = (size) => {\n    return typeof styles.borders.radius === ''object'' ? styles.borders.radius[size] : styles.borders.radius;\n  };\n  \n  // Handle escape key to close panel\n  useEffect(() => {\n    const handleEscape = (e) => {\n      if (e.key === ''Escape'' && isOpen) {\n        onClose?.();\n      }\n    };\n    \n    document.addEventListener(''keydown'', handleEscape);\n    return () => document.removeEventListener(''keydown'', handleEscape);\n  }, [isOpen, onClose]);\n  \n  // Handle open record button\n  const handleOpenRecord = useCallback(() => {\n    console.log(''EntityDetails handleOpenRecord called'', { entity, onOpenRecord });\n    if (entity?.Name) {\n      console.log(''Calling onOpenRecord with entity name:'', entity.Name);\n      if (onOpenRecord) {\n        onOpenRecord(entity.Name);\n      } else {\n        console.error(''onOpenRecord callback is not defined'');\n      }\n    } else {\n      console.error(''No entity or entity name available'');\n    }\n  }, [entity, onOpenRecord]);\n  \n  // Render field type badge\n  const renderFieldType = (type) => {\n    const typeColors = {\n      ''nvarchar'': styles.colors.info || styles.colors.primary,\n      ''varchar'': styles.colors.info || styles.colors.primary,\n      ''int'': styles.colors.success || styles.colors.primary,\n      ''bigint'': styles.colors.success || styles.colors.primary,\n      ''decimal'': styles.colors.success || styles.colors.primary,\n      ''float'': styles.colors.success || styles.colors.primary,\n      ''bit'': 