SET
  @Specification_c8de6f0d = N'{"name":"ProductRevenueMatrix","namespace":"CRM/Analytics","type":"Matrix","location":"embedded","code":"function ProductRevenueMatrix({ utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  const [products, setProducts] = useState([]);\n  const [lineItems, setLineItems] = useState([]);\n  const [invoices, setInvoices] = useState([]);\n  const [loading, setLoading] = useState(true);\n  const [error, setError] = useState(null);\n  const [selectedProduct, setSelectedProduct] = useState(null);\n  const [isPanelOpen, setIsPanelOpen] = useState(false);\n  const [viewMode, setViewMode] = useState(savedUserSettings?.viewMode || ''treemap'');\n  const [timePeriod, setTimePeriod] = useState(savedUserSettings?.timePeriod || ''month'');\n  const [sortBy, setSortBy] = useState(''revenue'');\n  const [startDate, setStartDate] = useState(savedUserSettings?.startDate || null);\n  const [endDate, setEndDate] = useState(savedUserSettings?.endDate || null);\n  const [aiInsights, setAiInsights] = useState(null);\n  const [loadingInsights, setLoadingInsights] = useState(false);\n  const [insightsError, setInsightsError] = useState(null);\n  const [insightsCollapsed, setInsightsCollapsed] = useState(false);\n  \n  // Load sub-components from registry\n  const Treemap = components[''ProductRevenueTreemap''];\n  const MatrixTable = components[''ProductRevenueMatrixTable''];\n  const DetailPanel = components[''ProductRevenueDetailPanel''];\n  const AIInsightsPanel = components[''AIInsightsPanel''];\n\n  useEffect(() => {\n    loadData();\n  }, [timePeriod, startDate, endDate]);\n\n  const loadData = async () => {\n    setLoading(true);\n    setError(null);\n    \n    try {\n      const dateFilter = getDateFilter();\n      \n      // Use RunViews for batch loading - single server call\n      const batchResults = await utilities.rv.RunViews([\n        {\n          EntityName: ''Products'',\n          OrderBy: ''ProductName ASC''\n        },\n        {\n          EntityName: ''Invoice Line Items'',\n          OrderBy: ''TotalPrice DESC'',\n          MaxRows: 1000\n        },\n        {\n          EntityName: ''Invoices'',\n          ExtraFilter: dateFilter,\n          OrderBy: ''InvoiceDate DESC'',\n          MaxRows: 500\n        }\n      ]);\n\n      // Process results - RunViews returns an array directly\n      if (batchResults && Array.isArray(batchResults)) {\n        const [productsResult, lineItemsResult, invoicesResult] = batchResults;\n        \n        let hasError = false;\n        \n        if (productsResult.Success) {\n          setProducts(productsResult.Results || []);\n        } else {\n          console.error(''Failed to load products:'', productsResult.ErrorMessage);\n          hasError = true;\n        }\n        \n        if (lineItemsResult.Success) {\n          setLineItems(lineItemsResult.Results || []);\n        } else {\n          console.error(''Failed to load line items:'', lineItemsResult.ErrorMessage);\n          hasError = true;\n        }\n        \n        if (invoicesResult.Success) {\n          setInvoices(invoicesResult.Results || []);\n        } else {\n          console.error(''Failed to load invoices:'', invoicesResult.ErrorMessage);\n          hasError = true;\n        }\n        \n        // Only set error if something critical failed and we have no data to work with\n        if (hasError && products.length === 0 && lineItems.length === 0 && invoices.length === 0) {\n          setError(''Failed to load data. Please try again.'');\n        } else {\n          // Clear any previous errors if we have some data\n          setError(null);\n        }\n      } else {\n        setError(''Failed to load data'');\n      }\n    } catch (err) {\n      setError(err.message);\n      console.error(''Error loading data:'', err);\n    } finally {\n      setLoading(false);\n    }\n  };\n\n  const getDateFilter = () => {\n    // If custom dates are set, use them\n    if (timePeriod === ''custom'' && startDate && endDate) {\n      return `InvoiceDate >= ''$' + '{startDate}'' AND InvoiceDate <= ''$' + '{endDate}''`;\n    }\n    \n    const now = new Date();\n    let filterStart;\n    \n    switch (timePeriod) {\n      case ''month'':\n        filterStart = new Date(now.getFullYear(), now.getMonth(), 1);\n        break;\n      case ''quarter'':\n        filterStart = new Date(now.getFullYear(), Math.floor(now.getMonth() / 3) * 3, 1);\n        break;\n      case ''year'':\n        filterStart = new Date(now.getFullYear(), 0, 1);\n        break;\n      case ''all'':\n        return '''';\n      default:\n        return '''';\n    }\n    \n    return filterStart ? `InvoiceDate >= ''$' + '{filterStart.toISOString().split(''T'')[0]}''` : '''';\n  };\n\n  const calculateProductMetrics = () => {\n    const productMetrics = {};\n    \n    // Initialize metrics for each product\n    products.forEach(product => {\n      productMetrics[product.ID] = {\n        id: product.ID,\n        name: product.ProductName,\n        category: product.Category || ''Uncategorized'',\n        price: product.UnitPrice || 0,\n        cost: product.Cost || 0,\n        revenue: 0,\n        quantity: 0,\n        margin: 0,\n        customers: new Set(),\n        transactions: 0,\n        trend: []\n      };\n    });\n    \n    // Calculate revenue from line items\n    const invoiceIds = new Set(invoices.map(inv => inv.ID));\n    \n    lineItems.forEach(item => {\n      if (invoiceIds.has(item.InvoiceID) && productMetrics[item.ProductID]) {\n        const metrics = productMetrics[item.ProductID];\n        // Use TotalPrice if available, otherwise calculate\n        const lineTotal = item.TotalPrice || (item.Quantity * item.UnitPrice * (1 - (item.Discount || 0) / 100));\n        metrics.revenue += lineTotal;\n        metrics.quantity += item.Quantity || 0;\n        metrics.transactions++;\n        \n        // Find invoice to get customer\n        const invoice = invoices.find(inv => inv.ID === item.InvoiceID);\n        if (invoice && invoice.AccountID) {\n          metrics.customers.add(invoice.AccountID);\n        }\n      }\n    });\n    \n    // Calculate margins and generate trend data\n    Object.values(productMetrics).forEach(metrics => {\n      if (metrics.price > 0 && metrics.cost > 0) {\n        metrics.margin = ((metrics.price - metrics.cost) / metrics.price * 100);\n      }\n      metrics.customerCount = metrics.customers.size;\n      \n      // Generate trend data (simulated for demo - in real app, calculate from historical data)\n      for (let i = 0; i < 12; i++) {\n        metrics.trend.push(Math.random() * metrics.revenue * 0.3 + metrics.revenue * 0.7);\n      }\n    });\n    \n    return productMetrics;\n  };\n\n\n  const generateAIInsights = async () => {\n    if (!products || products.length === 0 || !lineItems || lineItems.length === 0) {\n      setInsightsError(''No data available for analysis'');\n      return;\n    }\n\n    setLoadingInsights(true);\n    setInsightsError(null);\n    \n    try {\n      const metrics = calculateProductMetrics();\n      const topProductsList = Object.values(metrics)\n        .filter(p => p.revenue > 0)\n        .sort((a, b) => b.revenue - a.revenue);\n      \n      // Calculate key insights data\n      const totalRevenue = topProductsList.reduce((sum, p) => sum + p.revenue, 0);\n      const avgMargin = topProductsList.length > 0 \n        ? topProductsList.reduce((sum, p) => sum + p.margin, 0) / topProductsList.length \n        : 0;\n      \n      // Find underperformers\n      const underperformers = topProductsList.filter(p => p.margin < 20 && p.revenue > 0);\n      \n      // Calculate category performance\n      const categoryPerformance = {};\n      topProductsList.forEach(p => {\n        if (!categoryPerformance[p.category]) {\n          categoryPerformance[p.category] = { revenue: 0, products: 0, avgMargin: 0 };\n        }\n        categoryPerformance[p.category].revenue += p.revenue;\n        categoryPerformance[p.category].products++;\n        categoryPerformance[p.category].avgMargin += p.margin;\n      });\n      \n      Object.values(categoryPerformance).forEach(cat => {\n        cat.avgMargin = cat.products > 0 ? cat.avgMargin / cat.products : 0;\n      });\n      \n      // Customer concentration\n      const uniqueCustomers = new Set();\n      lineItems.forEach(item => {\n        const invoice = invoices.find(inv => inv.ID === item.InvoiceID);\n        if (invoice?.AccountID) uniqueCustomers.add(invoice.AccountID);\n      });\n\n      const prompt = `Analyze this product revenue data and provide strategic insights:\n\n## Product Performance Summary\n- **Total Products:** $' + '{products.length}\n- **Active Products (with revenue):** $' + '{topProductsList.length}\n- **Total Revenue:** $$' + '{totalRevenue.toLocaleString()}\n- **Average Margin:** $' + '{avgMargin.toFixed(1)}%\n- **Unique Customers:** $' + '{uniqueCustomers.size}\n- **Time Period:** $' + '{timePeriod === ''custom'' ? `$' + '{startDate} to $' + '{endDate}` : `Last $' + '{timePeriod}`}\n\n## Top 5 Products by Revenue\n$' + '{topProductsList.slice(0, 5).map((p, i) => \n  `$' + '{i + 1}. **$' + '{p.name}**\n   - Revenue: $$' + '{p.revenue.toLocaleString()}\n   - Margin: $' + '{p.margin.toFixed(1)}%\n   - Customers: $' + '{p.customers.size}\n   - Units Sold: $' + '{p.quantity}`\n).join(''\\n'')}\n\n## Category Performance\n$' + '{Object.entries(categoryPerformance)\n  .sort((a, b) => b[1].revenue - a[1].revenue)\n  .slice(0, 5)\n  .map(([cat, data]) => \n    `- **$' + '{cat}:** $$' + '{data.revenue.toLocaleString()} revenue, $' + '{data.products} products, $' + '{data.avgMargin.toFixed(1)}% avg margin`\n  ).join(''\\n'')}\n\n## Underperforming Products (margin < 20%)\n$' + '{underperformers.length > 0 \n  ? underperformers.slice(0, 5).map(p => \n      `- **$' + '{p.name}:** $' + '{p.margin.toFixed(1)}% margin, $$' + '{p.revenue.toLocaleString()} revenue`\n    ).join(''\\n'')\n  : ''No underperforming products identified''}\n\n## Revenue Distribution\n- **Top 20% of products generate:** $' + '{((topProductsList.slice(0, Math.ceil(topProductsList.length * 0.2))\n    .reduce((sum, p) => sum + p.revenue, 0) / totalRevenue) * 100).toFixed(1)}% of revenue\n- **Products with zero revenue:** $' + '{products.length - topProductsList.length}\n\nBased on this specific data, please provide:\n1. **Portfolio Health Assessment** - Is this product mix balanced and healthy?\n2. **Margin Optimization Opportunities** - Which specific products need pricing/cost review?\n3. **Growth Opportunities** - What expansion potential exists based on the data?\n4. **Risk Factors** - What concentration risks or declining products should we monitor?\n5. **Strategic Recommendations** - 3-4 specific actionable next steps\n\nUse markdown formatting with headers (##), bullet points, and **bold** text. Reference the actual numbers in your analysis.`;\n\n      const result = await utilities.ai.ExecutePrompt({\n        systemPrompt: ''You are a product portfolio analyst specializing in revenue optimization and product strategy. Analyze the specific product data provided and give actionable insights. Always reference the actual numbers and percentages from the data. Format your response in clear markdown.'',\n        messages: prompt,\n        preferredModels: [''GPT-OSS-120B'', ''Qwen3 32B''],\n        modelPower: ''high'',\n        temperature: 0.7,\n        maxTokens: 1500\n      });\n\n      if (result?.success && result?.result) {\n        setAiInsights(result.result);\n      } else {\n        setInsightsError(''Failed to generate insights. Please try again.'');\n      }\n    } catch (error) {\n      console.error(''Error generating AI insights:'', error);\n      setInsightsError(''An error occurred while generating insights'');\n    } finally {\n      setLoadingInsights(false);\n    }\n  };\n\n  const handleProductClick = (product) => {\n    setSelectedProduct(product);\n    setIsPanelOpen(true);\n  };\n\n  if (loading) {\n    return (\n      <div style={{ padding: ''40px'', textAlign: ''center'' }}>\n        <div style={{ fontSize: ''18px'', color: ''#6B7280'' }}>Loading product revenue data...</div>\n      </div>\n    );\n  }\n\n  if (error) {\n    return (\n      <div style={{ padding: ''40px'', textAlign: ''center'' }}>\n        <div style={{ fontSize: ''18px'', color: ''#EF4444'' }}>Error: {error}</div>\n        <button \n          onClick={loadData}\n          style={{\n            marginTop: ''16px'',\n            padding: ''8px 16px'',\n            backgroundColor: ''#3B82F6'',\n            color: ''white'',\n            border: ''none'',\n            borderRadius: ''6px'',\n            cursor: ''pointer''\n          }}\n        >\n          Retry\n        </button>\n      </div>\n    );\n  }\n\n  const productMetrics = calculateProductMetrics();\n  const topProducts = Object.values(productMetrics)\n    .filter(p => p.revenue > 0)\n    .sort((a, b) => b.revenue - a.revenue)\n    .slice(0, 5);\n\n  return (\n    <div style={{ padding: ''20px'', backgroundColor: ''#F3F4F6'', minHeight: ''100%'' }}>\n      <style>{`\n        @media (max-width: 1024px) {\n          .product-revenue-grid {\n            grid-template-columns: 1fr !important;\n          }\n        }\n        \n        /* Markdown content styling */\n        .markdown-insights h1 { font-size: 20px; font-weight: 600; color: #111827; margin: 16px 0 12px 0; }\n        .markdown-insights h2 { font-size: 18px; font-weight: 600; color: #1F2937; margin: 14px 0 10px 0; }\n        .markdown-insights h3 { font-size: 16px; font-weight: 600; color: #374151; margin: 12px 0 8px 0; }\n        .markdown-insights h4 { font-size: 14px; font-weight: 600; color: #4B5563; margin: 10px 0 6px 0; }\n        .markdown-insights p { margin: 8px 0; color: #374151; line-height: 1.6; }\n        .markdown-insights ul, .markdown-insights ol { margin: 8px 0; padding-left: 24px; color: #374151; }\n        .markdown-insights li { margin: 4px 0; line-height: 1.5; }\n        .markdown-insights strong { font-weight: 600; color: #1F2937; }\n        .markdown-insights em { font-style: italic; }\n        .markdown-insights code { background: #F3F4F6; padding: 2px 4px; border-radius: 3px; font-family: monospace; font-size: 0.9em; }\n        .markdown-insights blockquote { border-left: 3px solid #10B981; padding-left: 12px; margin: 12px 0; color: #4B5563; }\n        .markdown-insights hr { border: none; border-top: 1px solid #E5E7EB; margin: 16px 0; }\n        .markdown-insights a { color: #10B981; text-decoration: none; }\n        .markdown-insights a:hover { text-decoration: underline; }\n      `}</style>\n      <div style={{ marginBottom: ''20px'' }}>\n        <div style={{ display: ''flex'', justifyContent: ''space-between'', alignItems: ''center'', marginBottom: ''20px'', flexWrap: ''wrap'', gap: ''12px'' }}>\n          <h2 style={{ margin: 0, fontSize: ''24px'', fontWeight: ''bold'' }}>Product Revenue Matrix</h2>\n          <div style={{ display: ''flex'', gap: ''8px'' }}>\n            <select\n              value={timePeriod}\n              onChange={(e) => {\n                const newPeriod = e.target.value;\n                setTimePeriod(newPeriod);\n                if (newPeriod !== ''custom'') {\n                  setStartDate(null);\n                  setEndDate(null);\n                  onSaveUserSettings({ \n                    ...savedUserSettings, \n                    timePeriod: newPeriod,\n                    startDate: null,\n                    endDate: null\n                  });\n                } else {\n                  onSaveUserSettings({ ...savedUserSettings, timePeriod: newPeriod });\n                }\n              }}\n              style={{\n                padding: ''6px 12px'',\n                border: ''1px solid #D1D5DB'',\n                borderRadius: ''6px''\n              }}\n            >\n              <option value=\"month\">This Month</option>\n              <option value=\"quarter\">This Quarter</option>\n              <option value=\"year\">This Year</option>\n              <option value=\"all\">All Time</option>\n              <option value=\"custom\">Custom Range</option>\n            </select>\n            \n            {timePeriod === ''custom'' && (\n              <>\n                <input\n                  type=\"date\"\n                  value={startDate || ''''}\n                  onChange={(e) => {\n                    setStartDate(e.target.value);\n                    if (e.target.value && endDate) {\n                      onSaveUserSettings({ \n                        ...savedUserSettings, \n                        startDate: e.target.value,\n                        endDate,\n                        timePeriod: ''custom''\n                      });\n                    }\n                  }}\n                  style={{\n                    padding: ''6px 12px'',\n                    border: ''1px solid #D1D5DB'',\n                    borderRadius: ''6px''\n                  }}\n                />\n                <span style={{ color: ''#6B7280'', fontSize: ''14px'' }}>to</span>\n                <input\n                  type=\"date\"\n                  value={endDate || ''''}\n                  onChange={(e) => {\n                    setEndDate(e.target.value);\n                    if (startDate && e.target.value) {\n                      onSaveUserSettings({ \n                        ...savedUserSettings, \n                        startDate,\n                        endDate: e.target.value,\n                        timePeriod: ''custom''\n                      });\n                    }\n                  }}\n                  style={{\n                    padding: ''6px 12px'',\n                    border: ''1px solid #D1D5DB'',\n                    borderRadius: ''6px''\n                  }}\n                />\n              </>\n            )}\n            <button\n              onClick={() => {\n                const newMode = viewMode === ''treemap'' ? ''matrix'' : ''treemap'';\n                setViewMode(newMode);\n                onSaveUserSettings({ ...savedUserSettings, viewMode: newMode });\n              }}\n              style={{\n                padding: ''6px 12px'',\n                backgroundColor: ''#8B5CF6'',\n                color: ''white'',\n                border: ''none'',\n                borderRadius: ''6px'',\n                cursor: ''pointer''\n              }}\n            >\n              <i className={`fa-solid fa-$' + '{viewMode === ''treemap'' ? ''table'' : ''chart-treemap''}`}></i>\n              {viewMode === ''treemap'' ? '' Matrix View'' : '' Treemap View''}\n            </button>\n            <button\n              onClick={generateAIInsights}\n              disabled={loadingInsights}\n              style={{\n                padding: ''6px 12px'',\n                backgroundColor: loadingInsights ? ''#9CA3AF'' : ''#10B981'',\n                color: ''white'',\n                border: ''none'',\n                borderRadius: ''6px'',\n                cursor: loadingInsights ? ''not-allowed'' : ''pointer'',\n                display: ''flex'',\n                alignItems: ''center'',\n                gap: ''6px''\n              }}\n            >\n              <i className={`fa-solid fa-$' + '{loadingInsights ? ''spinner fa-spin'' : ''wand-magic-sparkles''}`}></i>\n              {loadingInsights ? ''Analyzing...'' : ''Get AI Insights''}\n            </button>\n          </div>\n        </div>\n      </div>\n      \n      {/* AI Insights Panel from Registry */}\n      {AIInsightsPanel && (aiInsights || insightsError) && (\n        <AIInsightsPanel\n          utilities={utilities}\n          styles={styles}\n          components={components}\n          callbacks={callbacks}\n          savedUserSettings={savedUserSettings}\n          onSaveUserSettings={onSaveUserSettings}\n          insights={aiInsights}\n          loading={loadingInsights}\n          error={insightsError}\n          onRefresh={generateAIInsights}\n          onClose={() => {\n            setAiInsights(null);\n            setInsightsError(null);\n            setInsightsCollapsed(false);\n          }}\n          title=\"AI-Powered Product Portfolio Insights\"\n          titleIcon=\"fa-wand-magic-sparkles\"\n          titleIconColor=\"#10B981\"\n          isCollapsed={insightsCollapsed}\n          onToggleCollapse={() => setInsightsCollapsed(!insightsCollapsed)}\n          exportFilename={`product-revenue-insights-$' + '{new Date().toISOString().split(''T'')[0]}.md`}\n        />\n      )}\n      \n      <div style={{ marginBottom: ''20px'' }}>\n        {/* Top Products Summary */}\n        <div style={{ display: ''grid'', gridTemplateColumns: ''repeat(auto-fit, minmax(200px, 1fr))'', gap: ''12px'', marginBottom: ''20px'' }}>\n          {topProducts.map((product, index) => (\n            <div\n              key={product.id}\n              style={{\n                padding: ''12px'',\n                backgroundColor: ''white'',\n                borderRadius: ''8px'',\n                border: ''1px solid #E5E7EB'',\n                cursor: ''pointer'',\n                transition: ''all 0.2s''\n              }}\n              onClick={() => handleProductClick(product)}\n              onMouseEnter={(e) => {\n                e.currentTarget.style.transform = ''translateY(-2px)'';\n                e.currentTarget.style.boxShadow = ''0 4px 6px rgba(0,0,0,0.1)'';\n              }}\n              onMouseLeave={(e) => {\n                e.currentTarget.style.transform = ''translateY(0)'';\n                e.currentTarget.style.boxShadow = ''none'';\n              }}\n            >\n              <div style={{ display: ''flex'', justifyContent: ''space-between'', alignItems: ''start'' }}>\n                <div>\n                  <div style={{ fontSize: ''14px'', fontWeight: ''bold'', marginBottom: ''4px'' }}>\n                    #{index + 1} {product.name}\n                  </div>\n                  <div style={{ fontSize: ''12px'', color: ''#6B7280'' }}>\n                    {product.category}\n                  </div>\n                </div>\n                <div style={{ textAlign: ''right'' }}>\n                  <div style={{ fontSize: ''16px'', fontWeight: ''bold'', color: ''#8B5CF6'' }}>\n                    $' + '{(product.revenue / 1000).toFixed(1)}K\n                  </div>\n                  <div style={{ fontSize: ''11px'', color: product.margin > 30 ? ''#10B981'' : ''#F59E0B'' }}>\n                    {product.margin.toFixed(0)}% margin\n                  </div>\n                </div>\n              </div>\n            </div>\n          ))}\n        </div>\n      </div>\n      \n      {/* Main Content Grid - responsive layout */}\n      <div \n        className=\"product-revenue-grid\"\n        style={{ \n          display: ''grid'', \n          gridTemplateColumns: isPanelOpen ? ''1fr 400px'' : ''1fr'',\n          gap: ''20px'',\n          transition: ''grid-template-columns 0.3s ease''\n        }}\n      >\n        {/* Main Visualization Area */}\n        <div style={{ backgroundColor: ''white'', borderRadius: ''8px'', padding: ''20px'', minWidth: 0 }}>\n          {viewMode === ''treemap'' && Treemap && (\n            <Treemap \n              products={products}\n              lineItems={lineItems}\n              invoices={invoices}\n              onProductClick={handleProductClick}\n            />\n          )}\n          \n          {viewMode === ''matrix'' && MatrixTable && (\n            <MatrixTable\n              products={products}\n              lineItems={lineItems}\n              invoices={invoices}\n              sortBy={sortBy}\n              onSortChange={setSortBy}\n              onProductClick={handleProductClick}\n            />\n          )}\n        </div>\n        \n        {/* Product Detail Panel - inline responsive */}\n        {isPanelOpen && DetailPanel && (\n          <div style={{ minWidth: 0 }}>\n            <DetailPanel\n              product={selectedProduct}\n              isOpen={isPanelOpen}\n              onClose={() => {\n                setIsPanelOpen(false);\n                setSelectedProduct(null);\n              }}\n              callbacks={callbacks}\n            />\n          </div>\n        )}\n      </div>\n    </div>\n  );\n}","functionalRequirements":"## Product Revenue Matrix\n\n### Purpose\nVisualize product performance across customers and time periods in an interactive matrix format.\n\n### Core Features\n- **Product/Customer Matrix**: Cross-reference sales data\n- **Time Period Analysis**: Monthly, quarterly, yearly views\n- **Growth Indicators**: Show trends with sparklines\n- **Profitability Metrics**: Margin analysis per product\n- **Interactive Treemap**: Using Highcharts visualization\n- **Product Categories**: Group products hierarchically\n- **Slide-out Details**: Product performance deep-dive\n- **Filtering & Sorting**: Multiple sort options\n\n### Visualizations\n- Treemap for product hierarchy\n- Matrix grid with color coding\n- Sparklines for trends\n- Bubble chart for volume/value analysis","dataRequirements":{"mode":"views","entities":[{"name":"Products","displayFields":["ProductName","Category","Price","Cost","SKU"],"filterFields":["Category"],"sortFields":["ProductName","Price"]},{"name":"Invoice Line Items","displayFields":["InvoiceID","ProductID","Quantity","TotalPrice","Discount"],"filterFields":["ProductID","InvoiceID"],"sortFields":["TotalPrice"]},{"name":"Invoices","displayFields":["InvoiceNumber","AccountID","InvoiceDate","TotalAmount"],"filterFields":["InvoiceDate","AccountID"],"sortFields":["InvoiceDate"]}]},"technicalDesign":"## Implementation\n\n### Components\n- Main: ProductRevenueMatrix\n- Sub-components:\n  - RevenueTreemap: Hierarchical product visualization\n  - ProductMatrix: Grid view of products\n  - TrendSparkline: Mini trend charts\n  - ProductDetails: Detailed analysis panel\n\n### Data Processing\n- Aggregate sales by product and period\n- Calculate margins and profitability\n- Generate trend data\n- Build hierarchical structures","properties":[],"events":[{"name":"OpenEntityRecord","description":"Navigate to product details"}],"dependencies":[{"name":"ProductRevenueTreemap","namespace":"Sales/Analytics","type":"Chart","location":"embedded","code":"function ProductRevenueTreemap({ products, lineItems, invoices, onProductClick }) {\n  const treemapRef = useRef(null);\n  \n  useEffect(() => {\n    if (products.length > 0) {\n      renderTreemap();\n    }\n    \n    return () => {\n      // Clean up tooltip on unmount\n      d3.select(''.product-revenue-tooltip'').remove();\n    };\n  }, [products, lineItems, invoices]);\n  \n  const calculateProductMetrics = () => {\n    const productMetrics = {};\n    \n    // Initialize metrics for each product\n    products.forEach(product => {\n      productMetrics[product.ID] = {\n        id: product.ID,\n        name: product.ProductName,\n        category: product.Category || ''Uncategorized'',\n        price: product.UnitPrice || 0,\n        cost: product.Cost || 0,\n        revenue: 0,\n        quantity: 0,\n        margin: 0,\n        customers: new Set(),\n        transactions: 0\n      };\n    });\n    \n    // Calculate revenue from line items\n    const invoiceIds = new Set(invoices.map(inv => inv.ID));\n    \n    lineItems.forEach(item => {\n      if (invoiceIds.has(item.InvoiceID) && productMetrics[item.ProductID]) {\n        const metrics = productMetrics[item.ProductID];\n        const lineTotal = item.TotalPrice || (item.Quantity * item.UnitPrice * (1 - (item.Discount || 0) / 100));\n        metrics.revenue += lineTotal;\n        metrics.quantity += item.Quantity || 0;\n        metrics.transactions++;\n        \n        // Find invoice to get customer\n        const invoice = invoices.find(inv => inv.ID === item.InvoiceID);\n        if (invoice && invoice.AccountID) {\n          metrics.customers.add(invoice.AccountID);\n        }\n      }\n    });\n    \n    // Calculate margins and generate trend data\n    Object.values(productMetrics).forEach(metrics => {\n      if (metrics.price > 0 && metrics.cost > 0) {\n        metrics.margin = ((metrics.price - metrics.cost) / metrics.price * 100);\n      }\n      metrics.customerCount = metrics.customers.size;\n      \n      // Generate trend data (simulated for demo - in real app, calculate from historical data)\n      metrics.trend = [];\n      for (let i = 0; i < 12; i++) {\n        metrics.trend.push(Math.random() * metrics.revenue * 0.3 + metrics.revenue * 0.7);\n      }\n    });\n    \n    return productMetrics;\n  };\n  \n  const renderTreemap = () => {\n    if (!treemapRef.current) return;\n    \n    const productMetrics = calculateProductMetrics();\n    \n    // Group products by category\n    const categories = {};\n    Object.values(productMetrics).forEach(product => {\n      if (!categories[product.category]) {\n        categories[product.category] = {\n          name: product.category,\n          children: []\n        };\n      }\n      \n      if (product.revenue > 0) {\n        categories[product.category].children.push({\n          name: product.name,\n          value: product.revenue,\n          margin: product.margin,\n          product: product\n        });\n      }\n    });\n    \n    const categoriesWithData = Object.values(categories).filter(cat => cat.children.length > 0);\n    \n    // Handle empty state\n    if (categoriesWithData.length === 0) {\n      d3.select(treemapRef.current).selectAll(''*'').remove();\n      d3.select(treemapRef.current)\n        .append(''div'')\n        .style(''padding'', ''40px'')\n        .style(''text-align'', ''center'')\n        .style(''color'', ''#6B7280'')\n        .text(''No revenue data available for the selected period'');\n      return;\n    }\n    \n    const treemapData = {\n      name: ''Products'',\n      children: categoriesWithData\n    };\n    \n    // Clear previous visualization\n    d3.select(treemapRef.current).selectAll(''*'').remove();\n    \n    // Set dimensions\n    const width = treemapRef.current.clientWidth || 800;\n    const height = 500;\n    \n    // Create varied pastel colors for better accessibility\n    // Using a categorical color scheme with good contrast\n    const pastelColors = [\n      ''#FFB3BA'', // Light pink\n      ''#FFDFBA'', // Light peach\n      ''#FFFFBA'', // Light yellow\n      ''#BAFFC9'', // Light mint green\n      ''#BAE1FF'', // Light sky blue\n      ''#E0BBE4'', // Light lavender\n      ''#C7CEEA'', // Light periwinkle\n      ''#FFDAC1'', // Light apricot\n      ''#B5EAD7'', // Light seafoam\n      ''#FF9AA2'', // Light coral\n      ''#C1E1C1'', // Light sage\n      ''#BEDADC'', // Light teal\n      ''#C4A1C2'', // Light mauve\n      ''#FCBAD3'', // Light rose\n      ''#FFFFD8''  // Light cream\n    ];\n    \n    // Create color scale that maps products to colors\n    // Use modulo to cycle through colors if more products than colors\n    const getProductColor = (index, margin) => {\n      // Use margin to slightly adjust the brightness\n      // Higher margin = slightly more saturated color\n      const baseColor = pastelColors[index % pastelColors.length];\n      \n      // If margin is very high (>40%), make it slightly more vibrant\n      if (margin > 40) {\n        return d3.color(baseColor).darker(0.2).toString();\n      } else if (margin < 10) {\n        // If margin is low, make it slightly lighter\n        return d3.color(baseColor).brighter(0.2).toString();\n      }\n      return baseColor;\n    };\n    \n    // Create treemap layout\n    const treemap = d3.treemap()\n      .size([width, height])\n      .padding(2);\n    \n    // Create hierarchy and compute layout\n    const root = d3.hierarchy(treemapData)\n      .sum(d => d.value)\n      .sort((a, b) => b.value - a.value);\n    \n    treemap(root);\n    \n    // Create SVG\n    const svg = d3.select(treemapRef.current)\n      .append(''svg'')\n      .attr(''width'', width)\n      .attr(''height'', height);\n    \n    // Create tooltip\n    const tooltip = d3.select(''body'').append(''div'')\n      .attr(''class'', ''product-revenue-tooltip'')\n      .style(''position'', ''absolute'')\n      .style(''visibility'', ''hidden'')\n      .style(''background'', ''rgba(0, 0, 0, 0.8)'')\n      .style(''color'', ''white'')\n      .style(''padding'', ''8px'')\n      .style(''border-radius'', ''4px'')\n      .style(''font-size'', ''12px'')\n      .style(''pointer-events'', ''none'')\n      .style(''z-index'', ''9999'');\n    \n    // Create groups for categories\n    const categories_g = svg.selectAll(''.category'')\n      .data(root.children || [])\n      .enter().append(''g'')\n      .attr(''class'', ''category'')\n      .attr(''transform'', d => `translate($' + '{d.x0},$' + '{d.y0})`);\n    \n    // Add category rectangles\n    categories_g.append(''rect'')\n      .attr(''width'', d => d.x1 - d.x0)\n      .attr(''height'', d => d.y1 - d.y0)\n      .style(''fill'', ''none'')\n      .style(''stroke'', ''#fff'')\n      .style(''stroke-width'', 2);\n    \n    // Add category labels\n    categories_g.append(''text'')\n      .attr(''x'', 4)\n      .attr(''y'', 16)\n      .style(''font-weight'', ''bold'')\n      .style(''font-size'', ''14px'')\n      .style(''fill'', ''#374151'')\n      .text(d => d.data.name);\n    \n    // Create product rectangles\n    const leaves = root.leaves() || [];\n    \n    // Sort leaves by value to ensure consistent color assignment\n    leaves.sort((a, b) => b.value - a.value);\n    \n    const products_g = svg.selectAll(''.product'')\n      .data(leaves)\n      .enter().append(''g'')\n      .attr(''class'', ''product'')\n      .attr(''transform'', d => `translate($' + '{d.x0},$' + '{d.y0})`);\n    \n    products_g.append(''rect'')\n      .attr(''width'', d => d.x1 - d.x0)\n      .attr(''height'', d => d.y1 - d.y0)\n      .style(''fill'', (d, i) => getProductColor(i, d.data.margin))\n      .style(''stroke'', ''#fff'')\n      .style(''stroke-width'', 2)\n      .style(''cursor'', ''pointer'')\n      .style(''opacity'', 0.9)\n      .on(''mouseover'', function(_, d) {\n        // Add hover effect\n        d3.select(this)\n          .style(''opacity'', 1)\n          .style(''stroke-width'', 3)\n          .style(''filter'', ''brightness(1.1)'');\n        \n        tooltip.style(''visibility'', ''visible'')\n          .html(`\n            <strong>$' + '{d.data.name}</strong><br/>\n            Revenue: $$' + '{(d.data.product.revenue / 1000).toFixed(1)}K<br/>\n            Quantity: $' + '{d.data.product.quantity}<br/>\n            Margin: $' + '{d.data.margin.toFixed(1)}%<br/>\n            Customers: $' + '{d.data.product.customerCount}\n          `);\n      })\n      .on(''mousemove'', function(event) {\n        tooltip.style(''top'', (event.pageY - 10) + ''px'')\n          .style(''left'', (event.pageX + 10) + ''px'');\n      })\n      .on(''mouseout'', function() {\n        // Remove hover effect\n        d3.select(this)\n          .style(''opacity'', 0.9)\n          .style(''stroke-width'', 2)\n          .style(''filter'', ''none'');\n        \n        tooltip.style(''visibility'', ''hidden'');\n      })\n      .on(''click'', function(_, d) {\n        if (onProductClick) {\n          onProductClick(d.data.product);\n        }\n      });\n    \n    // Add product labels\n    products_g.append(''text'')\n      .attr(''x'', 4)\n      .attr(''y'', 20)\n      .style(''font-size'', ''11px'')\n      .style(''fill'', ''#2D3748'')\n      .style(''font-weight'', ''500'')\n      .each(function(d) {\n        const text = d3.select(this);\n        const width = d.x1 - d.x0;\n        const name = d.data.name;\n        \n        if (width > 60) {\n          text.text(name.length > 15 ? name.substring(0, 15) + ''...'' : name);\n        }\n      });\n    \n    // Add revenue labels for larger boxes\n    products_g.append(''text'')\n      .attr(''x'', 4)\n      .attr(''y'', 36)\n      .style(''font-size'', ''10px'')\n      .style(''font-weight'', ''bold'')\n      .style(''fill'', ''#2D3748'')\n      .each(function(d) {\n        const width = d.x1 - d.x0;\n        const height = d.y1 - d.y0;\n        \n        if (width > 80 && height > 50) {\n          d3.select(this).text(`$$' + '{(d.data.product.revenue / 1000).toFixed(0)}K`);\n        }\n      });\n  };\n  \n  return (\n    <div ref={treemapRef} style={{ width: ''100%'', height: ''500px'' }} />\n  );\n}","functionalRequirements":"## Product Revenue Treemap Sub-Component\n\n### Purpose\nVisualize product revenue distribution using an interactive treemap chart.\n\n### Core Features\n- **Hierarchical View**: Products grouped by category\n- **Size by Revenue**: Box size represents revenue amount\n- **Color by Margin**: Color intensity shows profit margin\n- **Interactive Tooltips**: Hover for detailed metrics\n- **Click to Drill-down**: Select products for details","dataRequirements":{"mode":"props","props":["products","lineItems","invoices","onProductClick"]},"technicalDesign":"## Implementation\n\n### D3.js Treemap\n- Dynamic treemap layout with category grouping\n- Color scale based on margin percentages\n- Responsive sizing and text truncation\n- Tooltip on hover with key metrics","properties":[{"name":"products","type":"array","description":"Array of product entities","required":true},{"name":"lineItems","type":"array","description":"Array of invoice line item entities","required":true},{"name":"invoices","type":"array","description":"Array of invoice entities","required":true},{"name":"onProductClick","type":"function","description":"Callback when product is clicked","required":false}],"events":[],"dependencies":[],"libraries":[{"name":"d3","version":"^7.8.5","globalVariable":"d3"}]},{"name":"ProductRevenueMatrixTable","namespace":"Sales/Analytics","type":"Table","location":"embedded","code":"function ProductRevenueMatrixTable({ products, lineItems, invoices, sortBy, onSortChange, onProductClick }) {\n  const matrixRef = useRef(null);\n  const sparklineCharts = useRef({});\n  \n  useEffect(() => {\n    if (products.length > 0) {\n      renderMatrix();\n    }\n    \n    return () => {\n      // Clean up charts\n      Object.values(sparklineCharts.current).forEach(chart => {\n        if (chart) chart.destroy();\n      });\n    };\n  }, [products, lineItems, invoices, sortBy]);\n  \n  const calculateProductMetrics = () => {\n    const productMetrics = {};\n    \n    // Initialize metrics for each product\n    products.forEach(product => {\n      productMetrics[product.ID] = {\n        id: product.ID,\n        name: product.ProductName,\n        category: product.Category || ''Uncategorized'',\n        price: product.UnitPrice || 0,\n        cost: product.Cost || 0,\n        revenue: 0,\n        quantity: 0,\n        margin: 0,\n        customers: new Set(),\n        transactions: 0,\n        trend: []\n      };\n    });\n    \n    // Calculate revenue from line items\n    const invoiceIds = new Set(invoices.map(inv => inv.ID));\n    \n    lineItems.forEach(item => {\n      if (invoiceIds.has(item.InvoiceID) && productMetrics[item.ProductID]) {\n        const metrics = productMetrics[item.ProductID];\n        const lineTotal = item.TotalPrice || (item.Quantity * item.UnitPrice * (1 - (item.Discount || 0) / 100));\n        metrics.revenue += lineTotal;\n        metrics.quantity += item.Quantity || 0;\n        metrics.transactions++;\n        \n        // Find invoice to get customer\n        const invoice = invoices.find(inv => inv.ID === item.InvoiceID);\n        if (invoice && invoice.AccountID) {\n          metrics.customers.add(invoice.AccountID);\n        }\n      }\n    });\n    \n    // Calculate margins and generate trend data\n    Object.values(productMetrics).forEach(metrics => {\n      if (metrics.price > 0 && metrics.cost > 0) {\n        metrics.margin = ((metrics.price - metrics.cost) / metrics.price * 100);\n      }\n      metrics.customerCount = metrics.customers.size;\n      \n      // Generate trend data (simulated for demo)\n      for (let i = 0; i < 12; i++) {\n        metrics.trend.push(Math.random() * metrics.revenue * 0.3 + metrics.revenue * 0.7);\n      }\n    });\n    \n    return productMetrics;\n  };\n  \n  const renderMatrix = () => {\n    if (!matrixRef.current) return;\n    \n    const productMetrics = calculateProductMetrics();\n    const sortedProducts = Object.values(productMetrics)\n      .filter(p => p.revenue > 0)\n      .sort((a, b) => {\n        switch (sortBy) {\n          case ''revenue'':\n            return b.revenue - a.revenue;\n          case ''quantity'':\n            return b.quantity - a.quantity;\n          case ''margin'':\n            return b.margin - a.margin;\n          case ''customers'':\n            return b.customerCount - a.customerCount;\n          default:\n            return 0;\n        }\n      });\n    \n    // Clear previous content and charts\n    matrixRef.current.innerHTML = '''';\n    Object.values(sparklineCharts.current).forEach(chart => {\n      if (chart) chart.destroy();\n    });\n    sparklineCharts.current = {};\n    \n    // Handle empty state\n    if (sortedProducts.length === 0) {\n      const emptyDiv = document.createElement(''div'');\n      emptyDiv.style.padding = ''40px'';\n      emptyDiv.style.textAlign = ''center'';\n      emptyDiv.style.color = ''#6B7280'';\n      emptyDiv.textContent = ''No product revenue data available for the selected period'';\n      matrixRef.current.appendChild(emptyDiv);\n      return;\n    }\n    \n    // Create matrix grid\n    const table = document.createElement(''table'');\n    table.style.width = ''100%'';\n    table.style.borderCollapse = ''collapse'';\n    \n    // Header row with sort indicators\n    const headerRow = document.createElement(''tr'');\n    [''Product'', ''Category'', ''Revenue'', ''Quantity'', ''Margin'', ''Customers'', ''Trend''].forEach(header => {\n      const th = document.createElement(''th'');\n      th.style.padding = ''12px'';\n      th.style.borderBottom = ''2px solid #E5E7EB'';\n      th.style.textAlign = header === ''Product'' ? ''left'' : ''center'';\n      th.style.fontSize = ''14px'';\n      th.style.fontWeight = ''bold'';\n      th.style.position = ''relative'';\n      \n      // Create header content container\n      const headerContent = document.createElement(''div'');\n      headerContent.style.display = ''flex'';\n      headerContent.style.alignItems = ''center'';\n      headerContent.style.justifyContent = header === ''Product'' ? ''flex-start'' : ''center'';\n      headerContent.style.gap = ''4px'';\n      \n      // Add header text\n      const headerText = document.createElement(''span'');\n      headerText.textContent = header;\n      headerContent.appendChild(headerText);\n      \n      // Add sort indicator for sortable columns\n      const sortMap = {\n        ''Revenue'': ''revenue'',\n        ''Quantity'': ''quantity'',\n        ''Margin'': ''margin'',\n        ''Customers'': ''customers''\n      };\n      \n      if (sortMap[header]) {\n        th.style.cursor = ''pointer'';\n        \n        // Add sort indicator\n        const sortIndicator = document.createElement(''span'');\n        sortIndicator.style.fontSize = ''10px'';\n        sortIndicator.style.color = sortBy === sortMap[header] ? ''#8B5CF6'' : ''#D1D5DB'';\n        sortIndicator.style.transition = ''color 0.2s'';\n        \n        // Show appropriate arrow based on current sort\n        if (sortBy === sortMap[header]) {\n          sortIndicator.innerHTML = ''▼'';\n          sortIndicator.style.fontWeight = ''bold'';\n        } else {\n          sortIndicator.innerHTML = ''▼'';\n          sortIndicator.style.opacity = ''0.4'';\n        }\n        \n        headerContent.appendChild(sortIndicator);\n        \n   