toLocaleDateString()}`]);\n    csvData.push([]);\n    csvData.push([''Metric'', ''Value'']);\n    csvData.push([''Win Rate'', `$' + '{metrics.winRate.toFixed(1)}%`]);\n    csvData.push([''Won Deals'', metrics.wonCount]);\n    csvData.push([''Lost Deals'', metrics.lostCount]);\n    csvData.push([''Won Revenue'', `$$' + '{metrics.wonValue.toFixed(0)}`]);\n    csvData.push([''Lost Revenue'', `$$' + '{metrics.lostValue.toFixed(0)}`]);\n    csvData.push([]);\n    \n    \n    // Convert to CSV string\n    const csv = csvData.map(row => row.join('','')).join(''\\n'');\n    \n    // Download\n    const blob = new Blob([csv], { type: ''text/csv'' });\n    const url = URL.createObjectURL(blob);\n    const a = document.createElement(''a'');\n    a.href = url;\n    a.download = `win-loss-analysis-$' + '{new Date().toISOString().split(''T'')[0]}.csv`;\n    a.click();\n  };\n\n  // Enhanced Sub-component: Overview Cards with drill-down\n  const OverviewCards = () => {\n    const metrics = calculateWinLossMetrics();\n    \n    return (\n      <div style={{ display: ''grid'', gridTemplateColumns: ''repeat(auto-fit, minmax(200px, 1fr))'', gap: ''16px'', marginBottom: ''20px'' }}>\n        <div \n          style={{ \n            padding: ''16px'', \n            backgroundColor: ''white'', \n            borderRadius: ''8px'', \n            border: ''1px solid #E5E7EB'',\n            cursor: ''pointer'',\n            transition: ''all 0.2s'',\n            position: ''relative'',\n            overflow: ''hidden''\n          }}\n          onClick={() => openDrillDown(''winrate'', metrics)}\n          onMouseEnter={(e) => {\n            e.currentTarget.style.transform = ''translateY(-2px)'';\n            e.currentTarget.style.boxShadow = ''0 4px 12px rgba(0,0,0,0.1)'';\n          }}\n          onMouseLeave={(e) => {\n            e.currentTarget.style.transform = ''translateY(0)'';\n            e.currentTarget.style.boxShadow = ''none'';\n          }}\n        >\n          <div style={{\n            position: ''absolute'',\n            top: 0,\n            left: 0,\n            width: `$' + '{metrics.winRate}%`,\n            height: ''3px'',\n            backgroundColor: metrics.winRate > 50 ? ''#10B981'' : ''#EF4444'',\n            transition: ''width 0.5s''\n          }} />\n          <div style={{ fontSize: ''12px'', color: ''#6B7280'', marginBottom: ''4px'' }}>Win Rate</div>\n          <div style={{ fontSize: ''32px'', fontWeight: ''bold'', color: metrics.winRate > 50 ? ''#10B981'' : ''#EF4444'' }}>\n            {metrics.winRate.toFixed(1)}%\n          </div>\n          <div style={{ fontSize: ''11px'', color: ''#6B7280'' }}>\n            {metrics.wonCount} won / {metrics.totalDeals} total\n          </div>\n          <div style={{ fontSize: ''10px'', color: ''#9CA3AF'', marginTop: ''4px'' }}>\n            Click to view details →\n          </div>\n        </div>\n        \n        <div \n          style={{ \n            padding: ''16px'', \n            backgroundColor: ''white'', \n            borderRadius: ''8px'', \n            border: ''1px solid #E5E7EB'',\n            cursor: ''pointer'',\n            transition: ''all 0.2s''\n          }}\n          onClick={() => openDrillDown(''revenue'', { type: ''won'', deals: deals.filter(d => d.Stage === ''Closed Won'') })}\n          onMouseEnter={(e) => {\n            e.currentTarget.style.transform = ''translateY(-2px)'';\n            e.currentTarget.style.boxShadow = ''0 4px 12px rgba(0,0,0,0.1)'';\n          }}\n          onMouseLeave={(e) => {\n            e.currentTarget.style.transform = ''translateY(0)'';\n            e.currentTarget.style.boxShadow = ''none'';\n          }}\n        >\n          <div style={{ fontSize: ''12px'', color: ''#6B7280'', marginBottom: ''4px'' }}>Won Revenue</div>\n          <div style={{ fontSize: ''32px'', fontWeight: ''bold'', color: ''#10B981'' }}>\n            $' + '{(metrics.wonValue / 1000).toFixed(0)}K\n          </div>\n          <div style={{ fontSize: '