  @Specification_ea7a10a6 = N'{"name":"PipelineKanban","namespace":"CRM/Dashboards","type":"Chart","location":"embedded","code":"// PipelineKanban Sub-component\nconst PipelineKanban = ({ stages, deals, onDealClick, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) => {\n  const getStageColor = (stageName) => {\n    const colors = {\n      ''Prospecting'': ''#6f42c1'',\n      ''Qualification'': ''#007bff'',\n      ''Proposal'': ''#17a2b8'',\n      ''Negotiation'': ''#ffc107'',\n      ''Closed Won'': ''#28a745'',\n      ''Closed Lost'': ''#dc3545''\n    };\n    return colors[stageName] || ''#6c757d'';\n  };\n\n  const handleDealClick = (deal) => {\n    if (onDealClick) {\n      onDealClick(deal);\n    } else if (callbacks?.OpenEntityRecord) {\n      callbacks.OpenEntityRecord(''Deals'', deal.id);\n    }\n  };\n\n  return (\n    <div style={{\n      display: ''flex'',\n      gap: ''15px'',\n      overflowX: ''auto'',\n      padding: ''10px'',\n      minHeight: ''400px''\n    }}>\n      {stages.map((stage, stageIndex) => (\n        <div \n          key={stage.name || stageIndex}\n          style={{\n            minWidth: ''280px'',\n            backgroundColor: ''#f8f9fa'',\n            borderRadius: ''8px'',\n            padding: ''10px'',\n            display: ''flex'',\n            flexDirection: ''column''\n          }}\n        >\n          <div style={{\n            padding: ''10px'',\n            marginBottom: ''10px'',\n            borderBottom: `3px solid $' + '{getStageColor(stage.name)}`,\n            backgroundColor: ''#fff'',\n            borderRadius: ''4px''\n          }}>\n            <h4 style={{ margin: ''0 0 5px 0'', fontSize: ''16px'' }}>{stage.name}</h4>\n            <div style={{ display: ''flex'', justifyContent: ''space-between'', fontSize: ''13px'', color: ''#666'' }}>\n              <span>{stage.count} deals</span>\n              <span>$' + '{(stage.totalValue / 1000000).toFixed(1)}M</span>\n            </div>\n          </div>\n\n          <div style={{\n            flex: 1,\n            overflowY: ''auto'',\n            display: ''flex'',\n            flexDirection: ''column'',\n            gap: ''8px''\n          }}>\n            {deals\n              .filter(deal => deal.stage === stage.name)\n              .map((deal, dealIndex) => (\n                <div\n                  key={deal.id || dealIndex}\n                  onClick={() => handleDealClick(deal)}\n                  style={{\n                    backgroundColor: ''#fff'',\n                    border: ''1px solid #e0e0e0'',\n                    borderRadius: ''6px'',\n                    padding: ''10px'',\n                    cursor: ''pointer'',\n                    transition: ''transform 0.1s, box-shadow 0.1s''\n                  }}\n                  onMouseEnter={(e) => {\n                    e.currentTarget.style.transform = ''translateY(-2px)'';\n                    e.currentTarget.style.boxShadow = ''0 4px 8px rgba(0,0,0,0.1)'';\n                  }}\n                  onMouseLeave={(e) => {\n                    e.currentTarget.style.transform = ''translateY(0)'';\n                    e.currentTarget.style.boxShadow = ''none'';\n                  }}\n                >\n                  <div style={{ fontWeight: ''500'', fontSize: ''14px'', marginBottom: ''4px'' }}>\n                    {deal.name}\n                  </div>\n                  <div style={{ fontSize: ''12px'', color: ''#666'', marginBottom: ''8px'' }}>\n                    {deal.accountName}\n                  </div>\n                  <div style={{ display: ''flex'', justifyContent: ''space-between'', alignItems: ''center'' }}>\n                    <span style={{ fontSize: ''14px'', fontWeight: ''bold'', color: ''#333'' }}>\n                      $' + '{(deal.value / 1000).toFixed(0)}K\n                    </span>\n                    <span style={{\n                      fontSize: ''11px'',\n                      padding: ''2px 6px'',\n                      borderRadius: ''10px'',\n                      backgroundColor: deal.probability >= 70 ? ''#d4edda'' : \n                                       deal.probability >= 40 ? ''#fff3cd'' : ''#f8d7da'',\n                      color: deal.probability >= 70 ? ''#155724'' : \n                             deal.probability >= 40 ? ''#856404'' : ''#721c24''\n                    }}>\n                      {deal.probability}%\n                    </span>\n                  </div>\n                  {deal.daysInStage > 30 && (\n                    <div style={{\n                      marginTop: ''6px'',\n                      fontSize: ''11px'',\n                      color: ''#dc3545'',\n                      fontStyle: ''italic''\n                    }}>\n                      ⚠ {deal.daysInStage} days in stage\n                    </div>\n                  )}\n                </div>\n              ))\n            }\n          </div>\n        </div>\n      ))}\n    </div>\n  );\n}","functionalRequirements":"## Pipeline Kanban Board\n\n### Purpose\nDisplay deals in a Kanban board layout organized by pipeline stages.\n\n### Features\n- Column-based layout for stages\n- Deal cards in each column\n- Stage totals and counts\n- Scrollable columns\n- Color-coded stage headers\n- Deal value formatting","dataRequirements":{"mode":"props","description":"Receives deals grouped by stage from parent"},"technicalDesign":"## Implementation\n\n### Props\n- dealsByStage: Object with stages as keys, deals arrays as values\n- stages: Array of stage definitions\n- onDealClick: Callback for deal selection\n\n### Layout\n- Flexbox horizontal columns\n- Fixed height with scroll\n- Stage headers with colors","properties":[{"name":"dealsByStage","type":"object","required":true,"description":"Deals grouped by stage"},{"name":"stages","type":"array","required":true,"description":"Stage definitions with names and colors"},{"name":"onDealClick","type":"function","required":true,"description":"Callback for deal selection"}],"events":[],"dependencies":[],"libraries":[]}'
SET
  @FunctionalRequirements_ea7a10a6 = N'- Display all pipeline stages in a horizontal grid
- Show deal count and total value for each stage
- Use stage-specific colors for visual distinction
- Make each stage card clickable for drill-down
- Show hover effects on stage cards'
SET
  @TechnicalDesign_ea7a10a6 = N'Renders a responsive grid of stage cards. Filters deals by stage and calculates metrics. Uses color-coded borders and hover animations. Formats currency values using helper function.'
