  @Specification_96f31e2d = N'{"name":"DealCards","location":"embedded","code":"function DealCards({ deals, stageColors = {}, onDealClick, onOpenClick }) {\n  return (\n    <div style={{ display: ''grid'', gap: ''12px'' }}>\n      {deals.map((deal, index) => (\n        <div\n          key={deal.ID || index}\n          style={{\n            padding: ''16px'',\n            backgroundColor: ''#F9FAFB'',\n            borderRadius: ''8px'',\n            border: ''1px solid #E5E7EB'',\n            cursor: ''pointer'',\n            transition: ''all 0.2s''\n          }}\n          onClick={() => onDealClick && onDealClick(deal)}\n          onMouseEnter={(e) => {\n            e.currentTarget.style.backgroundColor = ''#F3F4F6'';\n            e.currentTarget.style.transform = ''translateY(-2px)'';\n          }}\n          onMouseLeave={(e) => {\n            e.currentTarget.style.backgroundColor = ''#F9FAFB'';\n            e.currentTarget.style.transform = ''translateY(0)'';\n          }}\n        >\n          <div style={{ display: ''flex'', justifyContent: ''space-between'', marginBottom: ''8px'' }}>\n            <div style={{ fontWeight: ''bold'', flex: 1 }}>{deal.DealName}</div>\n            <div style={{ display: ''flex'', alignItems: ''center'', gap: ''8px'' }}>\n              <div style={{ fontWeight: ''bold'', color: ''#059669'' }}>\n                $' + '{((deal.Amount || 0) / 1000).toFixed(0)}K\n              </div>\n              {onOpenClick && (\n                <button\n                  onClick={(e) => {\n                    e.stopPropagation();\n                    onOpenClick(''Deals'', [{ FieldName: ''ID'', Value: deal.ID }]);\n                  }}\n                  style={{\n                    background: ''none'',\n                    border: ''none'',\n                    color: ''#6B7280'',\n                    cursor: ''pointer'',\n                    padding: ''2px'',\n                    fontSize: ''14px''\n                  }}\n                  title=\"Open in Explorer\"\n                >\n                  <i className=\"fa-solid fa-up-right-from-square\"></i>\n                </button>\n              )}\n            </div>\n          </div>\n          <div style={{ fontSize: ''14px'', color: ''#6B7280'', marginBottom: ''4px'' }}>\n            {deal.AccountName || ''No Account''}\n          </div>\n          <div style={{ display: ''flex'', justifyContent: ''space-between'', fontSize: ''12px'', color: ''#9CA3AF'' }}>\n            <span>Close: {deal.CloseDate ? new Date(deal.CloseDate).toLocaleDateString() : ''TBD''}</span>\n            <span>{deal.Probability || 0}% probability</span>\n          </div>\n          {deal.Stage && stageColors[deal.Stage] && (\n            <div style={{ \n              marginTop: ''8px'',\n              padding: ''4px 8px'',\n              backgroundColor: stageColors[deal.Stage] || ''#6B7280'',\n              color: ''white'',\n              borderRadius: ''4px'',\n              fontSize: ''11px'',\n              display: ''inline-block''\n            }}>\n              {deal.Stage}\n            </div>\n          )}\n        </div>\n      ))}\n    </div>\n  );\n}","description":"Displays deals in an interactive card layout with open action","category":"Deals","type":"cards","properties":[{"name":"deals","type":"array","description":"Array of deal objects to display","required":true},{"name":"stageColors","type":"object","description":"Optional object mapping stage names to colors","required":true},{"name":"onDealClick","type":"function","description":"Callback when a deal card is clicked","required":true},{"name":"onOpenClick","type":"function","description":"Callback when the open icon is clicked, receives entityName and primaryKey array","required":true}],"events":[{"name":"onDealSelect","description":"Fired when a deal card is clicked","parameters":[{"name":"deal","type":"object","description":"The deal object that was clicked"}]},{"name":"onDealOpen","description":"Fired when the open icon is clicked","parameters":[{"name":"entityName","type":"string","description":"Name of the entity (Deals)"},{"name":"primaryKey","type":"array","description":"Array of key-value pairs for composite primary key"}]}],"usageExample":"```javascript\n<DealCards\n  deals={filteredDeals}\n  stageColors={stageColors}\n  onDealClick={(deal) => console.log(''Deal clicked:'', deal)}\n  onOpenClick={(entityName, primaryKey) => callbacks.OpenEntityRecord(entityName, primaryKey)}\n/>\n```","functionalRequirements":"- Display deal cards in a responsive grid\n- Show deal name, amount, account, close date, probability, and stage\n- Format currency values appropriately\n- Include open icon button for external navigation\n- Show hover effects on cards\n- Color-code stage badges","technicalDesign":"Renders deal information in card format with hover states. Uses formatCurrency for amounts. Includes Font Awesome icon for opening deals externally. Stage badges use dynamic colors from stageColors prop."}'
