  @Specification_a8ab068e = N'{"name":"SalesFunnelVisualization","namespace":"CRM/Analytics","type":"Chart","location":"embedded","code":"function SalesFunnelVisualization({ utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  // Extract AIInsightsPanel from components\n  const AIInsightsPanel = components?.AIInsightsPanel;\n  \n  if (!AIInsightsPanel) {\n    console.warn(''AIInsightsPanel component not available'');\n  }\n\n  const [deals, setDeals] = useState([]);\n  const [loading, setLoading] = useState(true);\n  const [error, setError] = useState(null);\n  const [selectedStage, setSelectedStage] = useState(null);\n  const [viewMode, setViewMode] = useState(savedUserSettings?.viewMode || ''count'');\n  const [timeFilter, setTimeFilter] = useState(savedUserSettings?.timeFilter || ''quarter'');\n  const [startDate, setStartDate] = useState(savedUserSettings?.startDate || null);\n  const [endDate, setEndDate] = useState(savedUserSettings?.endDate || null);\n  const [isPanelOpen, setIsPanelOpen] = useState(false);\n  \n  // AI Insights state\n  const [aiInsights, setAiInsights] = useState(null);\n  const [loadingInsights, setLoadingInsights] = useState(false);\n  const [insightsError, setInsightsError] = useState(null);\n\n  // Load sub-components from registry\n  const FunnelChart = components[''SalesFunnelChart''];\n  const StagePanel = components[''SalesFunnelStagePanel''];\n\n  // Define pipeline stages in order\n  const pipelineStages = [\n    { name: ''Lead'', color: ''#3B82F6'' },\n    { name: ''Qualified'', color: ''#6366F1'' },\n    { name: ''Proposal'', color: ''#8B5CF6'' },\n    { name: ''Negotiation'', color: ''#A855F7'' },\n    { name: ''Closed Won'', color: ''#10B981'' },\n    { name: ''Closed Lost'', color: ''#EF4444'' }\n  ];\n\n  useEffect(() => {\n    loadDeals();\n  }, [timeFilter, startDate, endDate]);\n\n  const loadDeals = async () => {\n    setLoading(true);\n    setError(null);\n    \n    try {\n      const dateFilter = getDateFilter();\n      const result = await utilities.rv.RunView({\n        EntityName: ''Deals'',\n        ExtraFilter: dateFilter,\n        OrderBy: ''CloseDate DESC''\n      });\n\n      if (result.Success) {\n        setDeals(result.Results || []);\n      } else {\n        setError(result.ErrorMessage || ''Failed to load deals'');\n      }\n    } catch (err) {\n      setError(err.message);\n    } finally {\n      setLoading(false);\n    }\n  };\n\n  const getDateFilter = () => {\n    // If custom dates are set, use them\n    if (startDate && endDate) {\n      return `CloseDate >= ''$' + '{startDate}'' AND CloseDate <= ''$' + '{endDate}''`;\n    }\n    \n    // Otherwise use preset filter\n    const now = new Date();\n    let filterStart;\n    \n    switch (timeFilter) {\n      case ''month'':\n        filterStart = new Date(now);\n        filterStart.setMonth(now.getMonth() - 1);\n        break;\n      case ''quarter'':\n        filterStart = new Date(now);\n        filterStart.setMonth(now.getMonth() - 3);\n        break;\n      case ''year'':\n        filterStart = new Date(now);\n        filterStart.setFullYear(now.getFullYear() - 1);\n        break;\n      case ''all'':\n        return '''';\n      default:\n        return '''';\n    }\n    \n    return filterStart ? `CloseDate >= ''$' + '{filterStart.toISOString().split(''T'')[0]}''` : '''';\n  };\n  \n  const handleDateRangeChange = (start, end) => {\n    setStartDate(start);\n    setEndDate(end);\n    setTimeFilter(''custom'');\n    if (onSaveUserSettings) {\n      onSaveUserSettings({ \n        ...savedUserSettings, \n        startDate: start, \n        endDate: end,\n        timeFilter: ''custom''\n      });\n    }\n  };\n  \n  const handlePresetChange = (preset) => {\n    setTimeFilter(preset);\n    setStartDate(null);\n    setEndDate(null);\n    if (onSaveUserSettings) {\n      onSaveUserSettings({ \n        ...savedUserSettings, \n        timeFilter: preset,\n        startDate: null,\n        endDate: null\n      });\n    }\n  };\n\n  const calculateFunnelData = () => {\n    const funnelData = [];\n    const closedLost = { count: 0, value: 0 };\n    \n    // Calculate metrics for each stage\n    pipelineStages.forEach((stage, index) => {\n      let stageDeals;\n      \n      if (stage.name === ''Closed Lost'') {\n        stageDeals = deals.filter(d => d.Stage === ''Closed Lost'');\n        closedLost.count = stageDeals.length;\n        closedLost.value = stageDeals.reduce((sum, d) => sum + (d.Amount || 0), 0);\n      } else if (stage.name === ''Closed Won'') {\n        stageDeals = deals.filter(d => d.Stage === ''Closed Won'');\n      } else {\n        // For other stages, include all deals at or after this stage (except closed lost)\n        stageDeals = deals.filter(d => {\n          if (d.Stage === ''Closed Lost'') return false;\n          const dealStageIndex = pipelineStages.findIndex(s => s.name === d.Stage);\n          return dealStageIndex >= index;\n        });\n      }\n      \n      if (stage.name !== ''Closed Lost'') {\n        const totalValue = stageDeals.reduce((sum, d) => sum + (d.Amount || 0), 0);\n        const prevStageCount = index > 0 ? funnelData[index - 1].count : 0;\n        const conversionRate = prevStageCount > 0 ? (stageDeals.length / prevStageCount) * 100 : 100;\n        \n        funnelData.push({\n          stage: stage.name,\n          count: stageDeals.length,\n          value: totalValue,\n          color: stage.color,\n          width: Math.max(30, 100 - (index * 15)), // Visual width for funnel effect\n          conversionRate,\n          deals: stageDeals\n        });\n      }\n    });\n    \n    return { funnelData, closedLost };\n  };\n\n  const handleStageClick = (stageData) => {\n    setSelectedStage({\n      ...stageData,\n      title: `$' + '{stageData.stage} Stage`\n    });\n    setIsPanelOpen(true);\n  };\n\n  const formatCurrency = (amount) => {\n    if (amount >= 1000000) {\n      return `$$' + '{(amount / 1000000).toFixed(1)}M`;\n    } else if (amount >= 1000) {\n      return `$$' + '{(amount / 1000).toFixed(0)}K`;\n    }\n    return `$$' + '{amount.toFixed(0)}`;\n  };\n\n  const formatNumber = (num) => {\n    if (num >= 1000000) return `$' + '{(num / 1000000).toFixed(1)}M`;\n    if (num >= 1000) return `$' + '{(num / 1000).toFixed(0)}K`;\n    return num.toString();\n  };\n  \n  // Format insights text using marked library for proper markdown rendering\n\n  const generateAIInsights = async () => {\n    setLoadingInsights(true);\n    setInsightsError(null);\n    \n    try {\n      const { funnelData, closedLost } = calculateFunnelData();\n      const totalDeals = deals.length;\n      const totalValue = deals.reduce((sum, d) => sum + (d.Amount || 0), 0);\n      const avgDealSize = totalDeals > 0 ? totalValue / totalDeals : 0;\n      const winRate = totalDeals > 0 ? \n        (deals.filter(d => d.Stage === ''Closed Won'').length / totalDeals * 100) : 0;\n      \n      const prompt = `Analyze this sales funnel data and provide insights:\n\nTime Filter: $' + '{timeFilter}$' + '{startDate && endDate ? ` ($' + '{startDate} to $' + '{endDate})` : ''''}\nTotal Deals: $' + '{totalDeals}\nTotal Value: $' + '{formatCurrency(totalValue)}\nWin Rate: $' + '{winRate.toFixed(1)}%\nAverage Deal Size: $' + '{formatCurrency(avgDealSize)}\n\nFunnel Stage Breakdown:\n$' + '{funnelData.map(stage => `$' + '{stage.stage}: $' + '{stage.count} deals ($' + '{formatCurrency(stage.value)}) - $' + '{stage.conversionRate.toFixed(1)}% conversion`).join(''\\n'')}\n\nClosed Lost: $' + '{closedLost.count} deals ($' + '{formatCurrency(closedLost.value)})\n\nConversion Analysis:\n$' + '{funnelData.map((stage, index) => {\n  if (index === 0) return `$' + '{stage.stage}: Entry point`;\n  const prevStage = funnelData[index - 1];\n  const dropoff = prevStage.count - stage.count;\n  const dropoffRate = prevStage.count > 0 ? (dropoff / prevStage.count * 100) : 0;\n  return `$' + '{prevStage.stage} → $' + '{stage.stage}: $' + '{dropoff} deals lost ($' + '{dropoffRate.toFixed(1)}% drop-off)`;\n}).join(''\\n'')}\n\nProvide:\n1. Sales funnel performance analysis and trends\n2. Conversion rate analysis between stages\n3. Identification of bottlenecks and drop-off points\n4. Win rate and deal velocity insights\n5. Specific recommendations to improve conversion rates\n6. Strategies to reduce drop-offs at critical stages\n7. Deal size and value optimization opportunities\n\nFocus on actionable recommendations to improve sales performance and funnel efficiency.`;\n      \n      const result = await utilities.ai.ExecutePrompt({\n        systemPrompt: ''You are an expert sales analyst with deep knowledge of sales funnels, conversion optimization, and revenue growth strategies. Provide clear, actionable insights with specific recommendations. Format your response in clear markdown.'',\n        messages: prompt,\n        preferredModels: [''GPT-OSS-120B'', ''Qwen3 32B''],\n        modelPower: ''high'',\n        temperature: 0.7,\n        maxTokens: 1500\n      });\n      \n      if (result?.success && result?.result) {\n        setAiInsights(result.result);\n      } else {\n        setInsightsError(''Failed to generate insights. Please try again.'');\n      }\n    } catch (error) {\n      setInsightsError(error.message || ''Failed to generate AI insights'');\n    } finally {\n      setLoadingInsights(false);\n    }\n  };\n\n  if (loading) {\n    return (\n      <div style={{ padding: ''40px'', textAlign: ''center'' }}>\n        <div style={{ fontSize: ''18px'', color: ''#6B7280'' }}>Loading sales funnel data...</div>\n      </div>\n    );\n  }\n\n  if (error) {\n    return (\n      <div style={{ padding: ''40px'', textAlign: ''center'' }}>\n        <div style={{ fontSize: ''18px'', color: ''#EF4444'' }}>Error: {error}</div>\n      </div>\n    );\n  }\n\n  const { funnelData, closedLost } = calculateFunnelData();\n  const totalDeals = deals.length;\n  const totalValue = deals.reduce((sum, d) => sum + (d.Amount || 0), 0);\n  const avgDealSize = totalDeals > 0 ? totalValue / totalDeals : 0;\n  const winRate = totalDeals > 0 ? \n    (deals.filter(d => d.Stage === ''Closed Won'').length / totalDeals * 100) : 0;\n\n  return (\n    <div style={{ display: ''flex'', flexDirection: ''column'', height: ''100%'', backgroundColor: ''#F3F4F6'' }}>\n      <style>{`\n        @keyframes fadeInUp {\n          from { \n            opacity: 0;\n            transform: translateY(20px);\n          }\n          to { \n            opacity: 1;\n            transform: translateY(0);\n          }\n        }\n        @keyframes fadeIn {\n          from { opacity: 0; }\n          to { opacity: 1; }\n        }\n      `}</style>\n      <style>{`\n        .ai-insights-content h1, .ai-insights-content h2, .ai-insights-content h3 {\n          margin-top: 16px;\n          margin-bottom: 8px;\n          color: #111827;\n        }\n        .ai-insights-content h1 { font-size: 1.5em; }\n        .ai-insights-content h2 { font-size: 1.3em; }\n        .ai-insights-content h3 { font-size: 1.1em; }\n        .ai-insights-content p {\n          margin: 8px 0;\n          line-height: 1.6;\n          color: #374151;\n        }\n        .ai-insights-content ul, .ai-insights-content ol {\n          margin: 8px 0;\n          padding-left: 24px;\n        }\n        .ai-insights-content li {\n          margin: 4px 0;\n          color: #374151;\n        }\n        .ai-insights-content strong {\n          color: #111827;\n          font-weight: 600;\n        }\n        .ai-insights-content code {\n          background: #F3F4F6;\n          padding: 2px 6px;\n          border-radius: 3px;\n          font-size: 0.9em;\n        }\n        .ai-insights-content blockquote {\n          border-left: 4px solid #10B981;\n          padding-left: 16px;\n          margin: 12px 0;\n          color: #4B5563;\n        }\n      `}</style>\n      \n      <div style={{ padding: ''20px'', borderBottom: ''1px solid #E5E7EB'', backgroundColor: ''white'' }}>\n        <div style={{ marginBottom: ''16px'' }}>\n          <h2 style={{ margin: 0, fontSize: ''24px'', fontWeight: ''bold'' }}>Sales Funnel</h2>\n        </div>\n        \n        <div style={{ display: ''flex'', gap: ''8px'', alignItems: ''center'', flexWrap: ''wrap'' }}>\n          <select\n            value={timeFilter}\n            onChange={(e) => handlePresetChange(e.target.value)}\n            style={{\n              padding: ''6px 12px'',\n              border: ''1px solid #D1D5DB'',\n              borderRadius: ''6px''\n            }}\n          >\n            <option value=\"month\">Last Month</option>\n            <option value=\"quarter\">Last Quarter</option>\n            <option value=\"year\">Last Year</option>\n            <option value=\"all\">All Time</option>\n            <option value=\"custom\">Custom Range</option>\n          </select>\n          \n          {timeFilter === ''custom'' && (\n            <>\n              <input\n                type=\"date\"\n                value={startDate || ''''}\n                onChange={(e) => handleDateRangeChange(e.target.value, endDate)}\n                style={{\n                  padding: ''6px 12px'',\n                  border: ''1px solid #D1D5DB'',\n                  borderRadius: ''6px''\n                }}\n              />\n              <span>to</span>\n              <input\n                type=\"date\"\n                value={endDate || ''''}\n                onChange={(e) => handleDateRangeChange(startDate, e.target.value)}\n                style={{\n                  padding: ''6px 12px'',\n                  border: ''1px solid #D1D5DB'',\n                  borderRadius: ''6px''\n                }}\n              />\n            </>\n          )}\n          \n          <button\n            onClick={() => {\n              setViewMode(viewMode === ''count'' ? ''value'' : ''count'');\n              onSaveUserSettings({ ...savedUserSettings, viewMode: viewMode === ''count'' ? ''value'' : ''count'' });\n            }}\n            style={{\n              padding: ''6px 12px'',\n              backgroundColor: ''#3B82F6'',\n              color: ''white'',\n              border: ''none'',\n              borderRadius: ''6px'',\n              cursor: ''pointer''\n            }}\n          >\n            Show {viewMode === ''count'' ? ''Values'' : ''Counts''}\n          </button>\n          \n          {/* AI Insights Button */}\n          <button\n            onClick={generateAIInsights}\n            disabled={loadingInsights || loading}\n            style={{\n              padding: ''6px 12px'',\n              backgroundColor: ''#10B981'',\n              color: ''white'',\n              border: ''none'',\n              borderRadius: ''6px'',\n              cursor: loadingInsights || loading ? ''not-allowed'' : ''pointer'',\n              display: ''flex'',\n              alignItems: ''center'',\n              gap: ''8px'',\n              opacity: loadingInsights || loading ? 0.6 : 1\n            }}\n          >\n            <i className={`fa-solid fa-$' + '{loadingInsights ? ''spinner fa-spin'' : ''wand-magic-sparkles''}`}></i>\n            {loadingInsights ? ''Analyzing...'' : ''Get AI Insights''}\n          </button>\n        </div>\n        \n        <div style={{ marginTop: ''16px'', display: ''grid'', gridTemplateColumns: ''repeat(4, 1fr)'', gap: ''12px'' }}>\n          <div style={{ padding: ''12px'', backgroundColor: ''#F9FAFB'', borderRadius: ''8px'' }}>\n            <div style={{ fontSize: ''12px'', color: ''#6B7280'' }}>Total Deals</div>\n            <div style={{ fontSize: ''20px'', fontWeight: ''bold'' }}>\n              {totalDeals}\n            </div>\n          </div>\n          <div style={{ padding: ''12px'', backgroundColor: ''#F9FAFB'', borderRadius: ''8px'' }}>\n            <div style={{ fontSize: ''12px'', color: ''#6B7280'' }}>Total Value</div>\n            <div style={{ fontSize: ''20px'', fontWeight: ''bold'' }}>\n              {formatCurrency(totalValue)}\n            </div>\n          </div>\n          <div style={{ padding: ''12px'', backgroundColor: ''#F9FAFB'', borderRadius: ''8px'' }}>\n            <div style={{ fontSize: ''12px'', color: ''#6B7280'' }}>Win Rate</div>\n            <div style={{ fontSize: ''20px'', fontWeight: ''bold'' }}>\n              {winRate.toFixed(1)}%\n            </div>\n          </div>\n          <div style={{ padding: ''12px'', backgroundColor: ''#F9FAFB'', borderRadius: ''8px'' }}>\n            <div style={{ fontSize: ''12px'', color: ''#6B7280'' }}>Avg Deal Size</div>\n            <div style={{ fontSize: ''20px'', fontWeight: ''bold'' }}>\n              {formatCurrency(avgDealSize)}\n            </div>\n          </div>\n        </div>\n      </div>\n      \n      {/* AI Insights Panel - Outside header to preserve funnel width */}\n      <AIInsightsPanel\n        utilities={utilities}\n        styles={styles}\n        components={components}\n        callbacks={callbacks}\n        savedUserSettings={savedUserSettings?.aiInsights}\n        onSaveUserSettings={(settings) => onSaveUserSettings?.({\n          ...savedUserSettings,\n          aiInsights: settings\n        })}\n        insights={aiInsights}\n        loading={loadingInsights}\n        error={insightsError}\n        onGenerate={generateAIInsights}\n        title=\"Sales Funnel Insights\"\n        icon=\"fa-wand-magic-sparkles\"\n        iconColor={styles?.colors?.primary || ''#8B5CF6''}\n        position=\"top\"\n        onClose={() => {\n          setAiInsights(null);\n          setInsightsError(null);\n        }}\n      />\n      \n      {FunnelChart && (\n        <FunnelChart \n          funnelData={funnelData}\n          viewMode={viewMode}\n          onStageClick={handleStageClick}\n          closedLost={closedLost}\n          formatCurrency={formatCurrency}\n        />\n      )}\n      \n      {StagePanel && (\n        <StagePanel\n          isOpen={isPanelOpen}\n          stageData={selectedStage}\n          onClose={() => {\n            setIsPanelOpen(false);\n            setSelectedStage(null);\n          }}\n          components={components}\n          callbacks={callbacks}\n          formatCurrency={formatCurrency}\n        />\n      )}\n      \n    </div>\n  );\n}","functionalRequirements":"## Sales Funnel Visualization\n\n### Purpose\nVisualize the sales pipeline as an interactive funnel showing conversion rates and deal values at each stage.\n\n### Core Features\n- **Interactive Funnel Chart**: Visual representation of deal flow through stages\n- **Dual View Modes**: \n  - Count view: Number of deals at each stage\n  - Value view: Total deal value at each stage\n- **Conversion Metrics**: Show conversion rates between stages\n- **Time Period Filtering**: Filter by date ranges (month, quarter, year)\n- **Slide-out Details Panel**: Click stage to see deals list\n- **Card/List View Toggle**: For deals display\n- **Sorting & Filtering**: Sort by value, date, probability\n\n### Visual Design\n- Animated funnel segments using Framer Motion\n- Color-coded stages with gradients\n- Percentage labels and value annotations\n- Smooth transitions between views\n- Side panel slides in from right","dataRequirements":{"mode":"views","entities":[{"name":"Deals","displayFields":["DealName","Stage","Amount","CloseDate","Probability","AccountID"],"filterFields":["Stage","CloseDate"],"sortFields":["Amount","CloseDate","Probability"],"fields":[{"name":"ID","type":"string","description":"Unique identifier"},{"name":"DealName","type":"string","description":"Name of the deal"},{"name":"Stage","type":"string","description":"Current stage in pipeline"},{"name":"Amount","type":"number","description":"Deal value"},{"name":"CloseDate","type":"date","description":"Expected close date"},{"name":"Probability","type":"number","description":"Win probability percentage"}]}]},"technicalDesign":"## Implementation Details\n\n### Component Architecture\n- Main component: SalesFunnelVisualization\n- Sub-components:\n  - FunnelChart: Renders the funnel visualization\n  - StageDetailsPanel: Slide-out panel for stage details\n  - DealCard: Card view for individual deals\n  - FilterBar: Time period and view mode controls\n\n### Animation Strategy\n- Framer Motion for smooth transitions\n- Stagger animations for funnel segments\n- Spring physics for panel slide\n- Opacity transitions for view changes\n\n### State Management\n- deals: Array of all deals\n- selectedStage: Currently selected stage\n- viewMode: ''count'' or ''value''\n- displayMode: ''cards'' or ''list''\n- timeFilter: Selected time period\n- sortBy: Current sort field\n- filterBy: Active filters","properties":[],"events":[{"name":"OpenEntityRecord","description":"Open deal record in explorer"}],"dependencies":[{"name":"SalesFunnelChart","namespace":"CRM/Analytics","type":"Chart","location":"embedded","code":"function SalesFunnelChart({ funnelData, viewMode, onStageClick, closedLost, formatCurrency }) {\n  return (\n    <div style={{ \n      flex: 1, \n      display: ''flex'', \n      justifyContent: ''center'',\n      alignItems: ''center'',\n      padding: ''40px 20px'',\n      width: ''100%'',\n      overflow: ''hidden''\n    }}>\n      <div style={{\n        display: ''flex'',\n        flexDirection: ''column'',\n        alignItems: ''center'',\n        width: ''650px'',\n        maxWidth: ''90%''\n      }}>\n      {funnelData.map((stage, index) => (\n        <div\n          key={stage.stage}\n          className=\"funnel-stage\"\n          style={{\n            width: `$' + '{stage.width}%`,\n            marginBottom: ''4px'',\n            position: ''relative'',\n            cursor: ''pointer'',\n            opacity: 0,\n            transform: ''translateY(20px)'',\n            animation: `fadeInUp 0.5s $' + '{index * 0.1}s forwards`,\n            transition: ''transform 0.2s ease''\n          }}\n          onClick={() => onStageClick(stage)}\n          onMouseEnter={(e) => e.currentTarget.style.transform = ''scale(1.02)''}\n          onMouseLeave={(e) => e.currentTarget.style.transform = ''scale(1)''}\n          onMouseDown={(e) => e.currentTarget.style.transform = ''scale(0.98)''}\n          onMouseUp={(e) => e.currentTarget.style.transform = ''scale(1.02)''}\n        >\n          <div\n            style={{\n              background: `linear-gradient(135deg, $' + '{stage.color}dd, $' + '{stage.color}99)`,\n              padding: ''20px'',\n              borderRadius: ''8px'',\n              boxShadow: ''0 2px 8px rgba(0,0,0,0.1)'',\n              display: ''flex'',\n              justifyContent: ''space-between'',\n              alignItems: ''center'',\n              minHeight: ''60px''\n            }}\n          >\n            <div style={{ color: ''white'' }}>\n              <div style={{ fontSize: ''14px'', opacity: 0.9 }}>{stage.stage}</div>\n              <div style={{ fontSize: ''20px'', fontWeight: ''bold'' }}>\n                {viewMode === ''count'' ? stage.count : formatCurrency(stage.value)}\n              </div>\n            </div>\n            {index > 0 && (\n              <div style={{ color: ''white'', textAlign: ''right'' }}>\n                <div style={{ fontSize: ''12px'', opacity: 0.8 }}>Conversion</div>\n                <div style={{ fontSize: ''16px'', fontWeight: ''bold'' }}>\n                  {stage.conversionRate.toFixed(1)}%\n                </div>\n              </div>\n            )}\n          </div>\n        </div>\n      ))}\n      \n      {/* Lost deals indicator */}\n      {closedLost.count > 0 && (\n        <div\n          style={{\n            marginTop: ''20px'',\n            padding: ''12px 20px'',\n            backgroundColor: ''#FEE2E2'',\n            borderRadius: ''8px'',\n            color: ''#991B1B'',\n            opacity: 0,\n            animation: ''fadeIn 0.5s 0.5s forwards''\n          }}\n        >\n          <div style={{ fontSize: ''14px'' }}>Lost