SET
  @Specification_f9f4c1d0 = N'{"name":"AIPromptsClusterControls","title":"AI Prompts Cluster Controls","description":"Control panel for filtering, searching, and adjusting clustering parameters","type":"panel","location":"embedded","code":"function AIPromptsClusterControls({\n  categories,\n  types,\n  currentFilters,\n  clusterCount,\n  similarityThreshold,\n  isProcessing,\n  onSearchChange,\n  onFilterChange,\n  onClusterCountChange,\n  onSimilarityThresholdChange,\n  onRecalculate,\n  onExport,\n  utilities,\n  styles,\n  components,\n  callbacks,\n  savedUserSettings,\n  onSaveUserSettings\n}) {\n  const [localSearch, setLocalSearch] = useState(currentFilters.search || '''');\n  const searchTimeout = useRef(null);\n\n  // Debounce search input\n  const handleSearchChange = (value) => {\n    setLocalSearch(value);\n    \n    if (searchTimeout.current) {\n      clearTimeout(searchTimeout.current);\n    }\n    \n    searchTimeout.current = setTimeout(() => {\n      onSearchChange(value);\n    }, 300);\n  };\n\n  // Modern style helpers\n  const inputStyle = {\n    width: ''100%'',\n    padding: ''10px 12px'',\n    fontSize: ''14px'',\n    border: ''2px solid transparent'',\n    borderRadius: ''10px'',\n    backgroundColor: ''rgba(255, 255, 255, 0.9)'',\n    backdropFilter: ''blur(10px)'',\n    transition: ''all 0.3s ease'',\n    outline: ''none'',\n    color: ''#2d3748''\n  };\n\n  const labelStyle = {\n    display: ''block'',\n    marginBottom: ''6px'',\n    fontSize: ''13px'',\n    fontWeight: ''600'',\n    color: ''#4a5568'',\n    letterSpacing: ''0.025em'',\n    textTransform: ''uppercase''\n  };\n\n  const sectionStyle = {\n    marginBottom: ''20px'',\n    padding: ''16px'',\n    background: ''linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(249,250,251,0.95) 100%)'',\n    borderRadius: ''16px'',\n    boxShadow: ''0 4px 12px rgba(0,0,0,0.05)'',\n    backdropFilter: ''blur(10px)'',\n    border: ''1px solid rgba(255,255,255,0.8)''\n  };\n\n  const buttonStyle = {\n    width: ''100%'',\n    padding: ''12px 16px'',\n    fontSize: ''14px'',\n    fontWeight: ''600'',\n    border: ''none'',\n    borderRadius: ''12px'',\n    cursor: isProcessing ? ''not-allowed'' : ''pointer'',\n    transition: ''all 0.3s ease'',\n    background: ''linear-gradient(135deg, #667eea 0%, #764ba2 100%)'',\n    color: ''white'',\n    boxShadow: ''0 4px 12px rgba(102, 126, 234, 0.3)''\n  };\n\n  return (\n    <div style={{ height: ''100%'', overflowY: ''auto'' }}>\n      {/* Search */}\n      <div style={sectionStyle}>\n        <label style={labelStyle}>\n          <i className=\"fa-solid fa-magnifying-glass\" style={{ marginRight: ''6px'' }}></i>\n          Search\n        </label>\n        <div style={{ position: ''relative'' }}>\n          <input\n            type=\"text\"\n            value={localSearch}\n            onChange={(e) => handleSearchChange(e.target.value)}\n            placeholder=\"Search prompts...\"\n            style={{\n              ...inputStyle,\n              paddingLeft: ''40px''\n            }}\n            onFocus={(e) => {\n              e.target.style.borderColor = ''#667eea'';\n              e.target.style.boxShadow = ''0 0 0 3px rgba(102, 126, 234, 0.1)'';\n            }}\n            onBlur={(e) => {\n              e.target.style.borderColor = ''transparent'';\n              e.target.style.boxShadow = ''none'';\n            }}\n            disabled={isProcessing}\n          />\n          <i className=\"fa-solid fa-magnifying-glass\" style={{\n            position: ''absolute'',\n            left: ''14px'',\n            top: ''50%'',\n            transform: ''translateY(-50%)'',\n            color: ''#a0aec0'',\n            fontSize: ''14px''\n          }}></i>\n        </div>\n      </div>\n\n      {/* Filters */}\n      <div style={sectionStyle}>\n        <h4 style={{\n          margin: ''0 0 16px 0'',\n          fontSize: ''15px'',\n          fontWeight: ''600'',\n          color: ''#2d3748'',\n          display: ''flex'',\n          alignItems: ''center''\n        }}>\n          <i className=\"fa-solid fa-filter\" style={{ marginRight: ''8px'', color: ''#667eea'' }}></i>\n          Filters\n        </h4>\n\n        {/* Category filter */}\n        <div style={{ marginBottom: styles.spacing?.sm || ''8px'' }}>\n          <label style={labelStyle}>Category</label>\n          <select\n            value={currentFilters.category || ''''}\n            onChange={(e) => onFilterChange({\n              ...currentFilters,\n              category: e.target.value || null\n            })}\n            style={inputStyle}\n            disabled={isProcessing}\n          >\n            <option value=\"\">All Categories</option>\n            {categories.map(cat => (\n              <option key={cat.id} value={cat.id}>{cat.name}</option>\n            ))}\n          </select>\n        </div>\n\n        {/* Type filter */}\n        <div style={{ marginBottom: styles.spacing?.sm || ''8px'' }}>\n          <label style={labelStyle}>Type</label>\n          <select\n            value={currentFilters.type || ''''}\n            onChange={(e) => onFilterChange({\n              ...currentFilters,\n              type: e.target.value || null\n            })}\n            style={inputStyle}\n            disabled={isProcessing}\n          >\n            <option value=\"\">All Types</option>\n            {types.map(type => (\n              <option key={type.id} value={type.id}>{type.name}</option>\n            ))}\n          </select>\n        </div>\n\n        {/* Status filter */}\n        <div style={{ marginBottom: styles.spacing?.sm || ''8px'' }}>\n          <label style={labelStyle}>Status</label>\n          <select\n            value={currentFilters.status || ''''}\n            onChange={(e) => onFilterChange({\n              ...currentFilters,\n              status: e.target.value || null\n            })}\n            style={inputStyle}\n            disabled={isProcessing}\n          >\n            <option value=\"\">All Statuses</option>\n            <option value=\"Active\">Active</option>\n            <option value=\"Pending\">Pending</option>\n            <option value=\"Disabled\">Disabled</option>\n          </select>\n        </div>\n\n        {/* Role filter */}\n        <div>\n          <label style={labelStyle}>Prompt Role</label>\n          <select\n            value={currentFilters.role || ''''}\n            onChange={(e) => onFilterChange({\n              ...currentFilters,\n              role: e.target.value || null\n            })}\n            style={inputStyle}\n            disabled={isProcessing}\n          >\n            <option value=\"\">All Roles</option>\n            <option value=\"System\">System</option>\n            <option value=\"User\">User</option>\n            <option value=\"Assistant\">Assistant</option>\n            <option value=\"SystemOrUser\">System or User</option>\n          </select>\n        </div>\n      </div>\n\n      {/* Clustering Parameters */}\n      <div style={sectionStyle}>\n        <h4 style={{\n          margin: ''0 0 16px 0'',\n          fontSize: ''15px'',\n          fontWeight: ''600'',\n          color: ''#2d3748'',\n          display: ''flex'',\n          alignItems: ''center''\n        }}>\n          <i className=\"fa-solid fa-diagram-project\" style={{ marginRight: ''8px'', color: ''#667eea'' }}></i>\n          Clustering\n        </h4>\n\n        {/* Number of clusters */}\n        <div style={{ marginBottom: styles.spacing?.sm || ''8px'' }}>\n          <label style={{...labelStyle, display: ''flex'', justifyContent: ''space-between'', marginBottom: ''10px''}}>\n            <span>Clusters</span>\n            <span style={{\n              background: ''linear-gradient(135deg, #667eea 0%, #764ba2 100%)'',\n              color: ''white'',\n              padding: ''2px 8px'',\n              borderRadius: ''6px'',\n              fontSize: ''12px'',\n              fontWeight: ''600''\n            }}>{clusterCount}</span>\n          </label>\n          <input\n            type=\"range\"\n            min=\"2\"\n            max=\"15\"\n            value={clusterCount}\n            onChange={(e) => onClusterCountChange(parseInt(e.target.value))}\n            style={{ width: ''100%'' }}\n            disabled={isProcessing}\n          />\n          <div style={{\n            display: ''flex'',\n            justifyContent: ''space-between'',\n            fontSize: styles.fonts?.sizes?.xs || ''10px'',\n            color: styles.colors?.text?.secondary || ''#666''\n          }}>\n            <span>2</span>\n            <span>15</span>\n          </div>\n        </div>\n\n        {/* Similarity threshold */}\n        <div>\n          <label style={{...labelStyle, display: ''flex'', justifyContent: ''space-between'', marginBottom: ''10px''}}>\n            <span>Similarity</span>\n            <span style={{\n              background: ''linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'',\n              color: ''white'',\n              padding: ''2px 8px'',\n              borderRadius: ''6px'',\n              fontSize: ''12px'',\n              fontWeight: ''600''\n            }}>{(similarityThreshold * 100).toFixed(0)}%</span>\n          </label>\n          <input\n            type=\"range\"\n            min=\"0\"\n            max=\"100\"\n            value={similarityThreshold * 100}\n            onChange={(e) => onSimilarityThresholdChange(parseInt(e.target.value) / 100)}\n            style={{ width: ''100%'' }}\n            disabled={isProcessing}\n          />\n          <div style={{\n            display: ''flex'',\n            justifyContent: ''space-between'',\n            fontSize: styles.fonts?.sizes?.xs || ''10px'',\n            color: styles.colors?.text?.secondary || ''#666''\n          }}>\n            <span>0%</span>\n            <span>100%</span>\n          </div>\n        </div>\n      </div>\n\n      {/* Actions */}\n      <div style={sectionStyle}>\n        <button\n          onClick={onRecalculate}\n          disabled={isProcessing}\n          style={{\n            ...buttonStyle,\n            opacity: isProcessing ? 0.6 : 1,\n            marginBottom: ''12px''\n          }}\n          onMouseEnter={e => !isProcessing && (e.currentTarget.style.transform = ''translateY(-2px)'')}\n          onMouseLeave={e => e.currentTarget.style.transform = ''translateY(0)''}\n        >\n          {isProcessing ? (\n            <>\n              <i className=\"fa-solid fa-spinner fa-spin\" style={{ marginRight: ''8px'' }}></i>\n              Processing...\n            </>\n          ) : (\n            <>\n              <i className=\"fa-solid fa-wand-magic-sparkles\" style={{ marginRight: ''8px'' }}></i>\n              Generate Clusters\n            </>\n          )}\n        </button>\n\n        <button\n          onClick={onExport}\n          disabled={isProcessing}\n          style={{\n            ...buttonStyle,\n            background: ''linear-gradient(135deg, #ffffff 0%, #f9fafb 100%)'',\n            color: ''#4a5568'',\n            border: ''2px solid #e2e8f0'',\n            boxShadow: ''0 2px 8px rgba(0,0,0,0.05)''\n          }}\n          onMouseEnter={e => !isProcessing && (e.currentTarget.style.transform = ''translateY(-2px)'')}\n          onMouseLeave={e => e.currentTarget.style.transform = ''translateY(0)''}\n        >\n          <i className=\"fa-solid fa-download\" style={{ marginRight: ''8px'' }}></i>\n          Export CSV\n        </button>\n      </div>\n\n      {/* Statistics */}\n      <div style={{\n        ...sectionStyle,\n        fontSize: styles.fonts?.sizes?.xs || ''12px'',\n        color: styles.colors?.text?.secondary || ''#666''\n      }}>\n        <h4 style={{\n          margin: `0 0 $' + '{styles.spacing?.xs || ''4px''} 0`,\n          fontSize: styles.fonts?.sizes?.sm || ''14px'',\n          color: styles.colors?.text?.primary || ''#333''\n        }}>\n          Statistics\n        </h4>\n        <div>Categories: {categories.length}</div>\n        <div>Types: {types.length}</div>\n        <div>Active Filters: {\n          Object.values(currentFilters).filter(v => v).length\n        }</div>\n      </div>\n    </div>\n  );\n}","functionalRequirements":"Provide search input for finding prompts, filter by category/type/status, adjust clustering parameters, control similarity threshold, toggle view options, export cluster assignments","technicalDesign":"React component with controlled inputs for various filters and parameters, emits change events to parent component for re-clustering or re-filtering","properties":[{"name":"categories","type":"array","required":true,"description":"Available prompt categories for filtering"},{"name":"types","type":"array","required":true,"description":"Available prompt types for filtering"},{"name":"currentFilters","type":"object","required":true,"description":"Current filter state"},{"name":"clusterCount","type":"number","required":true,"description":"Number of clusters to generate"},{"name":"similarityThreshold","type":"number","required":true,"description":"Current similarity threshold"},{"name":"isProcessing","type":"boolean","required":true,"description":"Whether embeddings are being generated"}],"events":[{"name":"onSearchChange","description":"Fired when search text changes","parameters":[{"name":"searchText","type":"string","description":"The search query"}]},{"name":"onFilterChange","description":"Fired when filters are updated","parameters":[{"name":"filters","type":"object","description":"Updated filter object"}]},{"name":"onClusterCountChange","description":"Fired when cluster count is adjusted","parameters":[{"name":"count","type":"number","description":"New cluster count"}]},{"name":"onSimilarityThresholdChange","description":"Fired when similarity threshold changes","parameters":[{"name":"threshold","type":"number","description":"New threshold value"}]},{"name":"onRecalculate","description":"Fired when user requests recalculation","parameters":[]},{"name":"onExport","description":"Fired when user wants to export clusters","parameters":[]}],"libraries":[],"usageExample":"<AIPromptsClusterControls categories={categories} onFilterChange={handleFilterChange} />","version":"1.0.0"}'
