SET
  @Specification_f8f62e6b = N'{"name":"EntityDetails","title":"Entity Details Panel","description":"Sliding panel that displays detailed information about a selected entity including fields and relationships","type":"form","functionalRequirements":"## Entity Details Requirements\n\n- Slide in from the right when an entity is selected\n- Display entity metadata at the top\n- Show fields in a formatted table\n- Display relationships with icons\n- Include ''Open Record'' button\n- Support closing via X button or Escape key\n- Smooth slide animation\n- Scrollable content area","technicalDesign":"## Technical Design\n\n### Props\n- entity: Selected entity object\n- fields: Array of entity fields\n- relationships: Array of entity relationships\n- isOpen: Whether panel is visible\n- onClose: Callback to close panel\n- onOpenRecord: Callback to open entity record\n\n### Layout\n- Fixed position overlay\n- Slide animation using transform\n- Header with entity name and close button\n- Sections for metadata, fields, relationships\n- Sticky ''Open Record'' button at bottom","dataRequirements":{"mode":"views","entities":[{"name":"Entities","description":"Metadata about all entities in the system","displayFields":["ID","Name","DisplayName","NameSuffix","Description","SchemaName","BaseTable","BaseView"],"filterFields":["SchemaName","BaseTable"],"sortFields":["Name","DisplayName"],"fieldMetadata":[{"name":"ID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":true,"description":"Unique identifier for the entity"},{"name":"Name","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"System name of the entity"},{"name":"DisplayName","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"User-friendly display name for the entity"},{"name":"NameSuffix","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Optional suffix appended to entity names for display purposes"},{"name":"Description","sequence":5,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Description of the entity"},{"name":"SchemaName","sequence":6,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Database schema name"},{"name":"BaseTable","sequence":7,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"Base table in the database"},{"name":"BaseView","sequence":8,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Base view used for the entity"}],"permissionLevelNeeded":["read"],"usageContext":"Main entity list display and filtering"},{"name":"Entity Fields","description":"Fields belonging to each entity","displayFields":["Name","DisplayName","Type","Length","AllowsNull","IsPrimaryKey","IsUnique"],"filterFields":["EntityID"],"sortFields":["Sequence","Name"],"fieldMetadata":[{"name":"EntityID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":false,"description":"Reference to parent entity"},{"name":"Name","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Field name"},{"name":"DisplayName","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"User-friendly field name"},{"name":"Type","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Data type of the field"},{"name":"Length","sequence":5,"defaultInView":true,"type":"int","allowsNull":true,"isPrimaryKey":false,"description":"Maximum length for string fields"},{"name":"AllowsNull","sequence":6,"defaultInView":true,"type":"bit","allowsNull":false,"isPrimaryKey":false,"description":"Whether field allows null values"},{"name":"IsPrimaryKey","sequence":7,"defaultInView":true,"type":"bit","allowsNull":false,"isPrimaryKey":false,"description":"Whether field is part of primary key"},{"name":"IsUnique","sequence":8,"defaultInView":true,"type":"bit","allowsNull":false,"isPrimaryKey":false,"description":"Whether field must be unique"},{"name":"Sequence","sequence":9,"defaultInView":false,"type":"int","allowsNull":false,"isPrimaryKey":false,"description":"Display order of the field"}],"permissionLevelNeeded":["read"],"usageContext":"Details panel to show entity fields"},{"name":"Entity Relationships","description":"Relationships between entities","displayFields":["RelatedEntity","Type","DisplayName","RelatedEntityJoinField"],"filterFields":["EntityID"],"sortFields":["Sequence","RelatedEntity"],"fieldMetadata":[{"name":"EntityID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":false,"description":"Reference to parent entity"},{"name":"RelatedEntity","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"The related entity in the relationship"},{"name":"Type","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Type of relationship (One to Many, Many to One, etc.)"},{"name":"DisplayName","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"User-friendly name for the relationship"},{"name":"RelatedEntityJoinField","sequence":5,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"The field in the related entity that joins to this entity"},{"name":"Sequence","sequence":6,"defaultInView":false,"type":"int","allowsNull":false,"isPrimaryKey":false,"description":"Display order"}],"permissionLevelNeeded":["read"],"usageContext":"Details panel to show entity relationships"}],"queries":[],"description":"This component requires access to entity metadata including entities, their fields, and relationships to provide a comprehensive entity browsing experience"},"properties":[{"name":"entity","description":"The selected entity object","type":"object","required":true},{"name":"fields","description":"Array of fields for the entity","type":"array","required":true,"defaultValue":[]},{"name":"relationships","description":"Array of relationships for the entity","type":"array","required":true,"defaultValue":[]},{"name":"isOpen","description":"Whether the panel is open","type":"boolean","required":true},{"name":"onClose","description":"Callback to close the panel","type":"function","required":true},{"name":"onOpenRecord","description":"Callback to open the entity record","type":"function","required":true}],"events":[{"name":"onClose","description":"Fired when the panel should close","parameters":[]},{"name":"onOpenRecord","description":"Fired when the open record button is clicked","parameters":[{"name":"entityName","description":"Name of the entity to open","type":"string"}]}],"exampleUsage":"<EntityDetails\n  entity={selectedEntity}\n  fields={entityFields}\n  relationships={entityRelationships}\n  isOpen={detailsPanelOpen}\n  onClose={handleCloseDetails}\n  onOpenRecord={handleOpenRecord}\n  utilities={utilities}\n  styles={styles}\n  components={components}\n  callbacks={callbacks}\n/>","code":"function EntityDetails({ \n  entity, \n  fields, \n  relationships, \n  isOpen, \n  onClose, \n  onOpenRecord,\n  utilities, \n  styles, \n  components, \n  callbacks, \n  savedUserSettings, \n  onSaveUserSettings \n}) {\n  // Helper function to get border radius value\n  const getBorderRadius = (size) => {\n    return typeof styles.borders.radius === ''object'' ? styles.borders.radius[size] : styles.borders.radius;\n  };\n  \n  // Handle escape key to close panel\n  useEffect(() => {\n    const handleEscape = (e) => {\n      if (e.key === ''Escape'' && isOpen) {\n        onClose?.();\n      }\n    };\n    \n    document.addEventListener(''keydown'', handleEscape);\n    return () => document.removeEventListener(''keydown'', handleEscape);\n  }, [isOpen, onClose]);\n  \n  // Load OpenRecordButton component\n  const OpenRecordButton = components[''OpenRecordButton''];\n  \n  // Render field type badge\n  const renderFieldType = (type) => {\n    const typeColors = {\n      ''nvarchar'': styles.colors.info || styles.colors.primary,\n      ''varchar'': styles.colors.info || styles.colors.primary,\n      ''int'': styles.colors.success || styles.colors.primary,\n      ''bigint'': styles.colors.success || styles.colors.primary,\n      ''decimal'': styles.colors.success || styles.colors.primary,\n      ''float'': styles.colors.success || styles.colors.primary,\n      ''bit'': styles.colors.warning || styles.colors.secondary,\n      ''datetime'': styles.colors.secondary,\n      ''uniqueidentifier'': styles.colors.primary,\n      ''text'': styles.colors.info || styles.colors.primary,\n      ''ntext'': styles.colors.info || styles.colors.primary\n    };\n    \n    const color = typeColors[type?.toLowerCase()] || styles.colors.textSecondary;\n    \n    return (\n      <span style={{\n        display: ''inline-block'',\n        padding: `$' + '{styles.spacing.xs} $' + '{styles.spacing.sm}`,\n        backgroundColor: color + ''15'',\n        color: color,\n        borderRadius: getBorderRadius(''sm''),\n        fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n        fontWeight: styles.typography.fontWeight?.medium || ''500''\n      }}>\n        {type}\n      </span>\n    );\n  };\n  \n  // Render relationship type icon\n  const renderRelationshipIcon = (type) => {\n    const icons = {\n      ''One to Many'': ''1:N'',\n      ''Many to One'': ''N:1'',\n      ''Many to Many'': ''N:N'',\n      ''One to One'': ''1:1''\n    };\n    \n    return (\n      <span style={{\n        display: ''inline-block'',\n        padding: `$' + '{styles.spacing.xs} $' + '{styles.spacing.sm}`,\n        backgroundColor: styles.colors.primary + ''15'',\n        color: styles.colors.primary,\n        borderRadius: getBorderRadius(''sm''),\n        fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n        fontWeight: styles.typography.fontWeight?.bold || ''700'',\n        fontFamily: ''monospace''\n      }}>\n        {icons[type] || type}\n      </span>\n    );\n  };\n  \n  return (\n    <>\n      {/* Backdrop */}\n      {isOpen && (\n        <div\n          onClick={onClose}\n          style={{\n            position: ''fixed'',\n            top: 0,\n            left: 0,\n            right: 0,\n            bottom: 0,\n            backgroundColor: ''rgba(0, 0, 0, 0.3)'',\n            zIndex: 99999,\n            opacity: isOpen ? 1 : 0,\n            transition: ''opacity 0.3s'',\n            pointerEvents: isOpen ? ''auto'' : ''none''\n          }}\n        />\n      )}\n      \n      {/* Panel */}\n      <div style={{\n        position: ''fixed'',\n        top: ''75px'',\n        right: 0,\n        bottom: 0,\n        width: ''480px'',\n        backgroundColor: styles.colors.background,\n        boxShadow: isOpen ? `-4px 0 24px $' + '{styles.colors.shadow || ''rgba(0, 0, 0, 0.1)''}` : ''none'',\n        transform: isOpen ? ''translateX(0)'' : ''translateX(100%)'',\n        transition: ''transform 0.3s ease-out'',\n        zIndex: 100000,\n        display: ''flex'',\n        flexDirection: ''column'',\n        overflow: ''hidden''\n      }}>\n        {/* Header */}\n        <div style={{\n          padding: styles.spacing.lg,\n          borderBottom: `1px solid $' + '{styles.colors.border}`,\n          backgroundColor: styles.colors.surface\n        }}>\n          <div style={{\n            display: ''flex'',\n            justifyContent: ''space-between'',\n            alignItems: ''flex-start''\n          }}>\n            <div style={{ flex: 1 }}>\n              <h2 style={{\n                margin: 0,\n                fontSize: styles.typography.fontSize.xl,\n                fontWeight: styles.typography.fontWeight?.bold || ''700'',\n                color: styles.colors.text,\n                marginBottom: styles.spacing.xs\n              }}>\n                {entity?.DisplayName || entity?.Name || ''No Entity Selected''}\n              </h2>\n              {entity?.DisplayName && entity?.Name && entity.DisplayName !== entity.Name && (\n                <div style={{\n                  fontSize: styles.typography.fontSize.sm,\n                  color: styles.colors.textSecondary,\n                  fontFamily: ''monospace''\n                }}>\n                  {entity.Name}\n                </div>\n              )}\n            </div>\n            <button\n              onClick={onClose}\n              style={{\n                width: ''32px'',\n                height: ''32px'',\n                borderRadius: getBorderRadius(''sm''),\n                border: ''none'',\n                backgroundColor: ''transparent'',\n                color: styles.colors.textSecondary,\n                fontSize: styles.typography.fontSize.lg,\n                cursor: ''pointer'',\n                display: ''flex'',\n                alignItems: ''center'',\n                justifyContent: ''center'',\n                transition: ''background-color 0.2s''\n              }}\n              onMouseEnter={(e) => {\n                e.currentTarget.style.backgroundColor = styles.colors.surfaceHover || styles.colors.surface;\n              }}\n              onMouseLeave={(e) => {\n                e.currentTarget.style.backgroundColor = ''transparent'';\n              }}\n            >\n              âœ•\n            </button>\n          </div>\n        </div>\n        \n        {/* Content */}\n        <div style={{\n          flex: 1,\n          overflow: ''auto'',\n          padding: styles.spacing.lg\n        }}>\n          {entity ? (\n            <>\n              {/* Entity Metadata */}\n              {entity.Description && (\n                <div style={{\n                  marginBottom: styles.spacing.xl,\n                  padding: styles.spacing.md,\n                  backgroundColor: styles.colors.surface,\n                  borderRadius: getBorderRadius(''md''),\n                  borderLeft: `3px solid $' + '{styles.colors.primary}`\n                }}>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.md,\n                    color: styles.colors.textSecondary,\n                    lineHeight: 1.6\n                  }}>\n                    {entity.Description}\n                  </div>\n                </div>\n              )}\n              \n              {/* Quick Info */}\n              <div style={{\n                display: ''grid'',\n                gridTemplateColumns: ''repeat(2, 1fr)'',\n                gap: styles.spacing.md,\n                marginBottom: styles.spacing.xl\n              }}>\n                <div style={{\n                  padding: styles.spacing.md,\n                  backgroundColor: styles.colors.surface,\n                  borderRadius: getBorderRadius(''sm'')\n                }}>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                    color: styles.colors.textSecondary,\n                    marginBottom: styles.spacing.xs\n                  }}>\n                    Schema\n                  </div>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.md,\n                    fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                    color: styles.colors.text\n                  }}>\n                    {entity.SchemaName || ''-''}\n                  </div>\n                </div>\n                <div style={{\n                  padding: styles.spacing.md,\n                  backgroundColor: styles.colors.surface,\n                  borderRadius: getBorderRadius(''sm'')\n                }}>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                    color: styles.colors.textSecondary,\n                    marginBottom: styles.spacing.xs\n                  }}>\n                    Base Table\n                  </div>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.md,\n                    fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                    color: styles.colors.text\n                  }}>\n                    {entity.BaseTable || ''-''}\n                  </div>\n                </div>\n                <div style={{\n                  padding: styles.spacing.md,\n                  backgroundColor: styles.colors.surface,\n                  borderRadius: getBorderRadius(''sm'')\n                }}>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                    color: styles.colors.textSecondary,\n                    marginBottom: styles.spacing.xs\n                  }}>\n                    Base View\n                  </div>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.md,\n                    fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                    color: styles.colors.text\n                  }}>\n                    {entity.BaseView || ''-''}\n                  </div>\n                </div>\n                <div style={{\n                  padding: styles.spacing.md,\n                  backgroundColor: styles.colors.surface,\n                  borderRadius: getBorderRadius(''sm'')\n                }}>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                    color: styles.colors.textSecondary,\n                    marginBottom: styles.spacing.xs\n                  }}>\n                    Field Count\n                  </div>\n                  <div style={{\n                    fontSize: styles.typography.fontSize.md,\n                    fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                    color: styles.colors.text\n                  }}>\n                    {fields?.length || 0}\n                  </div>\n                </div>\n              </div>\n              \n              {/* Fields Section */}\n              <div style={{ marginBottom: styles.spacing.xl }}>\n                <h3 style={{\n                  margin: 0,\n                  marginBottom: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.lg,\n                  fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                  color: styles.colors.text\n                }}>\n                  Fields ({fields?.length || 0})\n                </h3>\n                <div style={{\n                  backgroundColor: styles.colors.surface,\n                  borderRadius: getBorderRadius(''md''),\n                  overflow: ''hidden''\n                }}>\n                  {fields && fields.length > 0 ? (\n                    <table style={{\n                      width: ''100%'',\n                      borderCollapse: ''collapse''\n                    }}>\n                      <thead>\n                        <tr style={{\n                          borderBottom: `1px solid $' + '{styles.colors.border}`\n                        }}>\n                          <th style={{\n                            padding: styles.spacing.sm,\n                            textAlign: ''left'',\n                            fontSize: styles.typography.fontSize.sm,\n                            fontWeight: styles.typography.fontWeight?.medium || ''500'',\n                            color: styles.colors.textSecondary\n                          }}>\n                            Field\n                          </th>\n                          <th style={{\n                            padding: styles.spacing.sm,\n                            textAlign: ''left'',\n                            fontSize: styles.typography.fontSize.sm,\n                            fontWeight: styles.typography.fontWeight?.medium || ''500'',\n                            color: styles.colors.textSecondary\n                          }}>\n                            Type\n                          </th>\n                          <th style={{\n                            padding: styles.spacing.sm,\n                            textAlign: ''center'',\n                            fontSize: styles.typography.fontSize.sm,\n                            fontWeight: styles.typography.fontWeight?.medium || ''500'',\n                            color: styles.colors.textSecondary\n                          }}>\n                            Attributes\n                          </th>\n                        </tr>\n                      </thead>\n                      <tbody>\n                        {fields.map((field, index) => (\n                          <tr\n                            key={index}\n                            style={{\n                              borderBottom: index < fields.length - 1 \n                                ? `1px solid $' + '{styles.colors.borderLight || styles.colors.border}` \n                                : ''none''\n                            }}\n                          >\n                            <td style={{\n                              padding: styles.spacing.sm,\n                              fontSize: styles.typography.fontSize.sm,\n                              color: styles.colors.text\n                            }}>\n                              <div>\n                                <div style={{\n                                  fontWeight: field.IsPrimaryKey \n                                    ? (styles.typography.fontWeight?.semibold || ''600'')\n                                    : (styles.typography.fontWeight?.regular || ''400'')\n                                }}>\n                                  {field.DisplayName || field.Name}\n                                </div>\n                                {field.DisplayName && (\n                                  <div style={{\n                                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                                    color: styles.colors.textSecondary,\n                                    fontFamily: ''monospace''\n                                  }}>\n                                    {field.Name}\n                                  </div>\n                                )}\n                              </div>\n                            </td>\n                            <td style={{\n                              padding: styles.spacing.sm\n                            }}>\n                              {renderFieldType(field.Type)}\n                              {field.Length && (\n                                <span style={{\n                                  marginLeft: styles.spacing.xs,\n                                  fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                                  color: styles.colors.textSecondary\n                                }}>\n                                  ({field.Length})\n                                </span>\n                              )}\n                            </td>\n                            <td style={{\n                              padding: styles.spacing.sm,\n                              textAlign: ''center''\n                            }}>\n                              <div style={{\n                                display: ''flex'',\n                                gap: styles.spacing.xs,\n                                justifyContent: ''center'',\n                                flexWrap: ''wrap''\n                              }}>\n                                {field.IsPrimaryKey && (\n                                  <span style={{\n                                    padding: `2px $' + '{styles.spacing.xs}`,\n                                    backgroundColor: (styles.colors.warning || styles.colors.secondary) + ''15'',\n                                    color: styles.colors.warning || styles.colors.secondary,\n                                    borderRadius: getBorderRadius(''xs'') || getBorderRadius(''sm''),\n                                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                                    fontWeight: styles.typography.fontWeight?.bold || ''700''\n                                  }}>\n                                    PK\n                                  </span>\n                                )}\n                                {field.IsUnique && (\n                                  <span style={{\n                                    padding: `2px $' + '{styles.spacing.xs}`,\n                                    backgroundColor: (styles.colors.info || styles.colors.primary) + ''15'',\n                                    color: styles.colors.info || styles.colors.primary,\n                                    borderRadius: getBorderRadius(''xs'') || getBorderRadius(''sm''),\n                                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                                    fontWeight: styles.typography.fontWeight?.bold || ''700''\n                                  }}>\n                                    UQ\n                                  </span>\n                                )}\n                                {!field.AllowsNull && !field.IsPrimaryKey && (\n                                  <span style={{\n                                    padding: `2px $' + '{styles.spacing.xs}`,\n                                    backgroundColor: (styles.colors.error || styles.colors.secondary) + ''15'',\n                                    color: styles.colors.error || styles.colors.secondary,\n                                    borderRadius: getBorderRadius(''xs'') || getBorderRadius(''sm''),\n                                    fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                                    fontWeight: styles.typography.fontWeight?.bold || ''700''\n                                  }}>\n                                    NN\n                                  </span>\n                                )}\n                              </div>\n                            </td>\n                          </tr>\n                        ))}\n                      </tbody>\n                    </table>\n                  ) : (\n                    <div style={{\n                      padding: styles.spacing.lg,\n                      textAlign: ''center'',\n                      color: styles.colors.textSecondary,\n                      fontSize: styles.typography.fontSize.sm\n                    }}>\n                      No fields available\n                    </div>\n                  )}\n                </div>\n              </div>\n              \n              {/* Relationships Section */}\n              <div style={{ marginBottom: styles.spacing.xl }}>\n                <h3 style={{\n                  margin: 0,\n                  marginBottom: styles.spacing.md,\n                  fontSize: styles.typography.fontSize.lg,\n                  fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                  color: styles.colors.text\n                }}>\n                  Relationships ({relationships?.length || 0})\n                </h3>\n                <div style={{\n                  display: ''flex'',\n                  flexDirection: ''column'',\n                  gap: styles.spacing.sm\n                }}>\n                  {relationships && relationships.length > 0 ? (\n                    relationships.map((rel, index) => (\n                      <div\n                        key={index}\n                        style={{\n                          padding: styles.spacing.md,\n                          backgroundColor: styles.colors.surface,\n                          borderRadius: getBorderRadius(''sm''),\n                          display: ''flex'',\n                          alignItems: ''center'',\n                          gap: styles.spacing.md\n                        }}\n                      >\n                        {renderRelationshipIcon(rel.Type)}\n                        <div style={{ flex: 1 }}>\n                          <div style={{\n                            fontSize: styles.typography.fontSize.md,\n                            fontWeight: styles.typography.fontWeight?.medium || ''500'',\n                            color: styles.colors.text\n                          }}>\n                            {rel.DisplayName || rel.RelatedEntity}\n                          </div>\n                          {rel.RelatedEntityJoinField && (\n                            <div style={{\n                              fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                              color: styles.colors.textSecondary,\n                              fontFamily: ''monospace''\n                            }}>\n                              via {rel.RelatedEntityJoinField}\n                            </div>\n                          )}\n                        </div>\n                      </div>\n                    ))\n                  ) : (\n                    <div style={{\n                      padding: styles.spacing.lg,\n                      backgroundColor: styles.colors.surface,\n                      borderRadius: getBorderRadius(''sm''),\n                      textAlign: ''center'',\n                      color: styles.colors.textSecondary,\n                      fontSize: styles.typography.fontSize.sm\n                    }}>\n                      No relationships defined\n                    </div>\n                  )}\n                </div>\n              </div>\n            </>\n          ) : (\n            <div style={{\n              display: ''flex'',\n              flexDirection: ''column'',\n              alignItems: ''center'',\n              justifyContent: ''center'',\n              height: ''100%'',\n              color: styles.colors.textSecondary\n            }}>\n              <div style={{\n                fontSize: styles.typography.fontSize.lg,\n                marginBottom: styles.spacing.md\n              }}>\n                No Entity Selected\n              </div>\n              <div style={{\n                fontSize: styles.typography.fontSize.md\n              }}>\n                Select an entity from the list to view its details\n              </div>\n            </div>\n          )}\n        </div>\n        \n        {/* Footer with Open Record Button */}\n        {entity && OpenRecordButton && (\n          <div style={{\n            padding: styles.spacing.lg,\n            borderTop: `1px solid $' + '{styles.colors.border}`,\n            backgroundColor: styles.colors.surface\n          }}>\n            <OpenRecordButton\n              entityName=\"Entities\"\n              record={entity}\n              buttonText=\"Open Entity Record\"\n              utilities={utilities}\n              styles={styles}\n              components={components}\n              callbacks={callbacks}\n              savedUserSettings={savedUserSettings}\n              onSaveUserSettings={onSaveUserSettings}\n              buttonStyle={{\n                width: ''100%'',\n                padding: styles.spacing.md,\n                backgroundColor: styles.colors.primary,\n                color: ''white'',\n                border: ''none'',\n                borderRadius: getBorderRadius(''md''),\n                fontSize: styles.typography.fontSize.md,\n                fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                cursor: ''pointer'',\n                transition: ''background-color 0.2s''\n              }}\n            />\n          </div>\n        )}\n      </div>\n    </>\n  );\n}","dependencies":[{"name":"OpenRecordButton","location":"registry","namespace":"Generic/Navigation","version":"^1.0.0"}],"libraries":[]}'
