  @Specification_ea3a666b = N'{"name":"AccountsByIndustryDetails","title":"Accounts by Industry Details","description":"Sliding detail panel that displays comprehensive account information with an open record action button","type":"component","location":"embedded","namespace":"CRM/Analytics","functionalRequirements":"## Account Details Panel\n\n- Slide in from right with smooth animation\n- Display account name and industry prominently using SingleRecordView\n- Show key metrics (revenue, status, type) with consistent formatting\n- Include contact information section with custom formatting\n- Display location details if available\n- Provide ''Open Record'' action button\n- Support close via X button or overlay click\n- Use SingleRecordView for standardized basic account information display","technicalDesign":"## Technical Design\n\n### Props\n- account: Selected account object\n- isOpen: Panel visibility state\n- onClose: Close handler callback\n- onOpenRecord: Open record callback\n\n### Layout\n- Fixed positioning overlay\n- Transform-based slide animation\n- Sections for different data types\n- Sticky header with close button\n- Scrollable content area","dataRequirements":{"mode":"views","description":"Fetches account data using RunView for industry distribution analysis","entities":[{"name":"Accounts","description":"CRM account data grouped by industry","displayFields":["ID","AccountName","Industry","Revenue","Status","IsActive"],"filterFields":["IsActive","Industry"],"sortFields":["AccountName","Revenue","Industry"],"fieldMetadata":[],"usageContext":"Groups accounts by industry for pie chart visualization and filtered list display"}],"queries":[]},"properties":[{"name":"account","type":"object","required":true,"description":"Selected account object with all details"},{"name":"isOpen","type":"boolean","required":true,"description":"Whether the panel is visible"},{"name":"onClose","type":"function","required":true,"description":"Callback to close the panel"}],"exampleUsage":"<AccountsByIndustryDetails\n  account={selectedAccount}\n  isOpen={showDetails}\n  onClose={() => setShowDetails(false)}\n  onOpenRecord={handleOpenRecord}\n/>","code":"function AccountsByIndustryDetails ({ account, isOpen, onClose, onOpenRecord, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  if (!isOpen || !account) {\n    return null;\n  }\n\n  // Load components from registry\n  const OpenRecordButton = components?.OpenRecordButton;\n  const SingleRecordView = components?.SingleRecordView;\n\n  // Format currency\n  const formatCurrency = (value) => {\n    if (!value) return ''-'';\n    return new Intl.NumberFormat(''en-US'', {\n      style: ''currency'',\n      currency: ''USD'',\n      notation: ''compact'',\n      maximumFractionDigits: 1\n    }).format(value);\n  };\n   \n  return (\n    <>\n      {/* Overlay */}\n      <div\n        onClick={onClose}\n        style={{\n          position: ''fixed'',\n          top: 0,\n          left: 0,\n          right: 0,\n          bottom: 0,\n          backgroundColor: ''rgba(0, 0, 0, 0.3)'',\n          zIndex: 999\n        }}\n      />\n      \n      {/* Panel */}\n      <div style={{\n        position: ''fixed'',\n        top: ''75px'',\n        right: 0,\n        bottom: 0,\n        width: ''400px'',\n        backgroundColor: ''#fff'',\n        boxShadow: ''-4px 0 24px rgba(0, 0, 0, 0.1)'',\n        zIndex: 1000,\n        transform: ''translateX(0)'',\n        overflowY: ''auto'',\n        borderTopLeftRadius: ''8px''\n      }}>\n        {/* Panel Header */}\n        <div style={{\n          padding: ''20px'',\n          borderBottom: ''1px solid #E5E7EB'',\n          display: ''flex'',\n          justifyContent: ''space-between'',\n          alignItems: ''center''\n        }}>\n          <h3 style={{ fontSize: ''18px'', fontWeight: ''600'', color: ''#111827'' }}>\n            Account Details\n          </h3>\n          <button\n            onClick={onClose}\n            style={{\n              width: ''32px'',\n              height: ''32px'',\n              display: ''flex'',\n              alignItems: ''center'',\n              justifyContent: ''center'',\n              backgroundColor: ''#F3F4F6'',\n              border: ''none'',\n              borderRadius: ''6px'',\n              cursor: ''pointer'',\n              fontSize: ''18px'',\n              color: ''#6B7280''\n            }}\n          >\n            Ã—\n          </button>\n        </div>\n        \n        {/* Panel Content */}\n        <div style={{ padding: ''20px'' }}>\n          {/* Account Name and Open Button */}\n          <div style={{ marginBottom: ''24px'' }}>\n            <div style={{\n              display: ''flex'',\n              justifyContent: ''space-between'',\n              alignItems: ''center'',\n              marginBottom: ''12px''\n            }}>\n              <h4 style={{\n                fontSize: ''20px'',\n                fontWeight: ''700'',\n                color: ''#111827'',\n                margin: ''0''\n              }}>\n                {account.AccountName}\n              </h4>\n              {OpenRecordButton ? (\n                <OpenRecordButton\n                  entityName=\"Accounts\"\n                  record={account}\n                  buttonText=\"Open Record â†—\"\n                  utilities={utilities}\n                  styles={styles}\n                  components={components}\n                  callbacks={callbacks}\n                  savedUserSettings={savedUserSettings}\n                  onSaveUserSettings={onSaveUserSettings}\n                  buttonStyle={{\n                    padding: ''8px 16px'',\n                    fontSize: ''14px'',\n                    fontWeight: ''500'',\n                    color: ''#fff'',\n                    backgroundColor: ''#3B82F6'',\n                    border: ''none'',\n                    borderRadius: ''6px'',\n                    cursor: ''pointer'',\n                    display: ''flex'',\n                    alignItems: ''center'',\n                    gap: ''4px''\n                  }}\n                />\n              ) : (\n                <button\n                  onClick={onOpenRecord}\n                  style={{\n                    padding: ''8px 16px'',\n                    fontSize: ''14px'',\n                    fontWeight: ''500'',\n                    color: ''#fff'',\n                    backgroundColor: ''#3B82F6'',\n                    border: ''none'',\n                    borderRadius: ''6px'',\n                    cursor: ''pointer'',\n                    display: ''flex'',\n                    alignItems: ''center'',\n                    gap: ''4px''\n                  }}\n                >\n                  Open Record â†—\n                </button>\n              )}\n            </div>\n            <div style={{ fontSize: ''14px'', color: ''#6B7280'' }}>\n              {account.Industry || ''No industry''}\n            </div>\n          </div>\n          \n          {/* Key Information - Using SingleRecordView */}\n          <div style={{ marginBottom: ''24px'' }}>\n            <h5 style={{\n              fontSize: ''12px'',\n              fontWeight: ''600'',\n              color: ''#6B7280'',\n              textTransform: ''uppercase'',\n              letterSpacing: ''0.05em'',\n              marginBottom: ''12px''\n            }}>\n              Key Information\n            </h5>\n\n            {SingleRecordView ? (\n              <SingleRecordView\n                entityName=\"Accounts\"\n                record={account}\n                fields={[\"Industry\", \"AnnualRevenue\", \"AccountStatus\", \"AccountType\"]}\n                layout=\"list\"\n                showLabels={true}\n                showEmptyFields={false}\n                allowOpenRecord={false}\n                utilities={utilities}\n                styles={styles}\n                components={components}\n               