  @Specification_13eb01d1 = N'{"name":"RecentDealsList","title":"Recent Deals List","description":"Displays a list of the most recent deals with stage indicators and key metrics","type":"component","location":"embedded","namespace":"CRM/Sales","functionalRequirements":"## Recent Deals Display\n\n### Core Features\n- Display 10-20 most recent deals\n- Show key deal information: name, account, amount, stage\n- Color-code rows by stage for quick visual scanning\n- Format currency values appropriately\n- Show days until close date\n\n### Interactivity\n- Clickable rows to view deal details\n- Hover effects for better UX\n- Optional refresh button to reload data\n\n### Visual Design\n- Clean table layout with good spacing\n- Stage badges with semantic colors\n- Highlight deals closing soon\n- Show probability as progress bar","dataRequirements":{"mode":"views","description":"Fetches recent deals data using RunView","entities":[{"name":"Deals","description":"CRM deals/opportunities data","displayFields":["ID","DealName","AccountID","Account","Stage","Amount","Probability","CloseDate","DealSource","OwnerID","Owner","__mj_CreatedAt"],"filterFields":["CloseDate","Stage"],"sortFields":["CloseDate","Amount","DealName","__mj_CreatedAt"],"fieldMetadata":[],"permissionLevelNeeded":["read"],"usageContext":"Displays recent deals with stage indicators and metrics"}],"queries":[]},"technicalDesign":"## Implementation\n\n### Data Loading\n- Use RunView to load recent deals\n- Sort by CreatedDate DESC or CloseDate ASC\n- Limit to configurable number of rows (default 15)\n\n### Stage Styling\n- Prospecting: Gray\n- Qualification: Blue\n- Proposal: Yellow\n- Negotiation: Orange\n- Closed Won: Green\n- Closed Lost: Red\n\n### Calculations\n- Days to close: CloseDate - today\n- Expected revenue: Amount * Probability / 100\n- Format amounts with currency symbol","properties":[],"exampleUsage":"<RecentDealsList\n  maxRows={20}\n  sortBy=\"CloseDate\"\n  onDealClick={handleDealClick}\n/>","code":"function RecentDealsList({ utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  // Load DataGrid component from registry\n  const DataGrid = components[''DataGrid''];\n\n  const [deals, setDeals] = useState([]);\n  const [loading, setLoading] = useState(true);\n  const [error, setError] = useState(null);\n  const [sortBy, setSortBy] = useState(savedUserSettings?.sortBy || ''CloseDate'');\n  const [sortDirection, setSortDirection] = useState(savedUserSettings?.sortDirection || ''ASC'');\n  const [maxRows] = useState(savedUserSettings?.maxRows || 15);\n  const [dateFrom, setDateFrom] = useState(savedUserSettings?.dateFrom || '''');\n  const [dateTo, setDateTo] = useState(savedUserSettings?.dateTo || '''');\n  const [selectedDeal, setSelectedDeal] = useState(null);\n\n  // Stage colors mapping\n  const stageColors = {\n    ''Prospecting'': { bg: ''#F3F4F6'', text: ''#6B7280'', border: ''#D1D5DB'' },\n    ''Qualification'': { bg: ''#DBEAFE'', text: ''#1E40AF'', border: ''#93C5FD'' },\n    ''Proposal'': { bg: ''#FEF3C7'', text: ''#92400E'', border: ''#FDE047'' },\n    ''Negotiation'': { bg: ''#FED7AA'', text: ''#9A3412'', border: ''#FB923C'' },\n    ''Closed Won'': { bg: ''#D1FAE5'', text: ''#065F46'', border: ''#6EE7B7'' },\n    ''Closed Lost'': { bg: ''#FEE2E2'', text: ''#991B1B'', border: ''#FCA5A5'' }\n  };\n\n  useEffect(() => {\n    loadDeals();\n  }, [sortBy, sortDirection, maxRows, dateFrom, dateTo]);\n\n  const loadDeals = async () => {\n    setLoading(true);\n    setError(null);\n    \n    try {\n      // Build filter based on date range\n      let filters = [];\n      if (dateFrom) {\n        filters.push(`CloseDate >= ''$' + '{dateFrom}''`);\n      }\n      if (dateTo) {\n        filters.push(`CloseDate <= ''$' + '{dateTo}''`);\n      }\n      const extraFilter = filters.length > 0 ? filters.join('' AND '') : '''';\n      \n      const result = await utilities.rv.RunView({\n        EntityName: ''Deals'',\n        OrderBy: `$' + '{sortBy} $' + '{sortDirection}`,\n        MaxRows: maxRows,\n        ExtraFilter: extraFilter\n      });\n\n      if (result.Success) {\n        setDeals(result.Results || []);\n      } else {\n        setError(result.ErrorMessage || ''Failed to load deals'');\n      }\n    } catch (err) {\n      setError(err.message);\n    } finally {\n      setLoading(false);\n    }\n  };\n\n  const formatCurrency = (amount) => {\n    return new Intl.NumberFormat(''en-US'', {\n      style: ''currency'',\n      currency: ''USD''\n    }).format(amount || 0);\n  };\n\n  const formatDate = (dateStr) => {\n    if (!dateStr) return ''-'';\n    const date = new Date(dateStr);\n    return date.toLocaleDateString(''en-US'', { \n      month: ''short'', \n      day: ''numeric'', \n      year: ''numeric'' \n    });\n  };\n\n  const getDaysToClose = (closeDate) => {\n    if (!closeDate) return null;\n    const close = new Date(closeDate);\n    const today = new Date();\n    const days = Math.ceil((close - today) / (1000 * 60 * 60 * 24));\n    return days;\n  };\n\n  // Render stage badge\n  const renderStageBadge = (stage) => {\n    const stageStyle = stageColors[stage] || stageColors[''Prospecting''];\n    return (\n      <span style={{\n        padding: ''4px 12px'',\n        borderRadius: ''12px'',\n        fontSize: ''12px'',\n        fontWeight: ''600'',\n        backgroundColor: stageStyle.bg,\n        color: stageStyle.text,\n        border: `1px solid $' + '{stageStyle.border}`\n      }}>\n        {stage}\n      </span>\n    );\n  };\n\n  // Render probability bar\n  const renderProbabilityBar = (probability) => (\n    <div style={{ position: ''relative'' }}>\n      <div style={{\n        width: ''100px'',\n        height: ''8px'',\n        backgroundColor: ''#E5E7EB'',\n        borderRadius: ''4px'',\n        margin: ''0 auto''\n      }}>\n        <div style={{\n          width: `$' + '{probability}%`,\n          height: ''100%'',\n          backgroundColor: probability > 75 ? ''#10B981'' : probability > 50 ? ''#F59E0B'' : ''#6B7280'',\n          borderRadius: ''4px'',\n          transition: ''width 0.3s''\n        }} />\n      </div>\n      <div style={{ fontSize: ''12px'', marginTop: ''2px'', color: ''#6B7280'', textAlign: ''center'' }}>\n        {probability}%\n      </div>\n    </div>\n  );\n\n  // Render days to close badge\n  const renderDaysToClose = (closeDate) => {\n    const daysToClose = getDaysToClose(closeDate);\n    if (daysToClose === null) return null;\n\n    return (\n      <span style={{\n        padding: ''4px 8px'',\n        borderRadius: ''6px'',\n        fontSize: ''12px'',\n        fontWeight: ''600'',\n        backgroundColor: daysToClose < 0 ? ''#FEE2E2'' : daysToClose < 7 ? ''#FEF3C7'' : daysToClose < 30 ? ''#DBEAFE'' : ''#F3F4F6'',\n        color: daysToClose < 0 ? ''#991B1B'' : daysToClose < 7 ? ''#92400E'' : daysToClose < 30 ? ''#1E40AF'' : ''#6B7280''\n      }}>\n        {daysToClose < 0 ? `$' + '{Math.abs(daysToClose)} overdue` : `$' + '{daysToClose} days`}\n      </span>\n    );\n  };\n\n  // Define columns for DataGrid\n  const gridColumns = [\n    {\n      field: ''DealName'',\n      header: ''Deal Name'',\n      sortable: true,\n      width: ''200px''\n    },\n    {\n      field: ''Stage'',\n      header: ''Stage'',\n      sortable: false,\n      width: ''130px'',\n      render: (value) => renderStageBadge(value)\n    },\n    {\n      field: ''Amount'',\n      header: ''Amount'',\n      sortable: true,\n      width: ''120px'',\n      render: (value) => (\n        <span style={{ fontWeight: ''600'', color: ''#059669'' }}>\n          {formatCurrency(value)}\n        </span>\n      )\n    },\n    {\n      field: ''Probability'',\n      header: ''Probability'',\n      sortable: true,\n      width: ''120px'',\n      render: (value) => renderProbabilityBar(value)\n    },\n    {\n      field: ''CloseDate'',\n      header: ''Close Date'',\n      sortable: true,\n      width: ''100px'',\n      render: (value) 