  @Specification_51d6e5e1 = N'{"name":"StageDetailsPanel","namespace":"CRM/Analytics","type":"Other","location":"embedded","code":"// StageDetailsPanel Sub-component\nfunction StageDetailsPanel ({ stage, deals, isOpen, onClose, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  const [sortBy, setSortBy] = useState(''value'');\n  const [sortOrder, setSortOrder] = useState(''desc'');\n  const [filterText, setFilterText] = useState('''');\n\n  // Load SingleRecordView from component registry\n  const SingleRecordView = components?.SingleRecordView;\n\n  const sortedAndFilteredDeals = useMemo(() => {\n    if (!deals) return [];\n    \n    let filtered = deals;\n    if (filterText) {\n      filtered = deals.filter(deal => \n        deal.name?.toLowerCase().includes(filterText.toLowerCase()) ||\n        deal.accountName?.toLowerCase().includes(filterText.toLowerCase())\n      );\n    }\n\n    return [...filtered].sort((a, b) => {\n      const aVal = a[sortBy];\n      const bVal = b[sortBy];\n      const modifier = sortOrder === ''asc'' ? 1 : -1;\n      \n      if (typeof aVal === ''string'') {\n        return aVal.localeCompare(bVal) * modifier;\n      }\n      return (aVal - bVal) * modifier;\n    });\n  }, [deals, sortBy, sortOrder, filterText]);\n\n  const handleSort = (field) => {\n    if (sortBy === field) {\n      setSortOrder(sortOrder === ''asc'' ? ''desc'' : ''asc'');\n    } else {\n      setSortBy(field);\n      setSortOrder(''desc'');\n    }\n  };\n\n  const handleOpenDeal = (dealId) => {\n    if (callbacks?.OpenEntityRecord) {\n      callbacks.OpenEntityRecord(''Deals'', dealId);\n    }\n  };\n\n  return (\n    <div className={`stage-details-panel $' + '{isOpen ? ''open'' : ''''}`} style={{\n      position: ''fixed'',\n      top: 0,\n      right: isOpen ? 0 : ''-400px'',\n      width: ''400px'',\n      height: ''100%'',\n      backgroundColor: ''#fff'',\n      boxShadow: ''-2px 0 10px rgba(0,0,0,0.1)'',\n      transition: ''right 0.3s ease'',\n      zIndex: 1000,\n      display: ''flex'',\n      flexDirection: ''column'',\n      overflow: ''hidden''\n    }}>\n      <div style={{\n        padding: ''20px'',\n        borderBottom: ''1px solid #e0e0e0'',\n        backgroundColor: ''#f5f5f5''\n      }}>\n        <div style={{ display: ''flex'', justifyContent: ''space-between'', alignItems: ''center'' }}>\n          <h3 style={{ margin: 0 }}>{stage?.name || ''Stage Details''}</h3>\n          <button \n            onClick={onClose}\n            style={{\n              background: ''none'',\n              border: ''none'',\n              fontSize: ''24px'',\n              cursor: ''pointer'',\n              padding: ''0'',\n              width: ''30px'',\n              height: ''30px''\n            }}\n          >Ã—</button>\n        </div>\n        {stage && (\n          <div style={{ marginTop: ''10px'', fontSize: ''14px'', color: ''#666'' }}>\n            <div>{stage.count} deals</div>\n            <div>Total Value: $' + '{(stage.value || 0).toLocaleString()}</div>\n            <div>Avg Days: {stage.avgDays || 0}</div>\n          </div>\n        )}\n      </div>\n\n      <div style={{ padding: ''15px'', borderBottom: ''1px solid #e0e0e0'' }}>\n        <input\n          type=\"text\"\n          placeholder=\"Filter deals...\"\n          value={filterText}\n          onChange={(e) => setFilterText(e.target.value)}\n          style={{\n            width: ''100%'',\n            padding: ''8px'',\n            border: ''1px solid #ddd'',\n            borderRadius: ''4px''\n          }}\n        />\n      </div>\n\n      <div style={{ \n        flex: 1, \n        overflowY: ''auto'',\n        padding: ''15px''\n      }}>\n        <div style={{ marginBottom: ''10px'' }}>\n          <button \n            onClick={() => handleSort(''name'')}\n            style={{\n              marginRight: ''10px'',\n              padding: ''5px 10px'',\n              border: ''1px solid #ddd'',\n              borderRadius: ''4px'',\n              background: sortBy === ''name'' ? ''#007bff'' : ''#fff'',\n              color: sortBy === ''name'' ? ''#fff'' : ''#333'',\n              cursor: ''pointer''\n            }}\n          >\n            Name {sortBy === ''name'' && (sortOrder === ''asc'' ? ''â†‘'' : ''â†“'')}\n          </button>\n          <button \n            onClick={() => handleSort(''value'')}\n            style={{\n              marginRight: ''10px'',\n              padding: ''5px 10px'',\n              border: ''1px solid #ddd'',\n              borderRadius: ''4px'',\n              background: sortBy === ''value'' ? ''#007bff'' : ''#fff'',\n              color: sortBy === ''value'' ? ''#fff'' : ''#333'',\n              cursor: ''pointer''\n            }}\n          >\n            Value {sortBy === ''value'' && (sortOrder === ''asc'' ? ''â†‘'' : ''â†“'')}\n          </button>\n          <button \n            onClick={() => handleSort(''daysInStage'')}\n            style={{\n              padding: ''5px 10px'',\n              border: ''1px solid #ddd'',\n              borderRadius: ''4px'',\n              background: sortBy === ''daysInStage'' ? ''#007bff'' : ''#fff'',\n              color: sortBy === ''daysInStage'' ? ''#fff'' : ''#333'',\n              cursor: ''pointer''\n            }}\n          >\n            Days {sortBy === ''daysInStage'' && (sortOrder === ''asc'' ? ''â†‘'' : ''â†“'')}\n          </button>\n        </div>\n\n        <div>\n          {sortedAndFilteredDeals.map((deal, index) => (\n            <div\n              key={deal.id || index}\n              style={{\n                padding: ''10px'',\n                marginBottom: ''10px'',\n                border: ''1px solid #e0e0e0'',\n                borderRadius: ''5px'',\n                backgroundColor: ''#fafafa''\n              }}\n            >\n              {/* Use SingleRecordView for consistent deal display */}\n              {SingleRecordView ? (\n                <div>\n                  <SingleRecordView\n                    entityName=\"Deals\"\n                    record={deal}\n                    fields={[\"DealName\", \"Amount\", \"Stage\", \"Account\"]}\n                    layout=\"inline\"\n                    showLabels={false}\n                    showEmptyFields={false}\n                    allowOpenRecord={true}\n                    utilities={utilities}\n                    styles={styles}\n                    components={components}\n                    callbacks={callbacks}\n                    savedUserSettings={savedUserSettings}\n                    onSaveUserSettings={onSaveUserSettings}\n                    onOpenRecord={(eventData) => {\n                      // Use our custom handler for deal opening\n                      handleOpenDeal(deal.id);\n                      eventData.cancel = true; // Prevent default behavior\n                    }}\n                  />\n                  <div style={{ marginTop: ''5px'', fontSize: ''12px'', color: ''#888'' }}>\n                    {deal.daysInStage} days in stage\n                  </div>\n                </div>\n              ) : (\n                // Fallback display if SingleRecordView is not available\n                <div>\n                  <div style={{ fontWeight: ''bold'', marginBottom: ''5px'' }}>\n                    {deal.name || deal.DealName}\n                  </div>\n                  <div style={{ fontSize: ''13px'', color: ''#666'', marginBottom: ''5px'' }}>\n                    {deal.accountName || deal.Account}\n                  </div>\n                  <div style={{ display: ''flex'', justifyContent: ''space-between'', alignItems: ''center'' }}>\n                    <div>\n                      <span style={{ fontWeight: ''bold'' }}>$' + '{(deal.value || deal.Amount || 0).toLocaleString()}</span>\n                      <span style={{ marginLeft: ''10px'', color: ''#888'' }}>{deal.daysInStage} days</span>\n                    </div>\n                    <button\n                      onClick={() => handleOpenDeal(deal.id)}\n                      style={{\n                        padding: ''4px 8px'',\n                        fontSize: ''12px'',\n                        border: ''1px solid #007bff'',\n                        borderRadius: ''3px'',\n                        background: ''#fff'',\n                        color: ''#007bff'',\n                        cursor: ''pointer''\n                      }}\n                      title=\"Open in Explorer\"\n                    >\n                      â†—\n                    </button>\n                  </div>\n                </div>\n              )}\n            </div>\n          ))}\n        </div>\n      </div>\n    </div>\n  );\n}","functionalRequirements":"## Stage Details Panel\n\n### Purpose\nSlide-out panel displaying deals for a selected pipeline stage with filtering and sorting, using SingleRecordView for consistent deal display.\n\n### Features\n- Slide in/out animation from right\n- Stage header with name and metrics using SingleRecordView\n- Toggle between card and list views with SingleRecordView for deal info\n- Sort deals by amount, date, or probability\n- Filter deals by name\n- Display deal cards or list items using standardized formatting\n- Close button and animation\n\n### Interactions\n- Click to open deal record\n- Sort and filter controls\n- View mode toggle\n- Smooth transitions","dataRequirements":{"mode":"views","description":"Displays deals for a selected pipeline stage","entities":[{"name":"Deals","description":"Deal entities filtered by stage","displayFields":["ID","DealName","Amount","CloseDate","Probability","Stage","Account"],"filterFields":["Stage","DealName"],"sortFields":["Amount","CloseDate","Probability"],"fieldMetadata":[],"permissionLevelNeeded":["read"],"usageContext":"Displays deals for selected stage in cards or list view"}],"queries":[]},"technicalDesign":"## Implementation\n\n### Props\n- selectedStage: Stage data object with deals\n- isPanelOpen: Boolean for visibility\n- displayMode: ''cards'' or ''list''\n- onClose: Callback to close panel\n- onDisplayModeChange: Callback for view mode\n- onOpenDeal: Callback to open deal record\n\n### State\n- sortBy: Current sort field\n- filterText: Search filter\n\n### Layout\n- Fixed position overlay\n- Transform-based animation\n- Scrollable content area","properties":[{"name":"selectedStage","type":"{name: string, deals: Array<{ID: string, DealName: string, Amount: number, CloseDate: string, Probability: number}>}","required":false,"description":"Selected stage data with deals array"},{"name":"isPanelOpen","type":"boolean","required":true,"description":"Panel visibility state"},{"name":"displayMode","type":"''cards'' | ''list''","required":true,"description":"View mode for deals display"},{"name":"onClose","type":"() => void","required":true,"description":"Callback to close panel"},{"name":"onDisplayModeChange","type":"(mode: ''cards'' | ''list'') => void","required":false,"description":"Callback when view mode changes"},{"name":"onOpenDeal","type":"(dealId: string) => void","required":false,"description":"Callback to open deal record"}],"events":[],"dependencies":[{"name":"SingleRecordView","location":"registry","namespace":"Generic/UI/Form","version":"^1.0.0"}],"libraries":[]}'
SET
  @FunctionalRequirements_51d6e5e1 = N'## Stage Details Panel

### Purpose
Slide-out panel displaying deals for a selected pipeline stage with filtering and sorting, using SingleRecordView for consistent deal display.

### Features
- Slide in/out animation from right
- Stage header with name and metrics using SingleRecordView
- Toggle between card and list views with SingleRecordView for deal info
- Sort deals by amount, date, or probability
- Filter deals by name
- Display deal cards or list items using standardized formatting
- Close button and animation

### Interactions
- Click to open deal record
- Sort and filter controls
- View mode toggle
- Smooth transitions'
