  @Specification_456b721e = N'{"name":"DealList","namespace":"CRM/Analytics","type":"Table","location":"embedded","code":"// DealList Sub-component\nfunction DealList ({ deals, sortConfig, onSort, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  // Load OpenRecordButton component\n  const OpenRecordButton = components[''OpenRecordButton''];\n  const handleSort = (field) => {\n    const newOrder = sortConfig?.field === field && sortConfig?.order === ''asc'' ? ''desc'' : ''asc'';\n    if (onSort) {\n      onSort({ field, order: newOrder });\n    }\n  };\n\n  const getSortIcon = (field) => {\n    if (sortConfig?.field !== field) return ''↕'';\n    return sortConfig.order === ''asc'' ? ''↑'' : ''↓'';\n  };\n\n\n  return (\n    <div style={{ width: ''100%'', overflowX: ''auto'' }}>\n      <table style={{ \n        width: ''100%'', \n        borderCollapse: ''collapse'',\n        backgroundColor: ''#fff''\n      }}>\n        <thead>\n          <tr style={{ backgroundColor: ''#f8f9fa'', borderBottom: ''2px solid #dee2e6'' }}>\n            <th \n              onClick={() => handleSort(''name'')}\n              style={{\n                padding: ''12px'',\n                textAlign: ''left'',\n                cursor: ''pointer'',\n                userSelect: ''none'',\n                whiteSpace: ''nowrap''\n              }}\n            >\n              Deal Name {getSortIcon(''name'')}\n            </th>\n            <th \n              onClick={() => handleSort(''accountName'')}\n              style={{\n                padding: ''12px'',\n                textAlign: ''left'',\n                cursor: ''pointer'',\n                userSelect: ''none'',\n                whiteSpace: ''nowrap''\n              }}\n            >\n              Account {getSortIcon(''accountName'')}\n            </th>\n            <th \n              onClick={() => handleSort(''stage'')}\n              style={{\n                padding: ''12px'',\n          