  @Specification_1acc08ff = N'{"name":"KPIGauges","namespace":"CRM/Finance","type":"Chart","location":"embedded","code":"function KPIGauges ({ kpis, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  const gaugeRefs = useRef([]);\n  const chartInstances = useRef([]);\n\n  useEffect(() => {\n    if (!kpis || kpis.length === 0 || !window.ApexCharts) return;\n\n    // Clear existing charts\n    chartInstances.current.forEach(chart => {\n      if (chart) chart.destroy();\n    });\n    chartInstances.current = [];\n\n    kpis.forEach((kpi, index) => {\n      if (!gaugeRefs.current[index]) return;\n\n      const percentage = (kpi.actual / kpi.target) * 100;\n      const color = percentage >= 100 ? ''#28a745'' : \n                    percentage >= 75 ? ''#ffc107'' : ''#dc3545'';\n\n      const options = {\n        chart: {\n          type: ''radialBar'',\n          height: 200,\n          sparkline: {\n            enabled: true\n          }\n        },\n        series: [Math.min(percentage, 150)],\n        plotOptions: {\n          radialBar: {\n            startAngle: -90,\n            endAngle: 90,\n            track: {\n              background: ''#e7e7e7'',\n              strokeWidth: ''97%'',\n              margin: 5\n            },\n            dataLabels: {\n              name: {\n                show: true,\n                fontSize: ''12px'',\n                fontWeight: 400,\n                offsetY: -10\n              },\n              value: {\n                show: true,\n                fontSize: ''20px'',\n                fontWeight: ''bold'',\n                offsetY: 5,\n                formatter: function() {\n                  return `$' + '{percentage.toFixed(0)}%`;\n                }\n              }\n            },\n            hollow: {\n              margin: 0,\n              size: ''60%''\n            }\n          }\n        },\n        colors: [color],\n        labels: [kpi.name]\n      };\n\n      const chart = new window.ApexCharts(gaugeRefs.current[index], options);\n      chart.render();\n      chartInstances.current.push(chart);\n    });\n\n    return () => {\n      chartInstances.current.forEach(chart => {\n        if (chart) chart.destroy();\n      });\n      chartInstances.current = [];\n    };\n  }, [kpis]);\n\n  return (\n    <div style={{\n      display: ''grid'',\n      gridTemplateColumns: ''repeat(auto-fit, minmax(200px, 1fr))'',\n      gap: ''20px'',\n      padding: ''20px'',\n      backgroundColor: ''#fff'',\n      borderRadius: ''8px''\n    }}>\n      {kpis && kpis.map((kpi, index) => (\n        <div key={kpi.id || index} style={{ textAlign: ''center'' }}>\n          <div \n            ref={el => gaugeRefs.current[index] = el}\n            style={{ height: ''200px'' }}\n          />\n          <div style={{ marginTop: ''10px'' }}>\n            <div style={{ fontSize: ''18px'', fontWeight: ''bold'' }}>\n              $' + '{(kpi.actual / 1000).toFixed(0)}K\n            </div>\n            <div style={{ fontSize: ''12px'', color: ''#666'' }}>\n              Target: $' + '{(kpi.target / 1000).toFixed(0)}K\n            </div>\n            {kpi.trend && (\n              <div style={{\n                fontSize: ''11px'',\n                color: kpi.trend > 0 ? ''#28a745'' : ''#dc3545'',\n                marginTop: ''5px''\n              }}>\n                {kpi.trend > 0 ? ''↑'' : ''↓''} {Math.abs(kpi.trend)}% vs last period\n              </div>\n            )}\n          </div>\n        </div>\n      ))}\n    </div>\n  );\n}","functionalRequirements":"## KPI Gauges\n\n### Purpose\nDisplay key financial metrics as gauge charts.\n\n### Features\n- Multiple gauge displays\n- Target vs actual\n- Color coding by performance\n- Percentage indicators","dataRequirements":{"mode":"views","queries":[]},"technicalDesign":"## Implementation\n\n### Props\n- kpis: Array of KPI data\n\n### Display\n- ApexCharts radial gauges\n- Dynamic colors","properties":[{"name":"kpis","type":"Array<{name: string, value: number, target: number, unit: string, color?: string}>","required":true,"description":"KPI metrics data"}],"events":[],"dependencies":[],"libraries":[]}'
