  @Specification_0db00dea = N'{"name":"KPIGauges","namespace":"CRM/Finance","type":"Chart","location":"embedded","code":"function KPIGauges ({ kpis, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  const gaugeRefs = useRef([]);\n  const chartInstances = useRef([]);\n\n  useEffect(() => {\n    if (!kpis || kpis.length === 0 || !window.ApexCharts) return;\n\n    // Clear existing charts\n    chartInstances.current.forEach(chart => {\n      if (chart) chart.destroy();\n    });\n    chartInstances.current = [];\n\n    kpis.forEach((kpi, index) => {\n      if (!gaugeRefs.current[index]) return;\n\n      const percentage = (kpi.actual / kpi.target) * 100;\n      const color = percentage >= 100 ? ''#28a745'' : \n                    percentage >= 75 ? ''#ffc107'' : ''#dc3545'';\n\n      const options = {\n        chart: {\n          type: ''radialBar'',\n          height: 200,\n          sparkline: {\n            enabled: true\n          }\n        },\n        series: [Math.min(percentage, 150)],\n        plotOptions: {\n          radialBar: {\n            startAngle: -90,\n            endAngle: 90,\n            track: {\n              background: ''#e7e7e7'',\n              strokeWidth: ''97%'',\n              margin: 5\n            },\n            dataLabels: {\n              name: {\n                show: true,\n                fontSize: ''12px'',\n                fontWeight: 400,\n                offsetY: -10\n              },\n              value: {\n                show: true,\n                fontSize: ''20px'',\n                fontWeight: ''bold'',\n                offsetY: 5,\n                formatter: function() {\n                  return `$' + '{percentage.toFixed(0)}%`;\n                }\n              }\n            },\n            hollow: {\n              margin: 0,\n              size: ''60%''\n            }\n          }\n        },\n        colors: [color],\n        labels: [kpi.name]\n      };\n\n      const chart 