  @Specification_cc51d597 = N'{"name":"DealList","location":"embedded","code":"function DealList({ deals, stageColors = {}, onDealClick, onOpenClick }) {\n  return (\n    <table style={{ width: ''100%'', borderCollapse: ''collapse'' }}>\n      <thead>\n        <tr style={{ borderBottom: ''2px solid #E5E7EB'' }}>\n          <th style={{ padding: ''8px'', textAlign: ''left'', fontSize: ''12px'', color: ''#6B7280'' }}>Deal</th>\n          <th style={{ padding: ''8px'', textAlign: ''left'', fontSize: ''12px'', color: ''#6B7280'' }}>Stage</th>\n          <th style={{ padding: ''8px'', textAlign: ''right'', fontSize: ''12px'', color: ''#6B7280'' }}>Amount</th>\n          <th style={{ padding: ''8px'', width: ''30px'' }}></th>\n        </tr>\n      </thead>\n      <tbody>\n        {deals.map((deal, index) => (\n          <tr\n            key={deal.ID || index}\n            style={{\n              borderBottom: ''1px solid #F3F4F6'',\n              cursor: ''pointer'',\n              transition: ''background-color 0.2s''\n            }}\n            onClick={() => onDealClick && onDealClick(deal)}\n            onMouseEnter={(e) => e.currentTarget.style.backgroundColor = ''#F9FAFB''}\n            onMouseLeave={(e) => e.currentTarget.style.backgroundColor = ''transparent''}\n          >\n            <td style={{ padding: ''12px 8px'' }}>\n              <div style={{ fontWeight: ''500'' }}>{deal.DealName}</div>\n              <div style={{ fontSize: ''12px'', color: ''#9CA3AF'' }}>{deal.AccountName}</div>\n            </td>\n            <td style={{ padding: ''12px 8px'' }}>\n              {deal.Stage && (\n                <span style={{\n                  padding: ''2px 6px'',\n                  backgroundColor: stageColors[deal.Stage] || ''#6B7280'',\n                  color: ''white'',\n                  borderRadius: ''3px'',\n                  fontSize: ''11px''\n                }}>\n                  {deal.Stage}\n                </span>\n              )}\n            </td>\n            <td style={{ padding: ''12px 8px'', textAlign: ''right'', fontWeight: ''500'' }}>\n              $' + '{((deal.Amount || 0) / 1000).toFixed(0)}K\n            </td>\n            <td style={{ padding: ''12px 8px'', textAlign: ''center'' }}>\n              {onOpenClick && (\n                <button\n                  onClick={(e) => {\n                    e.stopPropagation();\n                    onOpenClick(''Deals'', [{ FieldName: ''ID'', Value: deal.ID }]);\n                  }}\n                  style={{\n                    background: ''none'',\n                    border: ''none'',\n                    color: ''#6B7280'',\n                    cursor: ''pointer'',\n                    padding: ''2px'',\n                    fontSize: ''14px''\n                  }}\n                  title=\"Open in Explorer\"\n                >\n                  <i className=\"fa-solid fa-up-right-from-square\"></i>\n                </button>\n              )}\n            </td>\n          </tr>\n        ))}\n      </tbody>\n    </table>\n  );\n}","description":"Displays deals in a sortable table format with open action","category":"Deals","type":"list","dataRequirements":{"mode":"views","entities":[{"name":"Deals","displayFields":["ID","Dealname","Accountname","Stage","Amount","CloseDate","Probability","Owner","CreatedAt"],"filterFields":["CloseDate","Stage","Owner"],"sortFields":["CloseDate","Amount","Probability"],"permissions":["read"]}]},"properties":[{"name":"deals","Type":"array","description":"Array of deal objects to display","required":true},{"name":"stageColors","Type":"object","description":"Optional object mapping stage names to colors","required":true},{"name":"onDealClick","Type":"function","description":"Callback when a deal row is clicked","required":true},{"name":"onOpenClick","Type":"function","description":"Callback when the open icon is clicked, receives entityName and primaryKey array","required":true}],"events":[{"name":"onDealSelect","description":"Fired when a deal row is clicked","parameters":[{"name":"deal","type":"object","description":"The deal object that was clicked"}]},{"name":"onDealOpen","description":"Fired when the open icon is clicked","parameters":[{"name":"entityName","type":"string","description":"Name of the entity (Deals)"},{"name":"primaryKey","type":"array","description":"Array of key-value pairs for composite primary key"}]}],"usageExample":"```javascript\n<DealList\n  deals={filteredDeals}\n  stageColors={stageColors}\n  onDealClick={(deal) => console.log(''Deal clicked:'', deal)}\n  onOpenClick={(entityName, primaryKey) => callbacks.OpenEntityRecord(entityName, primaryKey)}\n/>\n```","functionalRequirements":"- Display deals in a clean table format\n- Show deal name, account, stage, and amount\n- Include open icon button for external navigation\n- Show hover effects on rows\n- Color-code stage badges\n- Support row click for selection","technicalDesign":"Renders deals in table format with hover states. Formats currency values. Includes Font Awesome icon for opening deals externally. Stage badges use dynamic colors from stageColors prop."}'
