  @Specification_56c21e15 = N'{"name":"AccountsByIndustryChart","title":"Accounts by Industry Chart","description":"Interactive pie chart component that visualizes account distribution by industry with click-to-filter functionality","type":"component","location":"embedded","namespace":"CRM/Analytics","functionalRequirements":"## Pie Chart Component\n\n- Display interactive pie chart using Chart.js\n- Show industry names with counts in legend\n- Calculate and display percentages in tooltips\n- Support click-to-select functionality\n- Animate on hover with visual feedback\n- Configurable color schemes\n- Handle ''Others'' grouping for small segments","technicalDesign":"## Technical Design\n\n### Props\n- industryData: Grouped and calculated industry data\n- selectedIndustry: Currently selected slice\n- onSliceClick: Callback for slice selection\n- colorScheme: Color palette selection\n- maxIndustries: Maximum slices to show\n\n### Implementation\n- Uses Chart.js for rendering\n- Ref-based chart instance management\n- Cleanup on unmount to prevent memory leaks","dataRequirements":{"mode":"views","description":"Fetches account data using RunView for industry distribution analysis","entities":[{"name":"Accounts","description":"CRM account data grouped by industry","displayFields":["ID","AccountName","Industry","Revenue","Status","IsActive"],"filterFields":["IsActive","Industry"],"sortFields":["AccountName","Revenue","Industry"],"fieldMetadata":[],"usageContext":"Groups accounts by industry for pie chart visualization and filtered list display"}],"queries":[]},"properties":[{"name":"industryData","type":"object","required":true,"description":"Object containing labels, data, percentages, and total count"},{"name":"selectedIndustry","type":"string","required":true,"description":"Currently selected industry slice"},{"name":"onSliceClick","type":"function","required":true,"description":"Callback function when a slice is clicked"},{"name":"colorScheme","type":"string","required":true,"description":"Color palette: default, vibrant, or pastel","defaultValue":"default"}],"libraries":[{"name":"Chart.js","version":"4.4.0","globalVariable":"Chart"}],"exampleUsage":"<AccountsByIndustryChart\n  industryData={industryData}\n  selectedIndustry={selectedIndustry}\n  onSliceClick={handleSliceClick}\n  colorScheme=\"default\"\n/>","code":"function AccountsByIndustryChart ({ accounts, onSliceClick, colorScheme = ''default'', utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  // Load SimpleChart from the components registry\n  const SimpleChart = components[''SimpleChart''];\n\n  const handleChartClick = React.useCallback((event) => {\n    // Extract the industry from the clicked data point\n    if (event && event.label) {\n      onSliceClick(event.label);\n    }\n  }, [onSliceClick]);\n\n  // Color scheme mapping\n  const colorSchemes = {\n    default: [\n      ''#3B82F6'', ''#10B981'', ''#F59E0B'', ''#EF4444'', ''#8B5CF6'',\n      ''#EC4899'', ''#14B8A6'', ''#F97316'', ''#6366F1'', ''#84CC16''\n    ],\n    vibrant: [\n      ''#FF6B6B'', ''#4ECDC4'', ''#45B7D1'', ''#96CEB4'', ''#FFEAA7'',\n      ''#DDA0DD'', ''#98D8C8'', ''#FFD93D'', ''#6BCB77'', ''#FF6B9D''\n    ],\n    pastel: [\n      ''#FFE5E5'', ''#E5F3FF'', ''#E5FFE5'', ''#FFF5E5'', ''#F5E5FF'',\n      ''#FFE5F5'', ''#E5FFF5'', ''#FFF0E5'', ''#F0E5FF'', ''#E5F0FF''\n    ]\n  };\n\n  // Memoize the colors to prevent re-creation\n  const colors = React.useMemo(() => colorSchemes[colorScheme], [colorScheme]);\n\n  // Memoize the chart to prevent unnecessary re-renders\n  const chartContent = React.useMemo(() => {\n    if (!SimpleChart) {\n      return (\n        <div style={{ padding: ''20px'', textAlign: ''center'', color: ''#6B7280'' }}>\n          SimpleChart component not available\n        </div>\n      );\n    }\n\n    return (\n      <SimpleChart\n        key=\"industry-pie-chart\"  // Add stable key\n        entityName=\"Accounts\"\n        data={accounts}\n        groupBy=\"Industry\"\n        aggregateMethod=\"count\"\n        chartType=\"pie\"\n        title=\"Accounts by Industry\"\n        onDataPointClick={handleChartClick}\n        legendPosition=\"right\"  // Position legend on the right like original\n        legendFontSize={12}      // Keep smaller font size for legend\n        utilities={utilities}\n        styles={styles}\n        components={components}\n        callbacks={callbacks}\n        savedUserSettings={savedUserSettings}\n        onSaveUserSettings={onSaveUserSettings}\n        colors={colors}\n      />\n    );\n  }, [SimpleChart, accounts, handleChartClick, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings, colors]);\n\n  return (\n    <div style={{ height: ''100%'', position: ''relative'' }}>\n      {chartContent}\n    </div>\n  );\n}","dependencies":[{"name":"SimpleChart","location":"registry","namespace":"Generic/UI/Chart","version":"^1.0.0"}]}'
