  @Specification_bbc15112 = N'{"name":"AccountsByIndustryList","title":"Accounts by Industry List","description":"Sortable, paginated table component displaying filtered account records with row selection capability","type":"component","location":"embedded","namespace":"CRM/Analytics","functionalRequirements":"## Account List Component\n\n- Display accounts in sortable table format\n- Support column sorting with visual indicators\n- Implement client-side pagination\n- Enable row hover effects\n- Handle row click for account selection\n- Format currency values appropriately\n- Show status badges with color coding\n- Display clear filter button","technicalDesign":"## Technical Design\n\n### Props\n- accounts: Filtered account array\n- sortConfig: Current sort settings\n- onSort: Sort handler callback\n- onAccountClick: Row selection callback\n- currentPage: Current pagination page\n- pageSize: Items per page\n- onPageChange: Pagination callback\n\n### Features\n- Client-side sorting logic\n- Pagination calculation\n- Hover state management\n- Status badge styling","dataRequirements":{"mode":"views","description":"Fetches account data using RunView for industry distribution analysis","entities":[{"name":"Accounts","description":"CRM account data grouped by industry","displayFields":["ID","AccountName","Industry","Revenue","Status","IsActive"],"filterFields":["IsActive","Industry"],"sortFields":["AccountName","Revenue","Industry"],"fieldMetadata":[],"usageContext":"Groups accounts by industry for pie chart visualization and filtered list display"}],"queries":[]},"properties":[{"name":"accounts","type":"array","required":true,"description":"Array of account objects to display"},{"name":"selectedIndustry","type":"string","required":true,"description":"Currently selected industry for display"},{"name":"sortConfig","type":"object","required":true,"description":"Object with field and direction properties"},{"name":"onSort","type":"function","required":true,"description":"Callback when column header is clicked for sorting"},{"name":"onAccountClick","type":"function","required":true,"description":"Callback when account row is clicked"},{"name":"currentPage","type":"number","required":true,"description":"Current page number for pagination"},{"name":"pageSize","type":"number","required":true,"description":"Number of items per page"},{"name":"onPageChange","type":"function","required":true,"description":"Callback when page is changed"},{"name":"onClearFilter","type":"function","required":true,"description":"Callback to clear the industry filter"}],"exampleUsage":"<AccountsByIndustryList\n  accounts={filteredAccounts}\n  selectedIndustry={selectedIndustry}\n  sortConfig={sortConfig}\n  onSort={handleSort}\n  onAccountClick={handleAccountClick}\n  currentPage={currentPage}\n  pageSize={pageSize}\n  onPageChange={setCurrentPage}\n  onClearFilter={handleClearFilter}\n/>","code":"function AccountsByIndustryList({\n  accounts,\n  selectedIndustry,\n  onAccountClick,\n  pageSize,\n  onClearFilter,\n  utilities,\n  styles,\n  components,\n  callbacks,\n  savedUserSettings,\n  onSaveUserSettings\n}) {\n  // Load DataGrid component from registry\n  const DataGrid = components[''DataGrid''];\n\n  // Define columns to display in the DataGrid with custom formatting\n  const columns = [\n    { field: ''Name'', header: ''Account Name'' },\n    {\n      field: ''AnnualRevenue'',\n      header: ''Revenue'',\n      render: (value) => {\n        if (!value) return ''-'';\n\n        // Use Intl.NumberFormat for consistent formatting with compact notation\n        const formatted = new Intl.NumberFormat(''en-US'', {\n          style: ''currency'',\n          currency: ''USD'',\n          notation: ''compact'',\n          maximumFractionDigits: 1\n        }).format(value);\n\n        return formatted;\n      }\n    },\n    {\n      field: ''AccountStatus'',\n      header: ''Status'',\n      render: (value) => {\n        return (\n          <span style={{\n            padding: ''2px 8px'',\n            fontSize: ''12px'',\n            fontWeight: ''500'',\n            borderRadius: ''4px'',\n            backgroundColor: value === ''Active'' ? ''#D1FAE5'' : ''#FEE2E2'',\n            color: value === ''Active'' ? ''#065F46'' : ''#991B1B''\n          }}>\n            {value || ''Unknown''}\n          </span>\n        );\n      }\n    },\n    {\n      field: ''AccountType'',\n      header: ''Type'',\n      render: (value) => {\n        const colorMap = {\n          ''Customer'': { bg: ''#DBEAFE'', color: ''#1E40AF'' },\n          ''Prospect'': { bg: ''#FEF3C7'', color: ''#92400E'' },\n          ''Partner'': { bg: ''#E9D5FF'', color: ''#6B21A8'' },\n          ''Vendor'': { bg: ''#FED7AA'', color: ''#9A3412'' }\n        };\n        const defaultColors = { bg: ''#F3F4F6'', color: ''#374151'' };\n        const colors = value ? (colorMap[value] || defaultColors) : defaultColors;\n\n        return (\n          <span style={{\n            padding: ''2px 8px'',\n            fontSize: ''12px'',\n            fontWeight: ''500'',\n            borderRadius: ''4px'',\n            backgroundColor: colors.bg,\n            color: colors.color\n          }}>\n            {value || ''Unknown''}\n          </span>\n        );\n      }\n    }\n  ];\n\n  // Handle row click - DataGrid passes the clicked record directly\n  const handleRowClick = React.useCallback((record) => {\n    if (record) {\n      onAccountClick?.(record);\n    }\n  }, [onAccountClick]);\n\n  // Always render the container but control visibility to prevent mount/unmount\n  return (\n    <div style={{\n      marginTop: selectedIndustry ? ''32px'' : ''0'',\n      padding: selectedIndustry ? ''20px'' : ''0'',\n      backgroundColor: selectedIndustry ? ''#F9FAFB'' : ''transparent'',\n      borderRadius: ''8px'',\n      border: selectedIndustry ? ''1px solid #E5E7EB'' : ''none'',\n      display: selectedIndustry ? ''block'' : ''none'',\n      transition: ''none''\n    }}>\n      {selectedIndustry && (\n        <>\n          <div style={{\n            fontSize: ''18px'',\n            fontWeight: ''600'',\n            color: ''#111827'',\n            marginBottom: ''16px'',\n            display: ''flex'',\n            justifyContent: ''space-between'',\n            alignItems: ''center''\n          }}>\n            <span>Selected: {selectedIndustry} ({accounts.length} accounts)</span>\n            {onClearFilter && (\n              <button\n                onClick={onClearFilter}\n                style={{\n                  padding: ''6px 12px'',\n                  fontSize: ''14px'',\n                  color: ''#6B7280'',\n                  backgroundColor: ''#fff'',\n                  border: ''1px solid #D1D5DB'',\n                  borderRadius: ''6px'',\n                  cursor: ''pointer''\n                }}\n              >\n                Clear Filter\n              </button>\n            )}\n          </div>\n\n          {/* Account Table using DataGrid */}\n          <div style={{ backgroundColor: ''#fff'', borderRadius: ''6px'', overflow: ''hidden'' }}>\n            {DataGrid ? (\n              <DataGrid\n                key=\"accounts-data-grid\"  // Add stable key to prevent remounting\n                entityName=\"Accounts\"\n                data={accounts}\n                columns={columns}\n                pageSize={pageSize || 10}\n                filtering={false}\n                selectionMode=\"none\"\n                onRowClick={handleRowClick}\n                utilities={utilities}\n                styles={styles}\n                components={components}\n                callbacks={callbacks}\n                savedUserSettings={savedUserSettings}\n                onSaveUserSettings={onSaveUserSettings}\n              />\n            ) : (\n              <div style={{\n                padding: ''20px'',\n                textAlign: ''center'',\n                color: ''#6B7280''\n              }}>\n                DataGrid component not available\n              </div>\n            )}\n          </div>\n        </>\n      )}\n    </div>\n  );\n}","dependencies":[{"name":"DataGrid","location":"registry","namespace":"Generic/UI/Table","version":"^1.0.0"}]}'
