       style={{\n              width: ''32px'',\n              height: ''32px'',\n              display: ''flex'',\n              alignItems: ''center'',\n              justifyContent: ''center'',\n              backgroundColor: ''#F3F4F6'',\n              border: ''none'',\n              borderRadius: ''6px'',\n              cursor: ''pointer'',\n              fontSize: ''18px'',\n              color: ''#6B7280''\n            }}\n          >\n            Ã—\n          </button>\n        </div>\n        \n        {/* Panel Content */}\n        <div style={{ padding: ''20px'' }}>\n          {/* Account Name and Open Button */}\n          <div style={{ marginBottom: ''24px'' }}>\n            <div style={{\n              display: ''flex'',\n              justifyContent: ''space-between'',\n              alignItems: ''center'',\n              marginBottom: ''12px''\n            }}>\n              <h4 style={{\n                fontSize: ''20px'',\n                fontWeight: ''700'',\n                color: ''#111827'',\n                margin: ''0''\n              }}>\n                {account.Name}\n              </h4>\n              {OpenRecordButton ? (\n                <OpenRecordButton\n                  entityName=\"Accounts\"\n                  record={account}\n                  buttonText=\"Open Record â†—\"\n                  utilities={utilities}\n                  styles={styles}\n                  components={components}\n                  callbacks={callbacks}\n                  savedUserSettings={savedUserSettings}\n                  onSaveUserSettings={onSaveUserSettings}\n                  buttonStyle={{\n                    padding: ''8px 16px'',\n                    fontSize: ''14px'',\n                    fontWeight: ''500'',\n                    color: ''#fff'',\n                    backgroundColor: ''#3B82F6'',\n                    border: ''none'',\n                    borderRadius: ''6px'',\n                    cursor: ''pointer'',\n                    display: ''flex'',\n                    alignItems: ''center'',\n                    gap: ''4px''\n                  }}\n                />\n              ) : (\n                <button\n                  onClick={onOpenRecord}\n                  style={{\n                    padding: ''8px 16px'',\n                    fontSize: ''14px'',\n                    fontWeight: ''500'',\n                    color: ''#fff'',\n                    backgroundColor: ''#3B82F6'',\n                    border: ''none'',\n                    borderRadius: ''6px'',\n                    cursor: ''pointer'',\n                    display: ''flex'',\n                    alignItems: ''center'',\n                    gap: ''4px''\n                  }}\n                >\n                  Open Record â†—\n                </button>\n              )}\n            </div>\n            <div style={{ fontSize: ''14px'', color: ''#6B7280'' }}>\n              {account.Industry || ''No industry''}\n            </div>\n          </div>\n          \n          {/* Key Information - Using SingleRecordView */}\n          <div style={{ marginBottom: ''24px'' }}>\n            <h5 style={{\n              fontSize: ''12px'',\n              fontWeight: ''600'',\n              color: ''#6B7280'',\n              textTransform: ''uppercase'',\n              letterSpacing: ''0.05em'',\n              marginBottom: ''12px''\n            }}>\n              Key Information\n            </h5>\n\n            {SingleRecordView ? (\n              <SingleRecordView\n                entityName=\"Accounts\"\n                record={account}\n                fields={[\"Industry\", \"AnnualRevenue\", \"AccountStatus\", \"AccountType\"]}\n                layout=\"list\"\n                showLabels={true}\n                showEmptyFields={false}\n                allowOpenRecord={false}\n                utilities={utilities}\n                styles={styles}\n                components={components}\n                callbacks={callbacks}\n                savedUserSettings={savedUserSettings}\n                onSaveUserSettings={onSaveUserSettings}\n              />\n            ) : (\n              // Fallback display if SingleRecordView is not available\n              <div style={{ display: ''flex'', flexDirection: ''column'', gap: ''12px'' }}>\n                <div>\n                  <div style={{ fontSize: ''12px'', color: ''#6B7280'', marginBottom: ''2px'' }}>Annual Revenue</div>\n                  <div style={{ fontSize: ''16px'', fontWeight: ''600'', color: ''#111827'' }}>\n                    {formatCurrency(account.AnnualRevenue)}\n                  </div>\n                </div>\n\n                <div style={{ display: ''flex'', gap: ''16px'' }}>\n                  <div style={{ flex: 1 }}>\n                    <div style={{ fontSize: ''12px'', color: ''#6B7280'', marginBottom: ''2px'' }}>Status</div>\n                    <span style={{\n                      padding: ''2px 8px'',\n                      fontSize: ''12px'',\n                      fontWeight: ''500'',\n                      borderRadius: ''4px'',\n                      backgroundColor: account.AccountStatus === ''Active'' ? ''#D1FAE5'' : ''#FEE2E2'',\n                      color: account.AccountStatus === ''Active'' ? ''#065F46'' : ''#991B1B''\n                    }}>\n                      {account.AccountStatus}\n                    </span>\n                  </div>\n\n                  <div style={{ flex: 1 }}>\n                    <div style={{ fontSize: ''12px'', color: ''#6B7280'', marginBottom: ''2px'' }}>Type</div>\n                    <span style={{\n                      padding: ''2px 8px'',\n                      fontSize: ''12px'',\n                      fontWeight: ''500'',\n                      borderRadius: ''4px'',\n                      backgroundColor:\n                        account.AccountType === ''Customer'' ? ''#DBEAFE'' :\n                        account.AccountType === ''Prospect'' ? ''#FEF3C7'' :\n                        account.AccountType === ''Partner'' ? ''#E9D5FF'' :\n                        account.AccountType === ''Vendor'' ? ''#FED7AA'' :\n                        ''#F3F4F6'',\n                      color:\n                        account.AccountType === ''Customer'' ? ''#1E40AF'' :\n                        account.AccountType === ''Prospect'' ? ''#92400E'' :\n                        account.AccountType === ''Partner'' ? ''#6B21A8'' :\n                        account.AccountType === ''Vendor'' ? ''#9A3412'' :\n                        ''#374151''\n                    }}>\n                      {account.AccountType}\n                    </span>\n                  </div>\n                </div>\n              </div>\n            )}\n          </div>\n          \n          {/* Contact Information */}\n          <div style={{ marginBottom: ''24px'' }}>\n            <h5 style={{ \n              fontSize: ''12px'', \n              fontWeight: ''600'', \n              color: ''#6B7280'',\n              textTransform: ''uppercase'',\n              letterSpacing: ''0.05em'',\n              marginBottom: ''12px''\n            }}>\n              Contact Information\n            </h5>\n            \n            <div style={{ display: ''flex'', flexDirection: ''column'', gap: ''10px'' }}>\n              {account.Phone && (\n                <div style={{ display: ''flex'', alignItems: ''center'', gap: ''8px'' }}>\n                  <span style={{ fontSize: ''14px'' }}>ðŸ“ž</span>\n                  <span style={{ fontSize: ''14px'', color: ''#111827'' }}>{account.Phone}</span>\n                </div>\n              )}\n              \n              {account.Website && (\n                <div style={{ display: ''flex'', alignItems: ''center'', gap: ''8px'' }}>\n                  <span style={{ fontSize: ''14px'' }}>ðŸŒ</span>\n                  <a \n                    href={account.Website.startsWith(''http'') ? account.Website : `https://$' + '{account.Website}`}\n                    target=\"_blank\"\n                    rel=\"noopener noreferrer\"\n                    style={{ fontSize: ''14px'', color: ''#3B82F6'', textDecoration: ''none'' }}\n                  >\n                    {account.Website}\n                  </a>\n                </div>\n              )}\n            </div>\n          </div>\n          \n          {/* Address */}\n          {(account.BillingCity || account.BillingState || account.BillingCountry) && (\n            <div>\n              <h5 style={{ \n                fontSize: ''12px'', \n                fontWeight: ''600'', \n                color: ''#6B7280'',\n                textTransform: ''uppercase'',\n                letterSpacing: ''0.05em'',\n                marginBottom: ''12px''\n              }}>\n                Location\n              </h5>\n              \n              <div style={{ display: ''flex'', alignItems: ''center'', gap: ''8px'' }}>\n                <span style={{ fontSize: ''14px'' }}>ðŸ“</span>\n                <span style={{ fontSize: ''14px'', color: ''#111827'' }}>\n                  {[account.BillingCity, account.BillingState, account.BillingCountry]\n                    .filter(Boolean)\n                    .join('', '')}\n                </span>\n              </div>\n            </div>\n          )}\n        </div>\n      </div>\n    </>\n  );\n}","dependencies":[{"name":"OpenRecordButton","location":"registry","namespace":"Generic/Navigation","version":"^1.0.0"},{"name":"SingleRecordView","location":"registry","namespace":"Generic/UI/Form","version":"^1.0.0"}]}'
