  impact: ((wonAvg - lostAvg) / (lostAvg || 1)) * 100,\n        correlation: Math.abs(wonAvg - lostAvg) / (Math.max(wonAvg, lostAvg) || 1)\n      };\n    });\n    \n    return avgFactors;\n  };\n\n  const openDrillDown = (type, data) => {\n    setDrillDownType(type);\n    setDrillDownData(data);\n    setIsPanelOpen(true);\n  };\n\n  // Prepare data for export\n  const prepareExportData = () => {\n    const filteredDeals = getFilteredDeals();\n    \n    // Return simplified deal data with formatted amounts\n    return filteredDeals.map(deal => ({\n      DealName: deal.DealName || '''',\n      AccountName: deal.AccountName || '''',\n      Stage: deal.Stage || '''',\n      Status: deal.Status || '''',\n      Amount: deal.Amount ? `$$' + '{(deal.Amount / 1000).toFixed(1)}K` : ''$0'',\n      CloseDate: deal.CloseDate ? new Date(deal.CloseDate).toLocaleDateString() : '''',\n      LossReason: deal.LossReason || (deal.Stage === ''Closed Won'' ? ''Won'' : ''Unknown''),\n      Owner: deal.Owner || '''',\n      CreatedDate: deal.CreatedAt ? new Date(deal.CreatedAt || deal.__mj_CreatedAt).toLocaleDateString() : '''',\n      CycleTime: (() => {\n        if (deal.CloseDate && (deal.CreatedAt || deal.__mj_CreatedAt)) {\n          const created = new Date(deal.CreatedAt || deal.__mj_CreatedAt);\n          const closed = new Date(deal.CloseDate);\n          const days = Math.floor((closed - created) / (1000 * 60 * 60 * 24));\n          return `$' + '{days} days`;\n        }\n        return ''0 days'';\n      })()\n    }));\n  };\n\n  // Define export columns with correct key/label format\n  const getExportColumns = () => {\n    return [\n      { key: ''DealName'', label: ''Deal Name'' },\n      { key: ''AccountName'', label: ''Account'' },\n      { key: ''Stage'', label: ''Stage'' },\n      { key: ''Status'', label: ''Status'' },\n      { key: ''Amount'', label: ''Amount'' },\n      { key: ''CloseDate'', label: ''Close Date'' },\n      { key: ''LossReason'', label: ''Win/Loss Reason'' },\n      { key: ''Owner'', label: ''Owner'' },\n      { key: ''CreatedDate'', label: ''Created Date'' },\n      { key: ''CycleTime'', label: ''Cycle Time'' }\n    ];\n  };\n\n  // Enhanced Sub-component: Overview Cards with drill-down\n  const OverviewCards = () => {\n    const metrics = calculateWinLossMetrics();\n    \n    return (\n      <div style={{ display: ''grid'', gridTemplateColumns: ''repeat(auto-fit, minmax(200px, 1fr))'', gap: ''16px'', marginBottom: ''20px'' }}>\n        <div \n          style={{ \n            padding: ''16px'', \n            backgroundColor: ''white'', \n            borderRadius: ''8px'', \n            border: ''1px solid #E5E7EB'',\n            cursor: ''pointer'',\n            transition: ''all 0.2s'',\n            position: ''relative'',\n            overflow: ''hidden''\n          }}\n          onClick={() => openDrillDown(''winrate'', metrics)}\n          onMouseEnter={(e) => {\n            e.currentTarget.style.transform = ''translateY(-2px)'';\n            e.currentTarget.style.boxShadow = ''0 4px 12px rgba(0,0,0,0.1)'';\n          }}\n          onMouseLeave={(e) => {\n            e.currentTarget.style.transform = ''translateY(0)'';\n            e.currentTarget.style.boxShadow = ''none'';\n          }}\n        >\n          <div style={{\n            position: ''absolute'',\n            top: 0,\n            left: 0,\n            width: `$' + '{metrics.winRate}%`,\n            height: ''3px'',\n            backgroundColor: metrics.winRate > 50 ? ''#10B981'' : ''#EF4444'',\n            transition: ''width 0.5s''\n          }} />\n          <div style={{ fontSize: ''12px'', color: ''#6B7280'', marginBottom: ''4px'' }}>Win Rate</div>\n          <div style={{ fontSize: ''32px'', fontWeight: ''bold'', color: metrics.winRate > 50 ? ''#10B981'' : ''#EF4444'' }}>\n            {(metrics.winRate || 0).toFixed(1)}%\n          </div>\n          <div style={{ fontSize: ''11px'', color: ''#6B7280'' }}>\n            {metrics.wonCount} won 