SET
  @Specification_8f3521a7 = N'{"name":"AIModelList","title":"AI Model List","description":"Displays AI models in grid or list view with vendor information and sorting","type":"table","functionalRequirements":"Display models with vendor counts, support grid and list views, handle sorting and selection","technicalDesign":"## Technical Design\n\n### Component Structure\n- Display component for AI models in grid or list view\n- Receives model data and vendor associations via props\n- Handles selection and sorting through event callbacks\n\n### Props\n- models: Array of AI model objects\n- modelVendors: Array of vendor associations (grouped by model)\n- viewMode: ''grid'' | ''list''\n- selectedModelId: Currently selected model ID\n- sortBy: Field name for sorting\n- sortDirection: ''asc'' | ''desc''\n\n### Rendering\n- Grid mode: Compact card layout with key model info\n- List mode: Table view with sortable columns\n- Display vendor count badges for each model\n- Highlight selected model\n- Show sort indicators on column headers\n\n### Event Handling\n- onSelectModel: Emits modelId when user clicks a model\n- onSortChange: Emits sortBy and sortDirection when sort changes","dataRequirements":{"mode":"views","entities":[{"name":"AI Models","description":"Core AI model definitions and specifications","displayFields":["ID","Name","APIName","Description","AIModelTypeID","InputTokenLimit","PowerRank","SpeedRank","CostRank","IsActive","__mj_CreatedAt","__mj_UpdatedAt"],"filterFields":["AIModelTypeID","IsActive"],"sortFields":["Name","InputTokenLimit","CostRank","IsActive","__mj_CreatedAt"],"fieldMetadata":[{"name":"ID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":true,"description":"Unique identifier for the AI model"},{"name":"Name","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"System name of the AI model"},{"name":"APIName","sequence":4,"defaultInView":true,"type":"nvarchar","allowsNull":true,"isPrimaryKey":false,"description":"API identifier for the model"},{"name":"InputTokenLimit","sequence":5,"defaultInView":true,"type":"int","allowsNull":true,"isPrimaryKey":false,"description":"Maximum input token limit"},{"name":"CostRank","sequence":6,"defaultInView":true,"type":"int","allowsNull":true,"isPrimaryKey":false,"description":"Relative cost ranking (1-5)"}],"usageContext":"Main list of AI models"},{"name":"MJ: AI Model Vendors","description":"Vendor associations for AI models","displayFields":["ID","ModelID","VendorID","TypeID","Model","Vendor","Type","Status","Priority","APIName","MaxInputTokens","MaxOutputTokens","SupportsStreaming","SupportsEffortLevel"],"filterFields":["ModelID","Type","Status"],"sortFields":["Vendor","Priority","Type"],"fieldMetadata":[{"name":"ModelID","sequence":1,"defaultInView":false,"type":"uniqueidentifier","allowsNull":false,"isPrimaryKey":false,"description":"Reference to AI Model"},{"name":"Vendor","sequence":2,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Vendor name"},{"name":"Type","sequence":3,"defaultInView":true,"type":"nvarchar","allowsNull":false,"isPrimaryKey":false,"description":"Vendor type (Model Developer or Inference Provider)"}],"usageContext":"Vendor associations for models"},{"name":"AI Model Types","description":"Types/categories of AI models","displayFields":["ID","Name","Description"],"filterFields":[],"sortFields":["Name"],"usageContext":"Filter dropdown for model types"},{"name":"MJ: AI Prompt Runs","description":"Execution history for AI prompts","displayFields":["ID","PromptID","ModelID","StartTime","EndTime","TotalExecutionTime","InputTokens","OutputTokens","TotalTokens","Status","Error"],"filterFields":["ModelID","PromptID","Status"],"sortFields":["StartTime","TotalExecutionTime"],"usageContext":"Analytics and usage history"},{"name":"AI Prompts","description":"AI prompt definitions","displayFields":["ID","Name","Description","CategoryID"],"filterFields":[],"sortFields":["Name"],"usageContext":"Enrich prompt runs with names"}]},"properties":[{"name":"models","type":"array","required":true,"description":"Array of AI model objects to display"},{"name":"modelVendors","type":"array","required":true,"description":"Array of model vendor associations"},{"name":"viewMode","type":"string","required":true,"description":"Display mode - ''grid'' or ''list''"},{"name":"selectedModelId","type":"string","required":true,"description":"ID of the currently selected model"},{"name":"sortBy","type":"string","required":true,"description":"Field to sort by"},{"name":"sortDirection","type":"string","required":true,"description":"Sort direction - ''asc'' or ''desc''"}],"events":[{"name":"onSelectModel","description":"Fired when a model is selected","parameters":[{"name":"modelId","type":"string","description":"ID of the selected model"}]},{"name":"onSortChange","description":"Fired when sort criteria changes","parameters":[{"name":"sortBy","type":"string","description":"Field to sort by"},{"name":"sortDirection","type":"string","description":"Sort direction"}]}],"exampleUsage":"<AIModelList ... />","code":"function AIModelList({\n  models,\n  modelVendors,\n  viewMode,\n  selectedModelId,\n  onSelectModel,\n  sortBy,\n  sortDirection,\n  onSortChange,\n  utilities,\n  styles,\n  components,\n  callbacks,\n  savedUserSettings,\n  onSaveUserSettings\n}) {\n  // Load DataGrid component from registry\n  const DataGrid = components[''DataGrid''];\n\n  // Helper function to get border radius value\n  const getBorderRadius = (size) => {\n    return typeof styles.borders?.radius === ''object'' ? styles.borders.radius[size] : styles.borders?.radius || ''4px'';\n  };\n\n  // Get vendor info for a model\n  const getModelVendorInfo = useCallback((modelId) => {\n    const vendors = modelVendors.filter(v => v.ModelID === modelId);\n    const inferenceProviders = vendors.filter(v => v.Type === ''Inference Provider'');\n    const modelDeveloper = vendors.find(v => v.Type === ''Model Developer'');\n\n    return {\n      inferenceProviders,\n      modelDeveloper,\n      vendorCount: vendors.length\n    };\n  }, [modelVendors]);\n\n  // Render status badge\n  const renderStatusBadge = (status) => {\n    const statusColors = {\n      ''Active'': styles.colors.success || styles.colors.primary,\n      ''Inactive'': styles.colors.warning || styles.colors.secondary,\n      ''Deprecated'': styles.colors.error || styles.colors.secondary,\n      ''Preview'': styles.colors.info || styles.colors.primary\n    };\n\n    const color = statusColors[status] || styles.colors.textSecondary;\n\n    return (\n      <span style={{\n        display: ''inline-block'',\n        padding: `$' + '{styles.spacing.xs} $' + '{styles.spacing.sm}`,\n        backgroundColor: color + ''15'',\n        color: color,\n        borderRadius: getBorderRadius(''sm''),\n        fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n        fontWeight: styles.typography.fontWeight?.medium || ''500''\n      }}>\n        {status}\n      </span>\n    );\n  };\n\n  // Render cost rank\n  const renderCostRank = (rank) => {\n    if (rank == null) return ''-'';\n\n    const maxStars = 5;\n    const filledStars = Math.min(rank, maxStars);\n\n    return (\n      <span style={{\n        color: styles.colors.warning || styles.colors.secondary,\n        fontSize: styles.typography.fontSize.sm\n      }}>\n        {''$''.repeat(filledStars)}\n        <span style={{ opacity: 0.3 }}>\n          {''$''.repeat(maxStars - filledStars)}\n        </span>\n      </span>\n    );\n  };\n\n  // Format token limit\n  const formatTokens = (tokens) => {\n    if (!tokens) return ''-'';\n    if (tokens >= 1000000) return `$' + '{(tokens / 1000000).toFixed(1)}M`;\n    if (tokens >= 1000) return `$' + '{(tokens / 1000).toFixed(0)}K`;\n    return tokens.toString();\n  };\n\n  // Define columns for DataGrid\n  const gridColumns = [\n    {\n      field: ''Name'',\n      header: ''Model Name'',\n      sortable: true,\n      width: ''200px''\n    },\n    {\n      field: ''APIName'',\n      header: ''API Name'',\n      sortable: false,\n      width: ''150px'',\n      render: (value) => value ? (\n        <span style={{ fontFamily: ''monospace'', fontSize: styles.typography.fontSize.sm }}>\n          {value}\n        </span>\n      ) : ''-''\n    },\n    {\n      field: ''IsActive'',\n      header: ''Status'',\n      sortable: true,\n      width: ''100px'',\n      render: (value) => renderStatusBadge(value ? ''Active'' : ''Inactive'')\n    },\n    {\n      field: ''ModelDeveloper'',\n      header: ''Developer'',\n      sortable: false,\n      width: ''120px'',\n      render: (value, row) => {\n        const vendorInfo = getModelVendorInfo(row.ID);\n        return vendorInfo.modelDeveloper?.Vendor || ''-'';\n      }\n    },\n    {\n      field: ''InferenceProviders'',\n      header: ''Providers'',\n      sortable: false,\n      width: ''150px'',\n      render: (value, row) => {\n        const vendorInfo = getModelVendorInfo(row.ID);\n        if (vendorInfo.inferenceProviders.length === 0) {\n          return <span style={{ color: styles.colors.textSecondary }}>-</span>;\n        }\n\n        return (\n          <div style={{\n            display: ''flex'',\n            gap: styles.spacing.xs,\n            flexWrap: ''wrap''\n          }}>\n            {vendorInfo.inferenceProviders.slice(0, 3).map((provider, idx) => (\n              <span\n                key={idx}\n                style={{\n                  padding: `2px $' + '{styles.spacing.xs}`,\n                  backgroundColor: styles.colors.primary + ''15'',\n                  color: styles.colors.primary,\n                  borderRadius: getBorderRadius(''xs'') || getBorderRadius(''sm''),\n                  fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm\n                }}\n              >\n                {provider.Vendor}\n              </span>\n            ))}\n            {vendorInfo.inferenceProviders.length > 3 && (\n              <span style={{\n                color: styles.colors.textSecondary,\n                fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm\n              }}>\n                +{vendorInfo.inferenceProviders.length - 3}\n              </span>\n            )}\n          </div>\n        );\n      }\n    },\n    {\n      field: ''InputTokenLimit'',\n      header: ''Input Limit'',\n      sortable: true,\n      width: ''100px'',\n      render: (value) => formatTokens(value)\n    },\n    {\n      field: ''CostRank'',\n      header: ''Cost'',\n      sortable: true,\n      width: ''80px'',\n      render: (value) => renderCostRank(value)\n    }\n  ];\n\n  // Handle row selection\n  const handleRowSelect = useCallback((selectedRows) => {\n    if (selectedRows && selectedRows.length > 0) {\n      onSelectModel?.(selectedRows[0].ID);\n    }\n  }, [onSelectModel]);\n  \n  // Grid view rendering\n  const renderGridView = () => (\n    <div style={{\n      display: ''grid'',\n      gridTemplateColumns: ''repeat(auto-fill, minmax(320px, 1fr))'',\n      gap: styles.spacing.lg\n    }}>\n      {models.map((model) => {\n        const vendorInfo = getModelVendorInfo(model.ID);\n        const isSelected = model.ID === selectedModelId;\n\n        return (\n          <div\n            key={model.ID}\n            onClick={() => onSelectModel?.(model.ID)}\n            style={{\n              backgroundColor: styles.colors.surface,\n              borderRadius: getBorderRadius(''md''),\n              padding: styles.spacing.lg,\n              cursor: ''pointer'',\n              transition: ''all 0.2s'',\n              border: isSelected\n                ? `2px solid $' + '{styles.colors.primary}`\n                : `1px solid $' + '{styles.colors.border}`,\n              boxShadow: isSelected\n                ? `0 4px 12px $' + '{styles.colors.shadow || ''rgba(0, 0, 0, 0.1)''}`\n                : ''none''\n            }}\n            onMouseEnter={(e) => {\n              if (!isSelected) {\n                e.currentTarget.style.borderColor = styles.colors.primary;\n                e.currentTarget.style.boxShadow = `0 2px 8px $' + '{styles.colors.shadow || ''rgba(0, 0, 0, 0.05)''}`;\n              }\n            }}\n            onMouseLeave={(e) => {\n              if (!isSelected) {\n                e.currentTarget.style.borderColor = styles.colors.border;\n                e.currentTarget.style.boxShadow = ''none'';\n              }\n            }}\n          >\n            {/* Model Header */}\n            <div style={{\n              display: ''flex'',\n              justifyContent: ''space-between'',\n              alignItems: ''flex-start'',\n              marginBottom: styles.spacing.md\n            }}>\n              <div style={{ flex: 1 }}>\n                <h3 style={{\n                  margin: 0,\n                  fontSize: styles.typography.fontSize.lg,\n                  fontWeight: styles.typography.fontWeight?.semibold || ''600'',\n                  color: styles.colors.text,\n                  marginBottom: styles.spacing.xs\n                }}>\n                  {model.Name}\n                </h3>\n                {model.APIName && (\n                  <div style={{\n                    fontSize: styles.typography.fontSize.sm,\n                    color: styles.colors.textSecondary,\n                    fontFamily: ''monospace''\n                  }}>\n                    {model.APIName}\n                  </div>\n                )}\n              </div>\n              {renderStatusBadge(model.IsActive ? ''Active'' : ''Inactive'')}\n            </div>\n\n            {/* Model Description */}\n            {model.Description && (\n              <div style={{\n                fontSize: styles.typography.fontSize.sm,\n                color: styles.colors.textSecondary,\n                marginBottom: styles.spacing.md,\n                overflow: ''hidden'',\n                textOverflow: ''ellipsis'',\n                display: ''-webkit-box'',\n                WebkitLineClamp: 2,\n                WebkitBoxOrient: ''vertical''\n              }}>\n                {model.Description}\n              </div>\n            )}\n\n            {/* Model Specs */}\n            <div style={{\n              display: ''grid'',\n              gridTemplateColumns: ''repeat(2, 1fr)'',\n              gap: styles.spacing.sm,\n              marginBottom: styles.spacing.md\n            }}>\n              <div style={{\n                padding: styles.spacing.sm,\n                backgroundColor: styles.colors.background,\n                borderRadius: getBorderRadius(''sm'')\n              }}>\n                <div style={{\n                  fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                  color: styles.colors.textSecondary,\n                  marginBottom: styles.spacing.xs\n                }}>\n                  Input Token Limit\n                </div>\n                <div style={{\n                  fontSize: styles.typography.fontSize.sm,\n                  fontWeight: styles.typography.fontWeight?.medium || ''500'',\n                  color: styles.colors.text\n                }}>\n                  {formatTokens(model.InputTokenLimit)}\n                </div>\n              </div>\n              <div style={{\n                padding: styles.spacing.sm,\n                backgroundColor: styles.colors.background,\n                borderRadius: getBorderRadius(''sm'')\n              }}>\n                <div style={{\n                  fontSize: styles.typography.fontSize.xs || styles.typography.fontSize.sm,\n                  color: styles.colors.textSecondary,\n                  marginBottom: styles.spacing.xs\n                }}>\n                  Cost Rank\n                </div>\n                <div style={{\n                  fontSize: styles.typography.fontSize.sm,\n                  fontWeight: styles.typography.fontWeight?.medium || ''500''\n                }}>\n                  {renderCostRank(model.CostRank)}\n                </div>\n              </div>\n            </div>\n\n            {/* Vendor Info */}\n            <div style={{\n              paddingTop: styles.spacing.md,\n              borderTop: `1px solid $' + '{styles.colors.border}`,\n              display: ''flex'',\n              gap: styles.spacing.md,\n              fontSize: styles.typography.fontSize.sm\n            }}>\n              {vendorInfo.modelDeveloper && (\n                <div style={{\n                  display: ''flex'',\n                  alignItems: ''center'',\n                  gap: styles.spacing.xs\n                }}>\n                  <span style={{ color: styles.colors.textSecondary }}>Dev:</span>\n                  <span style={{\n                    color: styles.colors.text,\n                    fontWeight: styles.typography.fontWeight?.medium || ''500''\n                  }}>\n                    {vendorInfo.modelDeveloper.Vendor}\n                  </span>\n                </div>\n              )}\n              {vendorInfo.inferenceProviders.length > 0 && (\n                <div style={{\n                  display: ''flex'',\n                  alignItems: ''center'',\n                  gap: styles.spacing.xs\n                }}>\n                  <span style={{ color: styles.colors.textSecondary }}>Providers:</span>\n                  <span style={{\n                    color: styles.colors.primary,\n                    fontWeight: styles.typography.fontWeight?.medium || ''500''\n                  }}>\n                    {vendorInfo.inferenceProviders.length}\n                  </span>\n                </div>\n              )}\n            </div>\n          </div>\n        );\n      })}\n    </div>\n  );\n\n  // List view rendering\n  const renderListView = () => (\n    <div style={{ width: ''100%'' }}>\n      {DataGrid ? (\n        <DataGrid\n          data={models}\n          columns={gridColumns}\n          pageSize={25}\n          showFilters={true}\n          showExport={false}\n          selectionMode=\"single\"\n          selectedRows={selectedModelId ? models.filter(m => m.ID === selectedModelId) : []}\n          onRowSelect={handleRowSelect}\n          sortBy={sortBy}\n          sortDirection={sortDirection}\n          onSortChange={onSortChange}\n          utilities={utilities}\n          styles={styles}\n          components={components}\n          callbacks={callbacks}\n          savedUserSettings={savedUserSettings}\n          onSaveUserSettings={onSaveUserSettings}\n        />\n      ) : (\n        <div style={{\n          padding: styles.spacing.lg,\n          textAlign: ''center'',\n          color: styles.colors.textSecondary\n        }}>\n          DataGrid component not available\n        </div>\n      )}\n    </div>\n  );\n\n  return viewMode === ''grid'' ? renderGridView() : renderListView();\n}","dependencies":[{"name":"DataGrid","location":"registry","namespace":"Generic/UI/Table","version":"^1.0.0"}],"libraries":[]}'
