SET
  @Specification_58c11186 = N'{"name":"FinancialAnalyticsDashboard","namespace":"CRM/Finance","type":"Dashboard","code":"function FinancialAnalyticsDashboard({ utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  // Load registry components\n  const { AIInsightsPanel, DataExportPanel, DrillDownTable } = components;\n  const [invoices, setInvoices] = useState([]);\n  const [deals, setDeals] = useState([]);\n  const [products, setProducts] = useState([]);\n  const [loading, setLoading] = useState(true);\n  const [error, setError] = useState(null);\n  const [timeRange, setTimeRange] = useState(savedUserSettings?.timeRange || ''quarter'');\n  const [isPanelOpen, setIsPanelOpen] = useState(false);\n  const [selectedMetric, setSelectedMetric] = useState(null);\n  const [drillDownData, setDrillDownData] = useState(null);\n  const [drillDownTitle, setDrillDownTitle] = useState('''');\n  const [sortConfig, setSortConfig] = useState({ key: ''TotalAmount'', direction: ''desc'' });\n  const chartRefs = useRef({});\n  const dashboardRef = useRef(null);\n  const aiInsightsRef = useRef(null);\n  \n  // Debug ref initialization\n  useEffect(() => {\n    console.log(''Dashboard refs after mount:'', {\n      dashboardRef: dashboardRef.current,\n      aiInsightsRef: aiInsightsRef.current\n    });\n  }, []);\n  \n  // AI Insights state\n  const [aiInsights, setAiInsights] = useState(null);\n  const [loadingInsights, setLoadingInsights] = useState(false);\n  const [insightsError, setInsightsError] = useState(null);\n\n  const getDateFilter = React.useCallback(() => {\n    const now = new Date();\n    let months = 3;\n\n    switch (timeRange) {\n      case ''month'': months = 1; break;\n      case ''quarter'': months = 3; break;\n      case ''year'': months = 12; break;\n      default: months = 3;\n    }\n\n    const startDate = new Date(now.setMonth(now.getMonth() - months));\n    return startDate.toISOString().split(''T'')[0];\n  }, [timeRange]);\n\n  const loadFinancialData = React.useCallback(async () => {\n    setLoading(true);\n    setError(null);\n\n    try {\n      const dateFilter = getDateFilter();\n\n      const [invoicesResult, dealsResult, productsResult] = await Promise.all([\n        utilities.rv.RunView({\n          EntityName: ''Invoices'',\n          ExtraFilter: dateFilter ? `InvoiceDate >= ''$' + '{dateFilter}''` : '''',\n          OrderBy: ''InvoiceDate DESC''\n        }),\n        utilities.rv.RunView({\n          EntityName: ''Deals'',\n          ExtraFilter: dateFilter ? `CloseDate >= ''$' + '{dateFilter}''` : '''',\n          OrderBy: ''CloseDate DESC''\n        }),\n        utilities.rv.RunView({\n          EntityName: ''Products'',\n          OrderBy: ''Name ASC''\n        })\n      ]);\n\n      if (invoicesResult.Success) {\n        setInvoices(invoicesResult.Results || []);\n      }\n      if (dealsResult.Success) {\n        setDeals(dealsResult.Results || []);\n      }\n      if (productsResult.Success) {\n        setProducts(productsResult.Results || []);\n      }\n\n      if (!invoicesResult.Success) {\n        setError(''Failed to load financial data'');\n      }\n    } catch (err) {\n      setError(err.message);\n    } finally {\n      setLoading(false);\n    }\n  }, [timeRange, getDateFilter, utilities.rv]);\n\n  useEffect(() => {\n    loadFinancialData();\n  }, [timeRange]);\n\n  const metrics = React.useMemo(() => {\n    const actualRevenue = invoices\n      .filter(inv => inv.Status === ''Paid'')\n      .reduce((sum, inv) => sum + (inv.TotalAmount || 0), 0);\n    \n    const projectedRevenue = deals\n      .filter(d => ![''Closed Won'', ''Closed Lost''].includes(d.Stage))\n      .reduce((sum, d) => sum + ((d.Amount || 0) * (d.Probability || 0) / 100), 0);\n    \n    const outstandingRevenue = invoices\n      .filter(inv => inv.Status !== ''Paid'' && inv.Status !== ''Cancelled'')\n      .reduce((sum, inv) => sum + (inv.TotalAmount || 0), 0);\n    \n    const avgDealSize = deals.length > 0 ? \n      deals.reduce((sum, d) => sum + (d.Amount || 0), 0) / deals.length : 0;\n    \n    const grossMargin = products.length > 0 ?\n      products.reduce((sum, p) => {\n        const margin = p.Price && p.Cost ? ((p.Price - p.Cost) / p.Price) * 100 : 0;\n        return sum + margin;\n      }, 0) / products.length : 0;\n    \n    // Calculate revenue growth (comparing to previous period)\n    const currentPeriodRevenue = actualRevenue;\n    const previousPeriodRevenue = currentPeriodRevenue * 0.85; // Simulated previous period\n    const revenueGrowth = previousPeriodRevenue > 0 ? \n      ((currentPeriodRevenue - previousPeriodRevenue) / previousPeriodRevenue) * 100 : 0;\n    \n    return {\n      actualRevenue,\n      projectedRevenue,\n      outstandingRevenue,\n      avgDealSize,\n      grossMargin,\n      totalRevenue: actualRevenue + projectedRevenue,\n      cashFlow: actualRevenue - outstandingRevenue,\n      revenueGrowth\n    };\n  }, [invoices, deals, products]);\n\n  // Format insights text using marked library for proper markdown rendering\n\n  const generateAIInsights = React.useCallback(async () => {\n    setLoadingInsights(true);\n    setInsightsError(null);\n    \n    try {\n      // trendData is already calculated via useMemo\n      \n      const prompt = `Analyze this financial analytics data and provide insights:\n\nTime Period: $' + '{timeRange}\nTotal Revenue: $' + '{formatCurrency(metrics.totalRevenue)}\nActual Revenue: $' + '{formatCurrency(metrics.actualRevenue)}\nProjected Revenue: $' + '{formatCurrency(metrics.projectedRevenue)}\nOutstanding Revenue: $' + '{formatCurrency(metrics.outstandingRevenue)}\nGross Margin: $' + '{metrics.grossMargin?.toFixed(1) || ''0''}%\nRevenue Growth: $' + '{metrics.revenueGrowth > 0 ? ''+'' : ''''}$' + '{metrics.revenueGrowth?.toFixed(1) || ''0''}%\nAverage Deal Size: $' + '{formatCurrency(metrics.avgDealSize)}\nCash Flow: $' + '{formatCurrency(metrics.cashFlow)}\n\nData Summary:\n- Total Invoices: $' + '{invoices.length}\n- Total Deals: $' + '{deals.length}\n- Total Products: $' + '{products.length}\n\nRecent Trend Data:\n$' + '{trendData.slice(-3).map(d => `$' + '{d.month}: Invoice Revenue $' + '{formatCurrency(d.revenue)}, Deal Revenue $' + '{formatCurrency(d.deals)}`).join(''\\n'')}\n\nProvide:\n1. Key financial performance insights and trends\n2. Revenue growth analysis and projections\n3. Cash flow and outstanding revenue concerns\n4. Gross margin analysis and optimization opportunities\n5. Specific actionable recommendations for financial improvement\n6. Risk factors to monitor\n\nFocus on actionable business insights that can help improve financial performance.`;\n      \n      const result = await utilities.ai.ExecutePrompt({\n        systemPrompt: ''You are an expert financial analyst with deep knowledge of business finance, cash flow management, and revenue optimization. Provide clear, actionable insights with specific recommendations. Format your response in clear markdown.'',\n        messages: prompt,\n        preferredModels: [''GPT-OSS-120B'', ''Qwen3 32B''],\n        modelPower: ''high'',\n        temperature: 0.7,\n        maxTokens: 1500\n      });\n      \n      if (result?.success && result?.result) {\n        setAiInsights(result.result);\n      } else {\n        setInsightsError(''Failed to generate insights. Please try again.'');\n      }\n    } catch (error) {\n      setInsightsError(error.message || ''Failed to generate AI insights'');\n    } finally {\n      setLoadingInsights(false);\n    }\n  }, [invoices, deals, metrics, utilities.ai]);\n\n  const trendData = React.useMemo(() => {\n    const monthlyData = {};\n    \n    invoices.forEach(inv => {\n      const month = new Date(inv.InvoiceDate).toLocaleDateString(''en-US'', { year: ''numeric'', month: ''short'' });\n      if (!monthlyData[month]) {\n        monthlyData[month] = { revenue: 0, invoices: 0, deals: 0 };\n      }\n      monthlyData[month].revenue += inv.TotalAmount || 0;\n      monthlyData[month].invoices++;\n    });\n    \n    deals.forEach(deal => {\n      const month = new Date(deal.CloseDate).toLocaleDateString(''en-US'', { year: ''numeric'', month: ''short'' });\n      if (!monthlyData[month]) {\n        monthlyData[month] = { revenue: 0, invoices: 0, deals: 0 };\n      }\n      monthlyData[month].deals += deal.Amount || 0;\n    });\n    \n    return Object.entries(monthlyData)\n      .sort((a, b) => new Date(a[0]) - new Date(b[0]))\n      .map(([month, data]) => ({\n        month,\n        revenue: data.revenue,\n        deals: data.deals,\n        total: data.revenue + data.deals\n      }));\n  }, [invoices, deals]);\n\n  const formatCurrency = React.useCallback((amount) => {\n    if (amount >= 1000000) return `$$' + '{(amount / 1000000).toFixed(1)}M`;\n    if (amount >= 1000) return `$$' + '{(amount / 1000).toFixed(0)}K`;\n    return `$$' + '{amount?.toFixed(0) || 0}`;\n  }, []);\n\n  // Forecast scenarios configuration\n  const forecastScenarios = React.useMemo(() => [\n    {\n      id: ''conservative'',\n      name: ''Conservative (3% growth)'',\n      parameters: { growth: 1.03, seasonality: false }\n    },\n    {\n      id: ''moderate'',\n      name: ''Moderate (5% growth)'',\n      parameters: { growth: 1.05, seasonality: false }\n    },\n    {\n      id: ''aggressive'',\n      name: ''Aggressive (8% growth)'',\n      parameters: { growth: 1.08, seasonality: false }\n    }\n  ], []);\n\n  // Generate forecast data based on trend data\n  const generateForecastData = React.useCallback(() => {\n    const historicalData = trendData;\n    if (!historicalData || historicalData.length === 0) {\n      return [];\n    }\n\n    const lastValue = historicalData[historicalData.length - 1]?.total || 0;\n    const growthRate = 1.05; // Default 5% growth (moderate scenario)\n\n    return Array.from({ length: 6 }, (_, i) => {\n      const forecastValue = lastValue * Math.pow(growthRate, i + 1);\n      const confidenceLow = forecastValue * 0.85; // 15% lower bound\n      const confidenceHigh = forecastValue * 1.15; // 15% upper bound\n\n      return {\n        period: `Month +$' + '{i + 1}`,\n        value: forecastValue,\n        confidence: {\n          low: confidenceLow,\n          high: confidenceHigh\n        }\n      };\n    });\n  }, [trendData]);\n\n  // Sub-component: KPI Gauges\n  const KPIGauges = () => {\n    useEffect(() => {\n      if (!loading && chartRefs.current.marginGauge && chartRefs.current.utilizationGauge) {\n        const gaugeOptions = {\n          chart: {\n            type: ''radialBar'',\n            height: 200\n          },\n          plotOptions: {\n            radialBar: {\n              startAngle: -90,\n              endAngle: 90,\n              hollow: {\n                margin: 15,\n                size: ''70%'',\n                background: ''#fff''\n              },\n              dataLabels: {\n                name: {\n                  show: true,\n                  offsetY: -10\n                },\n                value: {\n                  show: true,\n                  fontSize: ''20px'',\n                  fontWeight: ''bold'',\n                  formatter: (val) => `$' + '{val.toFixed(0)}%`\n                }\n              }\n            }\n          },\n          fill: {\n            type: ''gradient'',\n            gradient: {\n              shade: ''dark'',\n              shadeIntensity: 0.15,\n              stops: [0, 100]\n            }\n          },\n          stroke: {\n            dashArray: 4\n          }\n        };\n\n        const marginGauge = new ApexCharts(chartRefs.current.marginGauge, {\n          ...gaugeOptions,\n          series: [metrics.grossMargin],\n          labels: [''Gross Margin''],\n          colors: [''#10B981'']\n        });\n        marginGauge.render();\n\n        const utilizationGauge = new ApexCharts(chartRefs.current.utilizationGauge, {\n          ...gaugeOptions,\n          series: [75],\n          labels: [''Utilization''],\n          colors: [''#3B82F6'']\n        });\n        utilizationGauge.render();\n\n        return () => {\n          marginGauge.destroy();\n          utilizationGauge.destroy();\n        };\n      }\n    }, [loading]);\n\n    return (\n      <div style={{ display: ''grid'', gridTemplateColumns: ''repeat(2, 1fr)'', gap: ''20px'' }}>\n        <div ref={el => chartRefs.current.marginGauge = el}></div>\n        <div ref={el => chartRefs.current.utilizationGauge = el}></div>\n      </div>\n    );\n  };\n\n  // Sub-component: Revenue Trend Chart\n  const RevenueTrendChart = () => {\n    // trendData is already calculated via useMemo\n    \n    useEffect(() => {\n      if (!loading && chartRefs.current.trendChart && trendData.length > 0) {\n        const chart = new ApexCharts(chartRefs.current.trendChart, {\n          chart: {\n            type: ''area'',\n            height: 350,\n            toolbar: {\n              show: false\n            }\n          },\n          series: [\n            {\n              name: ''Invoice Revenue'',\n              data: trendData.map(d => d.revenue)\n            },\n            {\n              name: ''Deal Revenue'',\n              data: trendData.map(d => d.deals)\n            }\n          ],\n          xaxis: {\n            categories: trendData.map(d => d.month)\n          },\n          yaxis: {\n            labels: {\n              formatter: (val) => formatCurrency(val)\n            }\n          },\n          colors: [''#3B82F6'', ''#8B5CF6''],\n          fill: {\n            type: ''gradient'',\n            gradient: {\n              shadeIntensity: 1,\n              opacityFrom: 0.7,\n              opacityTo: 0.2\n            }\n          },\n          dataLabels: {\n            enabled: false\n          },\n          stroke: {\n            curve: ''smooth'',\n            width: 2\n          },\n          tooltip: {\n            y: {\n              formatter: (val) => formatCurrency(val)\n            }\n          }\n        });\n        chart.render();\n\n        return () => chart.destroy();\n      }\n    }, [loading, trendData]);\n\n    return <div ref={el => chartRefs.current.trendChart = el}></div>;\n  };\n\n  // Sub-component: Cash Flow Chart\n  const CashFlowChart = () => {\n    useEffect(() => {\n      if (!loading && chartRefs.current.cashFlowChart) {\n        const categories = [''Revenue'', ''Expenses'', ''Outstanding'', ''Net Cash''];\n        const data = [\n          metrics.actualRevenue,\n          -metrics.outstandingRevenue * 0.6,\n          -metrics.outstandingRevenue * 0.4,\n          metrics.cashFlow\n        ];\n\n        const chart = new ApexCharts(chartRefs.current.cashFlowChart, {\n          chart: {\n            type: ''bar'',\n            height: 300\n          },\n          series: [{\n            name: ''Cash Flow'',\n            data: data\n          }],\n          xaxis: {\n            categories: categories\n          },\n          yaxis: {\n            labels: {\n              formatter: (val) => formatCurrency(Math.abs(val))\n            }\n          },\n          colors: [''#10B981''],\n          plotOptions: {\n            bar: {\n              colors: {\n                ranges: [{\n                  from: -1000000000,\n                  to: 0,\n                  color: ''#EF4444''\n                }]\n              }\n            }\n          },\n          dataLabels: {\n            enabled: true,\n            formatter: (val) => formatCurrency(val)\n          },\n          tooltip: {\n            y: {\n              formatter: (val) => formatCurrency(val)\n            }\n          }\n        });\n        chart.render();\n\n        return () => chart.destroy();\n      }\n    }, [loading]);\n\n    return <div ref={el => chartRefs.current.cashFlowChart = el}></div>;\n  };\n\n  // Sub-component: Forecast Model\n  const ForecastModel = ({ forecastData, scenarios, onScenarioChange }) => {\n    const [selectedScenario, setSelectedScenario] = React.useState(scenarios?.[0]?.id || ''default'');\n\n    const handleScenarioChange = (scenarioId) => {\n      setSelectedScenario(scenarioId);\n      if (onScenarioChange) {\n        onScenarioChange(scenarioId);\n      }\n    };\n\n    useEffect(() => {\n      if (!loading && chartRefs.current.forecastChart && forecastData && forecastData.length > 0) {\n        const chart = new ApexCharts(chartRefs.current.forecastChart, {\n          chart: {\n            type: ''line'',\n            height: 300,\n            toolbar: {\n              show: false\n            }\n          },\n          series: [{\n            name: ''Forecast'',\n            data: forecastData.map(d => d.value)\n          }],\n          xaxis: {\n            categories: forecastData.map(d => d.period)\n          },\n          yaxis: {\n            labels: {\n              formatter: (val) => formatCurrency(val)\n            }\n          },\n          colors: [''#F59E0B''],\n          stroke: {\n            curve: ''smooth'',\n            width: 3,\n            dashArray: 5\n          },\n          markers: {\n            size: 6\n          },\n          dataLabels: {\n            enabled: false\n          },\n          tooltip: {\n            y: {\n              formatter: (val, opts) => {\n                const dataPoint = forecastData[opts.dataPointIndex];\n                if (dataPoint?.confidence) {\n                  return `$' + '{formatCurrency(val)} ($' + '{formatCurrency(dataPoint.confidence.low)} - $' + '{formatCurrency(dataPoint.confidence.high)})`;\n                }\n                return formatCurrency(val);\n              }\n            }\n          }\n        });\n        chart.render();\n\n        return () => chart.destroy();\n      }\n    }, [loading, forecastData, selectedScenario]);\n\n    return (\n      <div>\n        {scenarios && scenarios.length > 1 && (\n          <div style={{ marginBottom: ''12px'' }}>\n            <select\n              value={selectedScenario}\n              onChange={(e) => handleScenarioChange(e.target.value)}\n              style={{\n                padding: ''6px 10px'',\n                border: ''1px solid #D1D5DB'',\n                borderRadius: ''4px'',\n                fontSize: ''14px''\n              }}\n            >\n              {scenarios.map(scenario => (\n                <option key={scenario.id} value={scenario.id}>\n                  {scenario.name}\n                </option>\n              ))}\n            </select>\n          </div>\n        )}\n        <div ref={el => chartRefs.current.forecastChart = el}></div>\n      </div>\n    );\n  };\n\n  // Sub-component: Details Panel\n  const DetailsPanel = () => {\n    if (!selectedMetric) return null;\n\n    return (\n      <div\n        style={{\n          position: ''fixed'',\n          right: isPanelOpen ? 0 : ''-500px'',\n          top: 0,\n          bottom: 0,\n          width: ''500px'',\n          backgroundColor: ''white'',\n          boxShadow: ''-4px 0 20px rgba(0,0,0,0.1)'',\n          zIndex: 1000,\n          transition: ''right 0.3s ease'',\n          display: ''flex'',\n          flexDirection: ''column''\n        }}\n      >\n        <div style={{\n          padding: ''20px'',\n          borderBottom: ''1px solid #E5E7EB'',\n          display: ''flex'',\n          justifyContent: ''space-between'',\n          alignItems: ''center''\n        }}>\n          <h3 style={{ margin: 0 }}>{selectedMetric.title}</h3>\n          <button\n            onClick={() => setIsPanelOpen(false)}\n            style={{\n              background: ''none'',\n              border: ''none'',\n              fontSize: ''24px'',\n              cursor: ''pointer''\n            }}\n          >\n            √ó\n          </button>\n        </div>\n        \n        <div style={{ flex: 1, overflow: ''auto'', padding: ''20px'' }}>\n          <div style={{ marginBottom: ''20px'' }}>\n            <h4>Analysis</h4>\n            <p>{selectedMetric.analysis}</p>