  @Specification_c14ebb3d = N'{"name":"ActivityFeed","namespace":"CRM/Dashboards","type":"Other","location":"embedded","code":"// ActivityFeed Sub-component\nfunction ActivityFeed ({ items, onItemClick, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  return (\n    <div style={{\n      backgroundColor: ''#fff'',\n      borderRadius: ''8px'',\n      padding: ''15px'',\n      maxHeight: ''400px'',\n      overflowY: ''auto''\n    }}>\n      <h4 style={{ margin: ''0 0 15px 0'', fontSize: ''16px'' }}>Recent Activity</h4>\n      <div style={{ display: ''flex'', flexDirection: ''column'', gap: ''10px'' }}>\n        {items && items.map((item, index) => (\n          <div\n            key={item.id || index}\n            onClick={() => onItemClick && onItemClick(item)}\n            style={{\n              padding: ''10px'',\n              backgroundColor: ''#f8f9fa'',\n              borderRadius: ''6px'',\n              borderLeft: `3px solid $' + '{item.color || ''#007bff''}`,\n              cursor: onItemClick ? ''pointer'' : ''default'',\n              transition: ''background-color 0.2s''\n            }}\n            onMouseEnter={(e) => {\n              if (onItemClick) e.currentTarget.style.backgroundColor = ''#e9ecef'';\n            }}\n            onMouseLeave={(e) => {\n              e.currentTarget.style.backgroundColor = ''#f8f9fa'';\n            }}\n          >\n            <div style={{ display: ''flex'', justifyContent: ''space-between'', alignItems: ''flex-start'' }}>\n              <div style={{ flex: 1 }}>\n                <div style={{ fontWeight: ''500'', fontSize: ''14px'', marginBottom: ''3px'' }}>{item.title}</div>\n                <div style={{ fontSize: ''12px'', color: ''#666'' }}>{item.description}</div>\n              </div>\n              <div style={{ fontSize: ''11px'', color: ''#999'', whiteSpace: ''nowrap'' }}>{item.time}</div>\n            </div>\n          </div>\n        ))}\n      </div>\n      {(!items