  @Specification_1392ffeb = N'{"name":"CashFlowChart","namespace":"CRM/Finance","type":"Chart","location":"embedded","code":"// CashFlowChart Sub-component\nfunction CashFlowChart ({ data, onDataClick, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings }) {\n  // Load SimpleChart from the components registry\n  const SimpleChart = components[''SimpleChart''];\n\n  const handleChartClick = React.useCallback((event) => {\n    if (event && onDataClick) {\n      onDataClick(event);\n    }\n  }, [onDataClick]);\n\n  // Memoize the chart content to prevent unnecessary re-renders\n  const chartContent = React.useMemo(() => {\n    if (!SimpleChart) {\n      return (\n        <div style={{ padding: ''20px'', textAlign: ''center'', color: ''#6B7280'' }}>\n          SimpleChart component not available\n        </div>\n      );\n    }\n\n    // SimpleChart expects raw data array, not pre-processed chart data\n    // The parent component should pass raw records instead of chart series/categories\n    return (\n      <SimpleChart\n        key=\"cash-flow-chart\"\n        data={data}\n        groupBy=\"Date\"  // Adjust this field name based on actual data structure\n        aggregateMethod=\"sum\"\n        chartType=\"line\"\n        title=\"Cash Flow\"\n        onDataPointClick={handleChartClick}\n        utilities={utilities}\n        styles={styles}\n        components={components}\n        callbacks={callbacks}\n        savedUserSettings={savedUserSettings}\n        onSaveUserSettings={onSaveUserSettings}\n      />\n    );\n  }, [SimpleChart, data, handleChartClick, utilities, styles, components, callbacks, savedUserSettings, onSaveUserSettings]);\n\n  return (\n    <div style={{ width: ''100%'', height: ''350px'' }}>\n      {chartContent}\n    </div>\n  );\n}","functionalRequirements":"## Cash Flow Chart\n\n### Purpose\nVisualize cash flow patterns.\n\n### Features\n- Waterfall chart\n- Inflows vs outflows\n- Running balance\n- Category breakdown","dataRequirements":{"mode":"views","description":"Visualizes cash flow patterns from payment and invoice data","entities":[{"name":"Payments","description":"Payment transactions for cash flow tracking","displayFields":["PaymentDate","Amount","Category"],"filterFields":[],"sortFields":[],"fieldMetadata":[],"permissionLevelNeeded":["read"],"usageContext":"Cash inflows from payments"},{"name":"Invoices","description":"Invoice data for cash outflow tracking","displayFields":["IssueDate","TotalAmount","Category"],"filterFields":[],"sortFields":[],"fieldMetadata":[],"permissionLevelNeeded":["read"],"usageContext":"Cash outflows from invoices"}],"queries":[]},"technicalDesign":"## Implementation\n\n### Props\n- cashFlowData: Array of cash flow entries\n\n### Chart\n- ApexCharts waterfall\n- Color coding","properties":[{"name":"cashFlowData","type":"Array<{date: string, category: string, amount: number, type: ''inflow'' | ''outflow'', balance: number}>","required":true,"description":"Cash flow data entries"}],"events":[],"dependencies":[],"libraries":[],"description":"Waterfall chart showing cash flow components"}'
