  @Specification_0516dd12 = N'{"name":"SalesFunnelDealCard","namespace":"CRM/Analytics","type":"Card","location":"embedded","code":"function SalesFunnelDealCard({ deal, callbacks, formatCurrency }) {\n  return (\n    <div\n      style={{\n        padding: ''16px'',\n        backgroundColor: ''#F9FAFB'',\n        borderRadius: ''8px'',\n        border: ''1px solid #E5E7EB'',\n        cursor: ''pointer'',\n        transition: ''all 0.2s''\n      }}\n      onClick={() => {\n        if (callbacks?.OpenEntityRecord) {\n          callbacks.OpenEntityRecord(''Deals'', [{ FieldName: ''ID'', Value: deal.ID }]);\n        }\n      }}\n      onMouseEnter={(e) => {\n        e.currentTarget.style.backgroundColor = ''#F3F4F6'';\n        e.currentTarget.style.transform = ''translateY(-2px)'';\n      }}\n      onMouseLeave={(e) => {\n        e.currentTarget.style.backgroundColor = ''#F9FAFB'';\n        e.currentTarget.style.transform = ''translateY(0)'';\n      }}\n    >\n      <div style={{ display: ''flex'', justifyContent: ''space-between'', marginBottom: ''8px'' }}>\n        <div style={{ fontWeight: ''bold'', flex: 1 }}>{deal.DealName}</div>\n        <div style={{ display: ''flex'', alignItems: ''center'', gap: ''8px'' }}>\n          <div style={{ fontWeight: ''bold'', color: ''#059669'' }}>\n            {formatCurrency(deal.Amount)}\n          </div>\n          <button\n            onClick={(e) => {\n              e.stopPropagation();\n              if (callbacks?.OpenEntityRecord) {\n                callbacks.OpenEntityRecord(''Deals'', [{ FieldName: ''ID'', Value: deal.ID }]);\n              }\n            }}\n            style={{\n              background: ''none'',\n              border: ''none'',\n              color: ''#6B7280'',\n              cursor: ''pointer'',\n              padding: ''2px'',\n              fontSize: ''14px''\n            }}\n            title=\"Open in Explorer\"\n          >\n            <i className=\"fa-solid fa-up-right-from-square\"></i>\n          </button>\n        </div>\n      </div>\n      <div style={{ fontSize: ''14px'', color: ''#6B7280'', marginBottom: ''4px'' }}>\n        {deal.AccountName || ''No Account''}\n      </div>\n      <div style={{ display: ''flex'', justifyContent: ''space-between'', fontSize: ''12px'', color: ''#9CA3AF'' }}>\n        <span>Close: {deal.CloseDate ? new Date(deal.CloseDate).toLocaleDateString() : ''TBD''}</span>\n        <span>{deal.Probability}% probability</span>\n      </div>\n    </div>\n  );\n}","functionalRequirements":"## Deal Card Sub-Component\n\n### Purpose\nDisplay individual deal information in a card format.\n\n### Core Features\n- **Deal Information**: Name, account, amount\n- **Visual Indicators**: Stage colors and status\n- **Interactive Elements**: Click to open deal\n- **Responsive Layout**: Adapts to container","dataRequirements":{"mode":"views","description":"Displays individual deal information","entities":[{"name":"Deals","description":"Deal entity data","displayFields":["ID","Name","Amount","Stage","AccountID","Account"],"filterFields":[],"sortFields":[],"fieldMetadata":[],"permissionLevelNeeded":["read"],"usageContext":"Individual deal card display"}],"queries":[]},"technicalDesign":"## Implementation\n\n### Card Layout\n- Flexbox for responsive design\n- Hover effects for interactivity\n- Click handlers for navigation","properties":[{"name":"deal","type":"{ID: string, Name: string, Amount: number, Stage: string, Account: string}","description":"Deal entity data","required":true},{"name":"formatCurrency","type":"(value: number) => string","description":"Currency formatting function","required":true}],"events":[],"dependencies":[],"libraries":[],"description":"Card component for displaying individual deal information in the sales funnel"}'
