  @Specification_540c028e = N'{"name":"MonthlyInvoiceRevenueChart","title":"Monthly Invoice Revenue Chart","description":"Displays monthly invoice revenue with drill-down to individual invoices. Demonstrates the CORRECT pattern for chart with drill-down: loading detail records, adding computed time period field, and enabling drill-down to see individual invoices for each month.","type":"chart","location":"embedded","functionalRequirements":"Shows total invoice revenue aggregated by month. Users can click any month''s bar to see all invoices for that period in a data grid below. Demonstrates proper use of detail records with computed grouping fields for time-based aggregation.","technicalDesign":"Loads all invoices for current year via RunView. Adds computed Month and MonthName fields to each invoice record. Passes detail records to SimpleDrilldownChart which performs client-side aggregation by month and sums TotalAmount. Uses bar chart for time series. Grid shows invoice details with standard invoice fields.","dataRequirements":{"mode":"props","description":"Loads Invoice entity records via RunView, filters to current year"},"properties":[{"name":"year","type":"number","description":"Year to display revenue for (defaults to current year)","required":false,"defaultValue":"new Date().getFullYear()"}],"events":[],"dependencies":[{"name":"SimpleDrilldownChart","namespace":"Generic/UI/Chart","location":"registry","version":"1.0.0","description":"Chart with drill-down capability"}],"code":"function MonthlyInvoiceRevenueChart({\n  utilities,\n  styles,\n  components,\n  callbacks,\n  savedUserSettings,\n  onSaveUserSettings,\n  year\n}) {\n  const { SimpleDrilldownChart } = components;\n  const [invoiceData, setInvoiceData] = React.useState([]);\n  const [loading, setLoading] = React.useState(true);\n  const [error, setError] = React.useState(null);\n\n  const selectedYear = year || new Date().getFullYear();\n\n  React.useEffect(() => {\n    const loadInvoices = async () => {\n      setLoading(true);\n      setError(null);\n\n      try {\n        const result = await utilities.rv.RunView({\n          EntityName: ''Invoices'',\n          ExtraFilter: `YEAR(InvoiceDate) = $' + '{selectedYear}`\n        });\n\n        if (!result.Success) {\n          setError(result.ErrorMessage || ''Failed to load invoices'');\n          setInvoiceData([]);\n          return;\n        }\n\n        // Add computed Month fields to each invoice for grouping\n        const invoicesWithMonth = (result.Results || []).map(invoice => {\n          const invoiceDate = new Date(invoice.InvoiceDate);\n          return {\n            ...invoice,\n            Month: invoiceDate.getMonth() + 1, // 1-12\n            MonthName: invoiceDate.toLocaleString(''default'', { month: ''short'' }),\n            MonthYear: `$' + '{invoiceDate.toLocaleString(''default'', { month: ''short'' })} $' + '{invoiceDate.getFullYear()}`\n          };\n        });\n\n        setInvoiceData(invoicesWithMonth);\n      } catch (err) {\n        console.error(''Error loading invoices:'', err);\n        setError(err.message || ''Unknown error occurred'');\n        setInvoiceData([]);\n      } finally {\n        setLoading(false);\n      }\n    };\n\n    loadInvoices();\n  }, [selectedYear, utilities?.refresh]);\n\n  if (loading) {\n    return (\n      <div style={{ padding: ''40px'', textAlign: ''center'', color: ''#666'' }}>\n        Loading invoice data...\n      </div>\n    );\n  }\n\n  if (error) {\n    return (\n      <div style={{\n        padding: ''20px'',\n        backgroundColor: ''#fff2f0'',\n        border: ''1px solid #ffccc7'',\n        borderRadius: ''4px'',\n        color: ''#cf1322''\n      }}>\n        <strong>Error:</strong> {error}\n      </div>\n    );\n  }\n\n  if (invoiceData.length === 0) {\n    return (\n      <div style={{\n        padding: ''40px'',\n        textAlign: ''center'',\n        backgroundColor: ''#fafafa'',\n        border: ''1px solid #d9d9d9'',\n        borderRadius: ''4px'',\n        color: ''#666''\n      }}>\n        No invoices found for {selectedYear}\n      </div>\n    );\n  }\n\n  return (\n    <SimpleDrilldownChart\n      entityName=\"Invoices\"\n      data={invoiceData}\n      groupBy=\"Month\"\n      valueField=\"TotalAmount\"\n      aggregateMethod=\"sum\"\n      chartType=\"bar\"\n      title={`Monthly Invoice Revenue - $' + '{selectedYear}`}\n      gridFields={[\n        ''InvoiceNumber'',\n        ''InvoiceDate'',\n        ''Account'',\n        ''Status'',\n        ''TotalAmount'',\n        ''BalanceDue''\n      ]}\n      entityPrimaryKeys={[''ID'']}\n      showDrilldown={true}\n      drilldownHeight={400}\n      showSingleRecordView={false}\n      utilities={utilities}\n      styles={styles}\n      components={components}\n      callbacks={callbacks}\n      savedUserSettings={savedUserSettings}\n      onSaveUserSettings={onSaveUserSettings}\n    />\n  );\n}\n","exampleUsage":"<MonthlyInvoiceRevenueChart year={2025} />"}'
