SET
  @Specification_2c0eef5e = N'{"name":"ProductRevenueByCategory","title":"Product Revenue by Category","description":"Shows total revenue by product category with drill-down to invoice line items. Demonstrates intermediate chart with drill-down usage with custom grid fields, pie chart type, and event handlers for segment selection.","type":"chart","location":"embedded","functionalRequirements":"Aggregates invoice line item revenue by product category. Displays as pie chart for easy comparison of category contributions. Clicking a category segment reveals all line items in that category with product details. Features custom event handler to track user interactions and optional external callbacks for parent component integration.","technicalDesign":"Loads InvoiceLineItems with Product relationships via RunView. Filters to current year and paid/partial invoices only. Each line item includes ProductCategory from related Product. Uses pie chart for categorical comparison. Implements onDataPointClick handler to track segment selections. Grid displays detailed line item information including product names, quantities, prices. Demonstrates proper use of aggregateMethod=''sum'' with valueField for revenue calculations.","dataRequirements":{"mode":"props","description":"Loads InvoiceLineItem entity with Product join, filters to current year paid invoices"},"properties":[{"name":"year","type":"number","description":"Year to display revenue for","required":false,"defaultValue":"new Date().getFullYear()"},{"name":"onCategorySelected","type":"function","description":"Callback fired when user clicks a category segment","required":false,"parameters":[{"name":"categoryData","type":"{ category: string, revenue: number, itemCount: number }","description":"Selected category information"}]}],"events":[{"name":"categorySelected","description":"Fired when a category segment is clicked","parameters":[{"name":"categoryData","type":"{ category: string, revenue: number, itemCount: number }","description":"Selected category details"}]}],"dependencies":[{"name":"SimpleDrilldownChart","namespace":"Generic/UI/Chart","location":"registry","version":"1.0.0","description":"Chart with drill-down capability"}],"code":"function ProductRevenueByCategory({\n  utilities,\n  styles,\n  components,\n  callbacks,\n  savedUserSettings,\n  onSaveUserSettings,\n  year,\n  onCategorySelected\n}) {\n  const { SimpleDrilldownChart } = components;\n  const [lineItemData, setLineItemData] = React.useState([]);\n  const [loading, setLoading] = React.useState(true);\n  const [error, setError] = React.useState(null);\n  const [selectedProduct, setSelectedProduct] = React.useState(null);\n\n  const selectedYear = year || new Date().getFullYear();\n\n  React.useEffect(() => {\n    const loadLineItems = async () => {\n      setLoading(true);\n      setError(null);\n\n      try {\n        // Load invoice line items - group by Product since Category isn''t in the view\n        const result = await utilities.rv.RunView({\n          EntityName: ''Invoice Line Items'',\n          ExtraFilter: ''''\n        });\n\n        if (!result.Success) {\n          setError(result.ErrorMessage || ''Failed to load line items'');\n          setLineItemData([]);\n          return;\n        }\n\n        // Process line items - use Product field (denormalized product name)\n        const processedItems = (result.Results || []).map(item => ({\n          ...item,\n          // Ensure we have product name, default if missing\n          ProductName: item.Product || ''Unknown Product'',\n          // Add computed fields for display\n          QuantityFormatted: item.Quantity ? item.Quantity.toFixed(2) : ''0'',\n          UnitPriceFormatted: item.UnitPrice != null\n            ? `$$' + '{item.UnitPrice.toFixed(2)}`\n            : ''N/A'',\n          TotalPriceFormatted: item.TotalPrice != null\n            ? `$$' + '{item.TotalPrice.toFixed(2)}`\n            : ''N/A'',\n          DiscountFormatted: item.Discount != null\n            ? `$' + '{item.Discount.toFixed(1)}%`\n            : ''0%''\n        }));\n\n        setLineItemData(processedItems);\n      } catch (err) {\n        console.error(''Error loading line items:'', err);\n        setError(err.message || ''Unknown error occurred'');\n        setLineItemData([]);\n      } finally {\n        setLoading(false);\n      }\n    };\n\n    loadLineItems();\n  }, [selectedYear, utilities?.refresh]);\n\n  // Handle product segment click\n  const handleDataPointClick = (clickData) => {\n    const productName = clickData.label;\n    const productRevenue = clickData.value;\n    const productItems = clickData.records || [];\n\n    setSelectedProduct({\n      product: productName,\n      revenue: productRevenue,\n      itemCount: productItems.length\n    });\n\n    // Fire external callback if provided\n    if (onCategorySelected) {\n      onCategorySelected({\n        category: productName,\n        revenue: productRevenue,\n        itemCount: productItems.length\n      });\n    }\n  };\n\n  if (loading) {\n    return (\n      <div style={{ padding: ''40px'', textAlign: ''center'', color: ''#666'' }}>\n        <div style={{ fontSize: ''16px'', marginBottom: ''8px'' }}>\n          Loading product revenue data...\n        </div>\n        <div style={{ fontSize: ''14px'', color: ''#999'' }}>\n          Analyzing sales by product\n        </div>\n      </div>\n    );\n  }\n\n  if (error) {\n    return (\n      <div style={{\n        padding: ''20px'',\n        backgroundColor: ''#fff2f0'',\n        border: ''1px solid #ffccc7'',\n        borderRadius: ''4px'',\n        color: ''#cf1322''\n      }}>\n        <div style={{ fontWeight: ''bold'', marginBottom: ''8px'' }}>\n          Error Loading Data\n        </div>\n        <div>{error}</div>\n      </div>\n    );\n  }\n\n  if (lineItemData.length === 0) {\n    return (\n      <div style={{\n        padding: ''40px'',\n        textAlign: ''center'',\n        backgroundColor: ''#fafafa'',\n        border: ''1px solid #d9d9d9'',\n        borderRadius: ''4px'',\n        color: ''#666''\n      }}>\n        <div style={{ fontSize: ''16px'', marginBottom: ''8px'' }}>\n          No sales data found\n        </div>\n        <div style={{ fontSize: ''14px'', color: ''#999'' }}>\n          No invoice line items in the system\n        </div>\n      </div>\n    );\n  }\n\n  return (\n    <div style={{ width: ''100%'' }}>\n      {/* Show selected product info */}\n      {selectedProduct && (\n        <div style={{\n          padding: ''12px 16px'',\n          marginBottom: ''16px'',\n          backgroundColor: ''#e6f7ff'',\n          border: ''1px solid #91d5ff'',\n          borderRadius: ''4px'',\n          display: ''flex'',\n          justifyContent: ''space-between'',\n          alignItems: ''center''\n        }}>\n          <div>\n            <strong style={{ color: ''#0050b3'' }}>\n              {selectedProduct.product}\n            </strong>\n            <span style={{ marginLeft: ''16px'', color: ''#666'' }}>\n              {selectedProduct.itemCount} items ‚Ä¢ $' + '{selectedProduct.revenue.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })} revenue\n            </span>\n          </div>\n          <button\n            onClick={() => setSelectedProduct(null)}\n            style={{\n              padding: ''4px 12px'',\n              backgroundColor: ''white'',\n              border: ''1px solid #91d5ff'',\n              borderRadius: ''4px'',\n              cursor: ''pointer'',\n              fontSize: ''12px'',\n              color: ''#0050b3''\n            }}\n          >\n            Clear\n          </button>\n        </div>\n      )}\n\n      <SimpleDrilldownChart\n        entityName=\"Invoice Line Items\"\n        data={lineItemData}\n        groupBy=\"ProductName\"\n        valueField=\"TotalPrice\"\n        aggregateMethod=\"sum\"\n        chartType=\"pie\"\n        title=\"Product Revenue by Product\"\n        gridFields={[\n          ''Product'',\n          ''Description'',\n          ''QuantityFormatted'',\n          ''UnitPriceFormatted'',\n          ''DiscountFormatted'',\n          ''TotalPriceFormatted''\n        ]}\n        entityPrimaryKeys={[''ID'']}\n        showDrilldown={true}\n        drilldownHeight={450}\n        showSingleRecordView={false}\n        onDataPointClick={handleDataPointClick}\n        utilities={utilities}\n        styles={styles}\n        components={components}\n        callbacks={callbacks}\n        savedUserSettings={savedUserSettings}\n        onSaveUserSettings={onSaveUserSettings}\n      />\n    </div>\n  );\n}\n","exampleUsage":"<ProductRevenueByCategory \n  year={2025} \n  onCategorySelected={(data) => console.log(''Selected:'', data.category, data.revenue)}\n/>"}'
SET
  @FunctionalRequirements_2c0eef5e = N'Aggregates invoice line item revenue by product category. Displays as pie chart for easy comparison of category contributions. Clicking a category segment reveals all line items in that category with product details. Features custom event handler to track user interactions and optional external callbacks for parent component integration.'
SET
  @TechnicalDesign_2c0eef5e = N'Loads InvoiceLineItems with Product relationships via RunView. Filters to current year and paid/partial invoices only. Each line item includes ProductCategory from related Product. Uses pie chart for categorical comparison. Implements onDataPointClick handler to track segment selections. Grid displays detailed line item information including product names, quantities, prices. Demonstrates proper use of aggregateMethod=''sum'' with valueField for revenue calculations.'
