  @Specification_ecbc93f8 = N'{"name":"EventSubmissionsByStatus","title":"Event Submissions by Status","description":"Conference abstract submissions grouped by status with drill-down to submission details. Demonstrates advanced chart with drill-down features including SingleRecordView panel, custom field configuration, and count-based aggregation for tracking submission volumes across workflow stages.","type":"chart","location":"embedded","functionalRequirements":"Displays count of abstract submissions by status (New, Analyzing, Passed Initial, Under Review, Accepted, Rejected, etc.) for a specific event. Users click a status segment to see all submissions in that status. Clicking a submission row in the grid opens a detailed SingleRecordView panel showing full submission information including title, abstract, evaluation scores, and speaker details. Useful for conference organizers tracking submission pipeline and review workflows.","technicalDesign":"Loads all Submission records for specified EventID via RunView. Uses count aggregation (no valueField) to show submission volumes by Status. Implements doughnut chart for workflow stage visualization. Enables both grid drill-down and SingleRecordView side panel. Grid shows key submission fields (title, submitted date, score). SingleRecordView displays comprehensive submission details for in-depth review. Demonstrates showSingleRecordView=true configuration with custom field selection.","dataRequirements":{"mode":"props","description":"Loads Submission entity filtered by EventID"},"properties":[{"name":"eventId","type":"string","description":"GUID of the event to show submissions for","required":true},{"name":"eventName","type":"string","description":"Name of the event for display in title","required":false},{"name":"onSubmissionSelected","type":"function","description":"Callback when a submission is selected in the grid","required":false,"parameters":[{"name":"submission","type":"object","description":"Selected submission record"}]}],"events":[{"name":"submissionSelected","description":"Fired when user selects a submission row","parameters":[{"name":"submission","type":"object","description":"Selected submission details"}]}],"dependencies":[{"name":"SimpleDrilldownChart","namespace":"Generic/UI/Chart","location":"registry","version":"1.0.0","description":"Chart with drill-down and SingleRecordView capability"}],"code":"function EventSubmissionsByStatus({\n  utilities,\n  styles,\n  components,\n  callbacks,\n  savedUserSettings,\n  onSaveUserSettings,\n  eventId,\n  eventName,\n  onSubmissionSelected\n}) {\n  const { SimpleDrilldownChart } = components;\n  const [submissionData, setSubmissionData] = React.useState([]);\n  const [loading, setLoading] = React.useState(true);\n  const [error, setError] = React.useState(null);\n\n  React.useEffect(() => {\n    if (!eventId) {\n      setError(''Event ID is required'');\n      setLoading(false);\n      return;\n    }\n\n    const loadSubmissions = async () => {\n      setLoading(true);\n      setError(null);\n\n      try {\n        const result = await utilities.rv.RunView({\n          EntityName: ''Submissions'',\n          ExtraFilter: `EventID = ''$' + '{eventId}''`,\n          ResultType: ''entity_object''\n        });\n\n        if (!result.Success) {\n          setError(result.ErrorMessage || ''Failed to load submissions'');\n          setSubmissionData([]);\n          return;\n        }\n\n        // Enhance submissions with computed fields for better display\n        const enhancedSubmissions = (result.Results || []).map(submission => {\n          const submittedDate = submission.SubmittedAt\n            ? new Date(submission.SubmittedAt)\n            : null;\n\n          return {\n            ...submission,\n            // Formatted dates\n            SubmittedDateFormatted: submittedDate\n              ? submittedDate.toLocaleDateString()\n              : ''N/A'',\n            SubmittedDateTimeFormatted: submittedDate\n              ? submittedDate.toLocaleString()\n              : ''N/A'',\n            // Truncated abstract for grid display\n            AbstractPreview: submission.SubmissionAbstract\n              ? submission.SubmissionAbstract.substring(0, 100) + ''...''\n              : '''',\n            // Score formatting\n            ScoreFormatted: submission.AIEvaluationScore != null\n              ? `$' + '{submission.AIEvaluationScore.toFixed(1)}/100`\n              : ''Not Scored'',\n            // Status badge style\n            StatusBadge: submission.Status || ''Unknown'',\n            // Days since submission\n            DaysSinceSubmission: submittedDate\n              ? Math.floor((new Date() - submittedDate) / (1000 * 60 * 60 * 24))\n              : null\n          };\n        });\n\n        setSubmissionData(enhancedSubmissions);\n      } catch (err) {\n        console.error(''Error loading submissions:'', err);\n        setError(err.message || ''Unknown error occurred'');\n        setSubmissionData([]);\n      } finally {\n        setLoading(false);\n      }\n    };\n\n    loadSubmissions();\n  }, [eventId, utilities?.refresh]);\n\n  // Handle submission row selection\n  const handleRowSelected = (selectionData) => {\n    const submission = selectionData.record;\n\n    console.log(''Submission selected:'', submission.SubmissionTitle);\n\n    // Fire external callback if provided\n    if (onSubmissionSelected) {\n      onSubmissionSelected(submission);\n    }\n  };\n\n  if (loading) {\n    return (\n      <div style={{\n        padding: ''40px'',\n        textAlign: ''center'',\n        color: ''#666''\n      }}>\n        <div style={{\n          fontSize: ''16px'',\n          marginBottom: ''8px'',\n          fontWeight: 500\n        }}>\n          Loading submission data...\n        </div>\n        {eventName && (\n          <div style={{ fontSize: ''14px'', color: ''#999'' }}>\n            {eventName}\n          </div>\n        )}\n      </div>\n    );\n  }\n\n  if (error) {\n    return (\n      <div style={{\n        padding: ''20px'',\n        backgroundColor: ''#fff2f0'',\n        border: ''1px solid #ffccc7'',\n        borderRadius: ''4px'',\n        color: ''#cf1322''\n      }}>\n        <div style={{\n          fontWeight: ''bold'',\n          marginBottom: ''8px'',\n          fontSize: ''16px''\n        }}>\n          Error Loading Submissions\n        </div>\n        <div>{error}</div>\n      </div>\n    );\n  }\n\n  if (submissionData.length === 0) {\n    return (\n      <div style={{\n        padding: ''40px'',\n        textAlign: ''center'',\n        backgroundColor: ''#fafafa'',\n        border: ''1px solid #d9d9d9'',\n        borderRadius: ''4px'',\n        color: ''#666''\n      }}>\n        <div style={{\n          fontSize: ''18px'',\n          marginBottom: ''8px'',\n          fontWeight: 500\n        }}>\n          No submissions yet\n        </div>\n        <div style={{ fontSize: ''14px'', color: ''#999'' }}>\n          {eventName ? `No submissions found for $' + '{eventName}` : ''No submissions found for this event''}\n        </div>\n      </div>\n    );\n  }\n\n  const chartTitle = eventName\n    ? `Submissions by Status - $' + '{eventName}`\n    : ''Submissions by Status'';\n\n  return (\n    <SimpleDrilldownChart\n      entityName=\"Submissions\"\n      data={submissionData}\n      groupBy=\"Status\"\n      aggregateMethod=\"count\"\n      chartType=\"doughnut\"\n      title={chartTitle}\n      gridFields={[\n        ''SubmissionTitle'',\n        ''SubmittedDateFormatted'',\n        ''SessionFormat'',\n        ''ScoreFormatted'',\n        ''PassedInitialScreening'',\n        ''DaysSinceSubmission''\n      ]}\n      showDrilldown={true}\n      drilldownHeight={500}\n      showSingleRecordView={true}\n      singleRecordViewFields={[\n        ''SubmissionTitle'',\n        ''Status'',\n        ''SubmittedDateTimeFormatted'',\n        ''SubmissionAbstract'',\n        ''SessionFormat'',\n        ''Duration'',\n        ''TargetAudienceLevel'',\n        ''KeyTopics'',\n        ''AIEvaluationScore'',\n        ''AIEvaluationReasoning'',\n        ''PassedInitialScreening'',\n        ''FailureReasons'',\n        ''IsFixable'',\n        ''ReviewNotes'',\n        ''FinalDecision'',\n        ''FinalDecisionReasoning''\n      ]}\n      onRowSelected={handleRowSelected}\n      utilities={utilities}\n      styles={styles}\n      components={components}\n      callbacks={callbacks}\n      savedUserSettings={savedUserSettings}\n      onSaveUserSettings={onSaveUserSettings}\n    />\n  );\n}\n","exampleUsage":"<EventSubmissionsByStatus \n  eventId=\"A1B2C3D4-E5F6-4A5B-8C9D-0E1F2A3B4C5D\"\n  eventName=\"Annual Technology Conference 2025\"\n  onSubmissionSelected={(submission) => console.log(''Viewing:'', submission.SubmissionTitle)}\n/>"}'
SET
  @FunctionalRequirements_ecbc93f8 = N'Displays count of abstract submissions by status (New, Analyzing, Passed Initial, Under Review, Accepted, Rejected, etc.) for a specific event. Users click a status segment to see all submissions in that status. Clicking a submission row in the grid opens a detailed SingleRecordView panel showing full submission information including title, abstract, evaluation scores, and speaker details. Useful for conference organizers tracking submission pipeline and review workflows.'
SET
  @TechnicalDesign_ecbc93f8 = N'Loads all Submission records for specified EventID via RunView. Uses count aggregation (no valueField) to show submission volumes by Status. Implements doughnut chart for workflow stage visualization. Enables both grid drill-down and SingleRecordView side panel. Grid shows key submission fields (title, submitted date, score). SingleRecordView displays comprehensive submission details for in-depth review. Demonstrates showSingleRecordView=true configuration with custom field selection.'
