  @Specification_b709cdc2 = N'{"name":"KPIGauges","namespace":"CRM/Finance","type":"Chart","code":"function KPIGauges({ loading, metrics, chartRefs }) {\n  useEffect(() => {\n    if (!loading && chartRefs.current.marginGauge && chartRefs.current.utilizationGauge) {\n      const gaugeOptions = {\n        chart: {\n          type: ''radialBar'',\n          height: 200\n        },\n        plotOptions: {\n          radialBar: {\n            startAngle: -90,\n            endAngle: 90,\n            hollow: {\n              margin: 15,\n              size: ''70%'',\n              background: ''#fff''\n            },\n            dataLabels: {\n              name: {\n                show: true,\n                offsetY: -10\n              },\n              value: {\n                show: true,\n                fontSize: ''20px'',\n                fontWeight: ''bold'',\n                formatter: (val) => `$' + '{val.toFixed(0)}%`\n              }\n            }\n          }\n        },\n        fill: {\n          type: ''gradient'',\n          gradient: {\n            shade: ''dark'',\n            shadeIntensity: 0.15,\n            stops: [0, 100]\n          }\n        },\n        stroke: {\n          dashArray: 4\n        }\n      };\n\n      const marginGauge = new ApexCharts(chartRefs.current.marginGauge, {\n        ...gaugeOptions,\n        series: [metrics.grossMargin],\n        labels: [''Gross Margin''],\n        colors: [''#10B981'']\n      });\n      marginGauge.render();\n\n      const utilizationGauge = new ApexCharts(chartRefs.current.utilizationGauge, {\n        ...gaugeOptions,\n        series: [75],\n        labels: [''Utilization''],\n        colors: [''#3B82F6'']\n      });\n      utilizationGauge.render();\n\n      return () => {\n        marginGauge.destroy();\n        utilizationGauge.destroy();\n      };\n    }\n  }, [loading, metrics.grossMargin, chartRefs]);\n\n  return (\n    <div style={{ display: ''grid'', gridTemplateColumns: ''repeat(2, 1fr)'', gap: ''20px'' }}>\n      <div ref={el => chartRefs.current.marginGauge = el}></div>\n      <div ref={el => chartRefs.current.utilizationGauge = el}></div>\n    </div>\n  );\n}\n","functionalRequirements":"Radial gauge charts displaying key performance indicators like gross margin and utilization.","dataRequirements":{"mode":"props"},"technicalDesign":"Uses ApexCharts radial bars to display KPI metrics as percentage gauges.","properties":[{"name":"loading","type":"boolean","required":true,"description":"Loading state"},{"name":"metrics","type":"object","required":true,"description":"Metrics object containing grossMargin and other KPIs"},{"name":"chartRefs","type":"object","required":true,"description":"Ref object for chart DOM elements"}],"events":[],"dependencies":[],"libraries":[{"name":"ApexCharts","version":"^3.45.1","globalVariable":"ApexCharts"}],"description":"Radial gauge charts for KPI visualization"}'
