  @Specification_540da25c = N'{"name":"CashFlowChart","namespace":"CRM/Finance","type":"Chart","code":"function CashFlowChart({ loading, metrics, chartRefs, formatCurrency }) {\n  useEffect(() => {\n    if (!loading && chartRefs.current.cashFlowChart) {\n      const categories = [''Revenue'', ''Expenses'', ''Outstanding'', ''Net Cash''];\n      const data = [\n        metrics.actualRevenue,\n        -metrics.outstandingRevenue * 0.6,\n        -metrics.outstandingRevenue * 0.4,\n        metrics.cashFlow\n      ];\n\n      const chart = new ApexCharts(chartRefs.current.cashFlowChart, {\n        chart: {\n          type: ''bar'',\n          height: 300\n        },\n        series: [{\n          name: ''Cash Flow'',\n          data: data\n        }],\n        xaxis: {\n          categories: categories\n        },\n        yaxis: {\n          labels: {\n            formatter: (val) => formatCurrency(Math.abs(val))\n          }\n        },\n        colors: [''#10B981''],\n        plotOptions: {\n          bar: {\n            colors: {\n              ranges: [{\n                from: -1000000000,\n                to: 0,\n                color: ''#EF4444''\n              }]\n            }\n          }\n        },\n        dataLabels: {\n          enabled: true,\n          formatter: (val) => formatCurrency(val)\n        },\n        tooltip: {\n          y: {\n            formatter: (val) => formatCurrency(val)\n          }\n        }\n      });\n      chart.render();\n\n      return () => chart.destroy();\n    }\n  }, [loading, metrics.actualRevenue, metrics.outstandingRevenue, metrics.cashFlow, chartRefs, formatCurrency]);\n\n  return <div ref={el => chartRefs.current.cashFlowChart = el}></div>;\n}\n","functionalRequirements":"Bar chart displaying cash flow analysis with revenue, expenses, and net cash.","dataRequirements":{"mode":"props"},"technicalDesign":"Uses ApexCharts bar chart with color-coded positive/negative values.","properties":[{"name":"loading","type":"boolean","required":true,"description":"Loading state"},{"name":"metrics","type":"object","required":true,"description":"Metrics object containing cash flow data"},{"name":"chartRefs","type":"object","required":true,"description":"Ref object for chart DOM elements"},{"name":"formatCurrency","type":"function","required":true,"description":"Function to format currency values"}],"events":[],"dependencies":[],"libraries":[{"name":"ApexCharts","version":"^3.45.1","globalVariable":"ApexCharts"}],"description":"Cash flow visualization with bar chart"}'
