SET
  @Specification_f4a6a9ab = N'{"name":"ForecastModel","namespace":"CRM/Finance","type":"Chart","code":"function ForecastModel({ loading, forecastData, scenarios, chartRefs, formatCurrency, onScenarioChange }) {\n  const [selectedScenario, setSelectedScenario] = React.useState(scenarios?.[0]?.id || ''default'');\n\n  const handleScenarioChange = (scenarioId) => {\n    setSelectedScenario(scenarioId);\n    if (onScenarioChange) {\n      onScenarioChange(scenarioId);\n    }\n  };\n\n  useEffect(() => {\n    if (!loading && chartRefs.current.forecastChart && forecastData && forecastData.length > 0) {\n      const chart = new ApexCharts(chartRefs.current.forecastChart, {\n        chart: {\n          type: ''line'',\n          height: 300,\n          toolbar: {\n            show: false\n          }\n        },\n        series: [{\n          name: ''Forecast'',\n          data: forecastData.map(d => d.value)\n        }],\n        xaxis: {\n          categories: forecastData.map(d => d.period)\n        },\n        yaxis: {\n          labels: {\n            formatter: (val) => formatCurrency(val)\n          }\n        },\n        colors: [''#F59E0B''],\n        stroke: {\n          curve: ''smooth'',\n          width: 3,\n          dashArray: 5\n        },\n        markers: {\n          size: 6\n        },\n        dataLabels: {\n          enabled: false\n        },\n        tooltip: {\n          y: {\n            formatter: (val, opts) => {\n              const dataPoint = forecastData[opts.dataPointIndex];\n              if (dataPoint?.confidence) {\n                return `$' + '{formatCurrency(val)} ($' + '{formatCurrency(dataPoint.confidence.low)} - $' + '{formatCurrency(dataPoint.confidence.high)})`;\n              }\n              return formatCurrency(val);\n            }\n          }\n        }\n      });\n      chart.render();\n\n      return () => chart.destroy();\n    }\n  }, [loading, forecastData, selectedScenario, chartRefs, formatCurrency]);\n\n  return (\n    <div>\n      {scenarios && scenarios.length > 1 && (\n        <div style={{ marginBottom: ''12px'' }}>\n          <select\n            value={selectedScenario}\n            onChange={(e) => handleScenarioChange(e.target.value)}\n            style={{\n              padding: ''6px 10px'',\n              border: ''1px solid #D1D5DB'',\n              borderRadius: ''4px'',\n              fontSize: ''14px''\n            }}\n          >\n            {scenarios.map(scenario => (\n              <option key={scenario.id} value={scenario.id}>\n                {scenario.name}\n              </option>\n            ))}\n          </select>\n        </div>\n      )}\n      <div ref={el => chartRefs.current.forecastChart = el}></div>\n    </div>\n  );\n}\n","functionalRequirements":"Line chart displaying revenue forecast with scenario selection and confidence intervals.","dataRequirements":{"mode":"props"},"technicalDesign":"Uses ApexCharts line chart with dashed stroke for forecast visualization and dropdown for scenario selection.","properties":[{"name":"loading","type":"boolean","required":true,"description":"Loading state"},{"name":"forecastData","type":"Array<object>","required":true,"description":"Array of forecast data with confidence intervals"},{"name":"scenarios","type":"Array<object>","required":false,"description":"Array of forecast scenarios"},{"name":"chartRefs","type":"object","required":true,"description":"Ref object for chart DOM elements"},{"name":"formatCurrency","type":"function","required":true,"description":"Function to format currency values"},{"name":"onScenarioChange","type":"function","required":false,"description":"Callback when scenario changes"}],"events":[],"dependencies":[],"libraries":[{"name":"ApexCharts","version":"^3.45.1","globalVariable":"ApexCharts"}],"description":"Revenue forecast visualization with scenario modeling"}'
