            payload: {
                statePath: statePath,
                newState: newState
            },
            bubbles: true
        });
    };

    // Accessibility: allow ESC to trigger back
    React.useEffect(() => {
        const handleKeyDown = (e) => {
            if (e.key === 'Escape') {
                handleBack();
