              uncategorizedIcon: (
                <span
                  style={{
                    marginRight: 8,
                    fontSize: '1.2em',
                    verticalAlign: 'middle',
                    color: '#e63946', // Red maple leaf
                    textShadow: '0 1px 2px #fff, 0 0 4px #009e49'
