ies }) {
    // <<SUB_COMPONENT_PLACEHOLDER>>

    // Extract config for uncategorized
    const uncategorizedId = config?.uncategorizedId;
    const uncategorizedIcon = config?.uncategorizedIcon;

    const { ID, Name, Children = [] } = data || {};
    const hasChildren = Array.isArray(Children) && Children.length > 0;

    // --- State ---
    const [localState, setLocalState] = React.useState({
        expanded: !!(userState && userState.expanded),
        ...userState
    });

    // --- Selection/Focus ---
    const selectedId = config?.selectedId;
    const focusedId = config?.focusedId;
    const isSelected = selectedId === ID;
    const isFocused = focusedId === ID;
    const isUncategorized = ID === uncategorizedId;

