    const itemRefs = React.useRef([]);
    const scrollItemIntoView = (idx) => {
        if (itemRefs.current[idx]) {
            itemRefs.current[idx].scrollIntoView({ block: 'nearest', behavior: 'smooth' });
        }
    };

    // Card background: alternate between Rasta/Canadian gradients for extra flair
    const getCardBackground = (isSelected, isFocused, idx) => {
        if (isSelected) return CANADIAN_GRADIENT;
        if (isFocused) return RASTA_GRADIENT;
        return idx % 2 === 0 ?
            'linear-gradient(90deg, #fff70022 0%, #fff 100%)' :
            'linear-gradient(90deg, #e0e0e088 0%, #fff 100%)';
    };

    // Card border: thick and energetic when selected/focused
    const getCardBorder = (isSelected, isFocused) => {
        if (isSelected) return '3px solid #ff3131';
        if (isFocused) return '2px solid #009e49';
        return `1px solid ${styles.colors.border}`;
    };

    // Card accent icon: maple leaf + reggae note
    const getCardIcon = (idx) => (
        <span style={{
            fontSize: '1.7rem',
            marginRight: styles.spacing.sm,
            verticalAlign: 'middle',
            opacity: 0.9,
            filter: idx % 2 === 0 ? 'drop-shadow(0 1px #ff3131)' : 'drop-shadow(0 1px #009e49)'
        }}>
            {idx % 2 === 0 ? MAPLE_LEAF_EMOJI : REGGAE_ICON}
