    // Keyboard navigation: track focus
    const nodeRefs = React.useRef({});
    React.useEffect(() => {
        if (localState.focusedNodeId && nodeRefs.current[localState.focusedNodeId]) {
            nodeRefs.current[localState.focusedNodeId].focus();
        }
    }, [localState.focusedNodeId]);

    // Recursively render the category tree
    const renderTree = (nodes, depth = 0) => {
        if (!nodes || nodes.length === 0) return null;
        return nodes.map(node => {
            const isExpanded = !!localState.expanded[node.ID];
            const isSelected = localState.lastSelectedId === node.ID;
            const isFocused = localState.focusedNodeId === node.ID;
            const hasChildren = node.children && node.children.length > 0;
            const mapleLeaf = <span style={{ fontSize: 18, marginRight: 6, color: '#ff1515', textShadow: '0 1px 2px #fff8' }} aria-label=\"Maple Leaf\">??</span>;

            // Keyboard events
            const handleNodeKeyDown = e => {
                if (e.key === 'ArrowRight' && hasChildren && !isExpanded) {
                    handleExpandToggle(node.ID);
                } else if (e.key === 'ArrowLeft' && hasChildren && isExpanded) {
                    handleExpandToggle(node.ID);
                } else if (e.key === 'Enter' || e.key === ' ') {
                    handleSelect(node.ID);
                } else if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {
                    // Arrow navigation: find visible flattened node list
                    const flat = flattenVisibleTree();
                    const idx = flat.findIndex(x => x.ID === node.ID);
                    let nextIdx = idx;
                    if (e.key === 'ArrowDown') nextIdx = Math.min(flat.length - 1, idx + 1);
                    if (e.key === 'ArrowUp') nextIdx = Math.max(0, idx - 1);
                    const nextNode = flat[nextIdx];
                    if (nextNode) {
                        updateState({ focusedNodeId: nextNode.ID });
                    }
                }
            };

            return (
                <React.Fragment key={node.ID}>
                    <div
                        tabIndex={0}
                        ref={el => nodeRefs.current[node.ID] = el}
                        aria-selected={isSelected}
                        aria-expanded={hasChildren ? isExpanded : undefined}
                        aria-label={node.Name}
                        role=\"treeitem\"
                        onClick={() => handleSelect(node.ID)}
