                                    <span aria-hidden=\"true\" style={{ fontSize: 18, fontWeight: 800 }}>â€“</span>
                                ) : (
                                    <span aria-hidden=\"true\" style={{ fontSize: 18, fontWeight: 800 }}>+</span>
                                )}
                            </button>
                        )}
                        {mapleLeaf}
                        <span style={{
                            fontWeight: isSelected ? 700 : 400,
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap',
                            letterSpacing: '.01em',
                        }}>{node.Name}</span>
                    </div>
                    {hasChildren && isExpanded && (
                        <div role=\"group\" style={{ marginLeft: styles.spacing.sm }}>
                            {renderTree(node.children, depth + 1)}
                        </div>
                    )}
                </React.Fragment>
            );
        });
    };

    // Flatten visible tree for keyboard navigation
    function flattenVisibleTree() {
        const out = [];
        function visit(nodes) {
            for (const node of nodes) {
                out.push(node);
                if (node.children && node.children.length && localState.expanded[node.ID]) {
                    visit(node.children);
                }
            }
        }
        visit(tree);
        if (showUncategorized && uncategorizedPosition === 'bottom') {
            out.push({ ID: uncategorizedNodeId, Name: uncategorizedLabel, isUncategorized: true });
        }
        if (showUncategorized && uncategorizedPosition === 'top') {
            out.unshift({ ID: uncategorizedNodeId, Name: uncategorizedLabel, isUncategorized: true });
        }
        return out;
    }

